// Minimal Log: VS Code + Node (read-only, one-click, dual output)
const vscode = require('vscode');
const fs = require('fs');
const path = require('path');
const { vm } = statusBarHelper.v1;
(function main(){ const ch = vscode.window.createOutputChannel('SBH Minimal Log'); const emit=(...a)=>{ const line=a.join(' '); ch.appendLine(line); console.log(line); }; ch.show(true); emit('‚ñ∂ Start'); emit('Node: ' + process.version + '  Platform: ' + process.platform + '/' + process.arch); const ws=vscode.workspace.workspaceFolders; const root=ws&&ws.length?ws[0].uri.fsPath:process.cwd(); emit('Workdir: '+root); try { fs.readdirSync(root,{withFileTypes:true}).slice(0,8).forEach(e=>emit((e.isDirectory()?'[D] ':'[F] ')+e.name)); } catch(e){ emit('readdir failed: '+e.message); } const ed=vscode.window.activeTextEditor; if(ed&&ed.document.uri.scheme==='file'){ emit('Active file: '+path.basename(ed.document.uri.fsPath)+' ('+ed.document.languageId+')'); } emit('‚úî Done'); vscode.window.showInformationMessage('Log demo finished.'); vm.stop(); })();

// Git Add: stage all in first workspace (one-click)
const vscode = require('vscode');
const { exec } = require('child_process');
const { vm } = statusBarHelper.v1;
(function main(){
  const ws = vscode.workspace.workspaceFolders;
  if (!ws || !ws.length) { console.log('[GitAdd] No workspace folder.'); vscode.window.showWarningMessage('No workspace folder ‚Äî cannot run git add.'); vm.stop(); return; }
  const cwd = ws[0].uri.fsPath;
  console.log('[GitAdd] cwd:', cwd);
  exec('git add .', { cwd }, (err, stdout, stderr) => {
    if (err) { console.error('[GitAdd] error:', stderr || err.message); vscode.window.showErrorMessage('Git add failed: ' + (stderr || err.message)); vm.stop(); return; }
    if (stdout && stdout.trim()) console.log(stdout.trim());
    console.log('[GitAdd] done.');
    vscode.window.showInformationMessage('‚úÖ git add . done');
    vm.stop();
  });
})();

// Status Bar Helper v1: storage & files API demo
const { Buffer } = require('buffer');
const { storage, files, vm } = statusBarHelper.v1;
function main() {
  const now = () => new Date().toISOString().replace('T',' ').replace('Z','');
  const dump = (x) => { if (x instanceof Uint8Array) return `Uint8Array(${x.length}) [${Array.from(x).slice(0,16).join(',')}${x.length>16?',‚Ä¶':''}]`; try { return JSON.stringify(x,null,2);} catch { return String(x);} };
  const log = (...a)=>console.log('[SBH]', ...a.map(dump));
  (async () => {
    log('‚ñ∂ start', now());
    log('‚Äî storage / global ‚Äî');
    const gPrev = await storage.global.get('demo.keep', { runs: 0 });
    await storage.global.set('demo.keep', { ...gPrev, runs:(gPrev.runs||0)+1, ts: now(), from:'global' });
    log('get keep =', await storage.global.get('demo.keep', null));
    log('keys =', await storage.global.keys());
    log('‚Äî storage / workspace ‚Äî');
    try {
      const wPrev = await storage.workspace.get('demo.keep', { runs: 0 });
      await storage.workspace.set('demo.keep', { ...wPrev, runs:(wPrev.runs||0)+1, ts: now(), from:'workspace' });
      log('get keep =', await storage.workspace.get('demo.keep', null));
      log('keys =', await storage.workspace.keys());
    } catch (e) { log('workspace storage not available:', e?.message || e); }
    const dirs = await files.dirs(); log('dirs =', dirs);
    const txtRel='demo/notes/hello.txt';
    const txtOld=(await files.exists('global', txtRel))?await files.readText('global', txtRel):'';
    await files.writeText('global', txtRel, txtOld + `hello @ ${now()}
`);
    log('readText:', await files.readText('global', txtRel));
    const jsonScope = dirs.workspace ? 'workspace':'global';
    const jsonRel='demo/data/sample.json';
    const cur=(await files.exists(jsonScope, jsonRel))?await files.readJSON(jsonScope, jsonRel):{ a:1, createdAt: now() };
    cur.updatedAt=now(); cur.count=(cur.count||0)+1; await files.writeJSON(jsonScope, jsonRel, cur);
    log('readJSON:', await files.readJSON(jsonScope, jsonRel));
    const binRel='demo/bin/demo.bin'; await files.writeBytes('global', binRel, new Uint8Array([1,2,3,4,5,254,255]));
    log('readBytes demo.bin:', await files.readBytes('global', binRel));
    const b64Rel='demo/bin/base64.bin'; await files.writeBytes('global', b64Rel, Buffer.from([9,8,7,6]).toString('base64'));
    log('exists base64.bin:', await files.exists('global', b64Rel));
    const candidates=[{scope:'global',rel:'demo/notes/hello.txt',type:'text'},{scope:jsonScope,rel:'demo/data/sample.json',type:'json'},{scope:'global',rel:'demo/bin/demo.bin',type:'bytes'},{scope:'global',rel:'demo/bin/base64.bin',type:'bytes'},{scope:'global',rel:'demo/tmp/kept.txt',type:'text'}];
    const rows=[]; for (const c of candidates){ if (await files.exists(c.scope,c.rel)){ let size=0; if(c.type==='text'){ const s=await files.readText(c.scope,c.rel); size=Buffer.byteLength(s,'utf8'); } else if(c.type==='json'){ const o=await files.readJSON(c.scope,c.rel); size=Buffer.byteLength(JSON.stringify(o),'utf8'); } else { const b=await files.readBytes(c.scope,c.rel); size=b?.length||0; } rows.push({ scope:c.scope, path:c.rel, type:c.type, size }); }}
    log('files (simulated list):', rows);
    log('‚úî done', now()); vm.stop();
  })().catch(e=>{ console.error('‚ùå', e); vm.stop(); });
}
main();

// Toggle between Light and Dark themes
const vscode = require('vscode');
const { vm } = statusBarHelper.v1;
(async () => { try { await vscode.commands.executeCommand('workbench.action.toggleLightDarkThemes'); } catch (e) { vscode.window.showErrorMessage('Toggle theme failed: ' + (e?.message || e)); } finally { vm.stop(); } })();

// Whiteboard (no save) minimal
const vscode = require('vscode');
const fs = require('fs');
const path = require('path');
const { vm } = statusBarHelper.v1;
(function main(){ const panel = vscode.window.createWebviewPanel('sbhWhiteboard','Whiteboard ‚Äî Draw Only',vscode.ViewColumn.Active,{ enableScripts:true, retainContextWhenHidden:true }); vm.onStop(()=>{ try { panel.dispose(); } catch {} }); panel.onDidDispose(()=>{ vm.stop(); }); const nonce=Math.random().toString(36).slice(2); panel.webview.html=getHtml(nonce); panel.webview.onDidReceiveMessage(async (msg)=>{ if(msg?.type!=='exportPNG'||typeof msg.dataURL!=='string') return; try { const now=new Date(); const y=now.getFullYear(); const m=String(now.getMonth()+1).padStart(2,'0'); const d=String(now.getDate()).padStart(2,'0'); const hh=String(now.getHours()).padStart(2,'0'); const mm=String(now.getMinutes()).padStart(2,'0'); const ss=String(now.getSeconds()).padStart(2,'0'); const defaultName=`whiteboard-${y}${m}${d}-${hh}${mm}${ss}.png`; const uri=await vscode.window.showSaveDialog({ saveLabel:'Export PNG', defaultUri:vscode.Uri.file(path.join(process.cwd(), defaultName)), filters:{ 'PNG Image':['png'] } }); if(!uri) return; const base64=msg.dataURL.split(',')[1]||''; fs.writeFileSync(uri.fsPath, Buffer.from(base64,'base64')); vscode.window.showInformationMessage(`‚úÖ Exported: ${path.basename(uri.fsPath)}`); panel.webview.postMessage({ type:'exported', file: uri.fsPath }); } catch(e){ vscode.window.showErrorMessage('Export failed: ' + (e?.message || e)); } }); function getHtml(nonce){ return `<!doctype html>
<meta http-equiv="Content-Security-Policy" content="default-src 'none'; img-src data:; style-src 'unsafe-inline'; script-src 'nonce-${nonce}';">
<title>Whiteboard (Draw Only)</title>
<style>:root{ --h:32px } body{font-family:var(--vscode-font-family);color:var(--vscode-foreground);background:var(--vscode-editor-background);margin:0}.bar{display:flex;align-items:center;gap:8px;border-bottom:1px solid var(--vscode-editorGroup-border);padding:6px 8px;height:var(--h);user-select:none;background:var(--vscode-sideBar-background)}.bar input[type=color]{width:28px;height:20px;border:1px solid var(--vscode-input-border);background:var(--vscode-input-background)}.bar input[type=range]{width:120px}button{background:var(--vscode-button-background);color:var(--vscode-button-foreground);border:1px solid var(--vscode-button-border,transparent);padding:4px 10px;border-radius:4px;cursor:pointer}button:hover{background:var(--vscode-button-hoverBackground)}#wrap{position:relative;height:calc(100vh - var(--h) - 2px)}canvas{width:100%;height:100%}#grid{position:absolute;inset:0;background:linear-gradient(to right, transparent 99%, var(--vscode-editorGroup-border) 0) 0 0/20px 20px,linear-gradient(to bottom, transparent 99%, var(--vscode-editorGroup-border) 0) 0 0/20px 20px;pointer-events:none;opacity:.2}#msg{margin-left:auto;opacity:.85;font-size:.9em}</style>
<div class=bar><span>Color</span><input id=color type=color value=#00d3a7><span>Size</span><input id=size type=range min=1 max=32 value=4><button id=undo>Undo</button><button id=redo>Redo</button><button id=clear>Clear</button><span style=flex:1></span><button id=export>Export PNG</button><span id=msg></span></div>
<div id=wrap><canvas id=c></canvas><div id=grid></div></div>
<script nonce=${nonce}>const vscode=acquireVsCodeApi();const c=document.getElementById('c');const ctx=c.getContext('2d');let drawing=false,last=null;let color=document.getElementById('color').value;let size=+document.getElementById('size').value;const undoStack=[],redoStack=[];const msgEl=document.getElementById('msg');function flash(t){ msgEl.textContent=t; setTimeout(()=>msgEl.textContent='',2000);}function dpr(){ return window.devicePixelRatio||1;}function applyScale(){ ctx.setTransform(1,0,0,1,0,0); ctx.scale(dpr(),dpr());}function resize(){ const r=c.getBoundingClientRect(); const tmp=document.createElement('canvas'); tmp.width=c.width; tmp.height=c.height; tmp.getContext('2d').drawImage(c,0,0); c.width=Math.max(1,Math.round(r.width*dpr())); c.height=Math.max(1,Math.round(r.height*dpr())); applyScale(); try{ ctx.drawImage(tmp,0,0,tmp.width,tmp.height,0,0,c.width,c.height);}catch{}} new ResizeObserver(resize).observe(document.getElementById('wrap')); setTimeout(resize,0);function line(p1,p2){ ctx.strokeStyle=color; ctx.lineWidth=size; ctx.lineCap='round'; ctx.lineJoin='round'; ctx.beginPath(); ctx.moveTo(p1.x,p1.y); ctx.lineTo(p2.x,p2.y); ctx.stroke();}function pos(e){ const r=c.getBoundingClientRect(); return { x:e.clientX-r.left, y:e.clientY-r.top }; }function makeSnap(){ return { w:c.width, h:c.height, url:c.toDataURL('image/png') }; }function snapshot(){ try{ undoStack.push(makeSnap()); if(undoStack.length>30) undoStack.shift(); }catch{} redoStack.length=0; }function restore(snap){ return new Promise(res=>{ const img=new Image(); img.onload=()=>{ ctx.setTransform(1,0,0,1,0,0); ctx.clearRect(0,0,c.width,c.height); ctx.drawImage(img,0,0,snap.w,snap.h,0,0,c.width,c.height); applyScale(); res(); }; img.src=snap.url; }); } c.addEventListener('pointerdown', e=>{ e.preventDefault(); snapshot(); drawing=true; last=pos(e); }); c.addEventListener('pointermove', e=>{ if(!drawing) return; const p=pos(e); line(last,p); last=p; }); c.addEventListener('pointerup',()=>{ drawing=false; last=null; }); c.addEventListener('pointerleave',()=>{ drawing=false; last=null; }); document.getElementById('color').oninput=e=>color=e.target.value; document.getElementById('size').oninput=e=>size=+e.target.value; document.getElementById('undo').onclick=async()=>{ if(!undoStack.length) return; const snap=undoStack.pop(); redoStack.push(makeSnap()); await restore(snap); }; document.getElementById('redo').onclick=async()=>{ if(!redoStack.length) return; const snap=redoStack.pop(); undoStack.push(makeSnap()); await restore(snap); }; document.getElementById('clear').onclick=()=>{ snapshot(); ctx.setTransform(1,0,0,1,0,0); ctx.clearRect(0,0,c.width,c.height); applyScale(); }; document.getElementById('export').onclick=()=>{ try{ const url=c.toDataURL('image/png'); vscode.postMessage({ type:'exportPNG', dataURL:url }); flash('Exporting‚Ä¶'); }catch(e){ flash('Export failed'); } }; window.addEventListener('message', e=>{ const m=e.data||{}; if(m.type==='exported'){ const name=(m.file||'').split(/[\\/]/).pop()||m.file; flash('Saved: '+name); } });</script>`; } })();
