const vscode = require("vscode");
const { vm, storage } = statusBarHelper.v1;

const SETTINGS_KEY = 'scriptLauncher.visibleScripts';
let allScripts = [];
let statusBarItem;
let disposables = [];
let settingsPanel;

(async function main() {
  const rawScripts = await vm.scripts();
  allScripts = rawScripts.map((script) => ({
    label: script.text || script.command,
    description: script.tooltip || '',
    commandId: script.command,
  }));

  vm.onStop(() => {
    console.log("Disposing hybrid launcher resources...");
    disposables.forEach((d) => {
      try {
        d.dispose();
      } catch (e) {
        console.error("Failed to dispose resource:", e);
      }
    });
    if (settingsPanel) {
        settingsPanel.dispose();
    }
  });

  const openSettingsCommand = vscode.commands.registerCommand(
    "sbh.scriptLauncher.openSettings",
    openSettingsWebview
  );
  disposables.push(openSettingsCommand);

  await updateUI();

  console.log("Hybrid script launcher with settings is ready.");
})();

async function updateUI() {
  if (statusBarItem) {
    statusBarItem.dispose();
  }
  const oldCommandHandlerIndex = disposables.findIndex(d => d.commandId === "sbh.custom.showScriptLauncherQuickPick");
  if (oldCommandHandlerIndex > -1) {
    disposables.splice(oldCommandHandlerIndex, 1)[0].dispose();
  }

  const settings = await storage.global.get(SETTINGS_KEY, {});
  const visibleScripts = allScripts.filter(
    (script) => settings[script.commandId] !== false
  );

  const markdownTooltip = new vscode.MarkdownString("", true);
  markdownTooltip.supportThemeIcons = true;
  markdownTooltip.isTrusted = true;

  const markdownLines = visibleScripts.map((script) => {
    const args = encodeURIComponent(JSON.stringify(script.commandArgs || []));
    const commandUri = `command:${script.commandId}?${args}`;
    return `[${script.label}](${commandUri} "${script.description}")`;
  });

  markdownTooltip.value = `**Script Launcher**\n\n---\n\n${markdownLines.join(
    "\n\n"
  )}\n\n---\n\n[$(gear) Configure Scripts...](command:sbh.scriptLauncher.openSettings "Open script visibility settings")`;

  const launcherCommandId = "sbh.custom.showScriptLauncherQuickPick";
  const commandHandler = vscode.commands.registerCommand(
    launcherCommandId,
    async () => {
      const quickPickItems = [
        ...visibleScripts,
        { label: "", kind: vscode.QuickPickItemKind.Separator },
        {
          label: "$(gear) Configure Visible Scripts...",
          description: "Choose which scripts to show in this list",
          commandId: "sbh.scriptLauncher.openSettings",
        },
      ];

      const selected = await vscode.window.showQuickPick(quickPickItems, {
        placeHolder: "Select a script to execute",
        title: "Script Launcher",
      });

      if (selected) {
        try {
          vscode.commands.executeCommand(
            selected.commandId,
            ...(selected.commandArgs || [])
          );
        } catch (e) {
          vscode.window.showErrorMessage(
            `Failed to execute command: ${selected.commandId}. Error: ${
              e?.message || e
            }`
          );
        }
      }
    }
  );
  disposables.push(commandHandler);

  statusBarItem = vscode.window.createStatusBarItem(
    vscode.StatusBarAlignment.Left,
    100
  );
  statusBarItem.text = "$(list-selection) Scripts";
  statusBarItem.tooltip = markdownTooltip;
  statusBarItem.command = launcherCommandId;
  statusBarItem.show();
  disposables.push(statusBarItem);
}

async function openSettingsWebview() {
  const column = vscode.window.activeTextEditor
    ? vscode.window.activeTextEditor.viewColumn
    : undefined;

  if (settingsPanel) {
    settingsPanel.reveal(column);
    return;
  }

  settingsPanel = vscode.window.createWebviewPanel(
    "scriptLauncherSettings",
    "Script Launcher Settings",
    column || vscode.ViewColumn.One,
    {
      enableScripts: true,
      localResourceRoots: [],
    }
  );

  settingsPanel.onDidDispose(() => {
    settingsPanel = undefined;
  }, null, disposables);

  settingsPanel.webview.html = getWebviewHtml(settingsPanel.webview);

  settingsPanel.webview.onDidReceiveMessage(
    async (message) => {
      switch (message.command) {
        case 'webviewReady':
          const currentSettings = await storage.global.get(SETTINGS_KEY, {});
          settingsPanel.webview.postMessage({
            command: 'init',
            payload: {
              allScripts: allScripts,
              settings: currentSettings
            }
          });
          break;
        case 'saveSettings':
          await storage.global.set(SETTINGS_KEY, message.payload);
          await updateUI();
          vscode.window.showInformationMessage('Script Launcher settings saved!');
          if (settingsPanel) {
            settingsPanel.dispose();
          }
          break;
      }
    },
    null,
    disposables
  );
}

/**
 * Generates the HTML content for the webview (VS Code Native Style - Final English Version)
 * @param {vscode.Webview} webview - The webview instance
 * @returns {string}
 */
function getWebviewHtml(webview) {
    const nonce = Math.random().toString(36).slice(2);
    return `<!DOCTYPE html>
    <html lang="en">
    <head>
        <meta charset="UTF-8">
        <meta http-equiv="Content-security-policy" content="default-src 'none'; style-src 'unsafe-inline'; script-src 'nonce-${nonce}';">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Script Launcher Settings</title>
        <style>
            html, body { height: 100vh; margin: 0; padding: 0; display: flex; flex-direction: column; overflow: hidden; font-family: var(--vscode-font-family); color: var(--vscode-foreground); background-color: var(--vscode-editor-background); }
            .header { padding: 1em 2em; border-bottom: 1px solid var(--vscode-divider-background); flex-shrink: 0; }
            .main-content { flex: 1; overflow-y: auto; padding: 0.5em 2em; }
            .footer { padding: 1em 2em; background-color: var(--vscode-editor-background); border-top: 1px solid var(--vscode-divider-background); display: flex; justify-content: flex-end; gap: 10px; flex-shrink: 0; }
            h1 { font-weight: 500; margin: 0 0 0.5em 0; font-size: 1.5em; }
            p { margin: 0; color: var(--vscode-descriptionForeground); }
            .script-item { display: flex; align-items: center; padding: 0.6em 0.5em; margin-bottom: 2px; border-radius: 5px; cursor: pointer; transition: background-color 0.2s ease; }
            .script-item:hover { background-color: var(--vscode-list-hoverBackground); }
            .script-info { display: flex; align-items: baseline; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; }
            .script-label { font-size: 1em; color: var(--vscode-settings-textInputForeground); margin-right: 0.6em; }
            .script-description { font-size: 0.9em; color: var(--vscode-descriptionForeground); opacity: 0.9; }
            .script-item input[type="checkbox"] { appearance: none; -webkit-appearance: none; width: 16px; height: 16px; margin-right: 12px; border-radius: 3px; outline: none; cursor: pointer; position: relative; background-color: var(--vscode-checkbox-background); border: 1px solid var(--vscode-checkbox-border); }
            .script-item input[type="checkbox"]:checked { background-color: var(--vscode-checkbox-selectBackground); border-color: var(--vscode-checkbox-selectBackground); }
            .script-item input[type="checkbox"]:checked::before { content: ''; position: absolute; top: 2px; left: 5px; width: 4px; height: 8px; border-style: solid; border-color: var(--vscode-checkbox-foreground); border-width: 0 2px 2px 0; transform: rotate(45deg); }
            .footer .actions-left { margin-right: auto; display: flex; gap: 10px; }

            button {
                padding: 8px 15px;
                border-style: solid;
                border-width: 1px;
                cursor: pointer;
                border-radius: 5px;
                font-size: 0.9em;
                background-color: var(--vscode-button-secondaryBackground);
                color: var(--vscode-button-secondaryForeground);
                border-color: var(--vscode-button-border, var(--vscode-contrastBorder));
            }
            button:hover {
                 background-color: var(--vscode-button-secondaryHoverBackground);
            }
        </style>
    </head>
    <body>
        <div class="header"><h1>Visible Scripts</h1><p>Select which scripts you want to see in the Script Launcher.</p></div>
        <div class="main-content"><div id="script-list-container"></div></div>
        <div class="footer">
             <div class="actions-left">
                <button id="select-all-btn">Select All</button>
                <button id="deselect-all-btn">Deselect All</button>
            </div>
            <button id="save-btn">Save and Close</button>
        </div>
        <script nonce="${nonce}">
            const vscode = acquireVsCodeApi();
            const container = document.getElementById('script-list-container');
            const saveBtn = document.getElementById('save-btn');
            const selectAllBtn = document.getElementById('select-all-btn');
            const deselectAllBtn = document.getElementById('deselect-all-btn');
            window.addEventListener('message', event => {
                const message = event.data;
                if (message.command === 'init') {
                    const { allScripts, settings } = message.payload;
                    renderCheckboxes(allScripts, settings);
                }
            });
            function renderCheckboxes(scripts, currentSettings) {
                container.innerHTML = '';
                scripts.forEach(script => {
                    const isVisible = currentSettings[script.commandId] !== false;
                    const descriptionSpan = script.description ? \`<span class="script-description">\${script.description}</span>\` : '';
                    const item = document.createElement('label');
                    item.className = 'script-item';
                    item.innerHTML = \`<input type="checkbox" data-command-id="\${script.commandId}" \${isVisible ? 'checked' : ''}><div class="script-info" title="\${script.label} \${script.description || ''}"><span class="script-label">\${script.label}</span>\${descriptionSpan}</div>\`;
                    container.appendChild(item);
                });
            }
            saveBtn.addEventListener('click', () => {
                const newSettings = {};
                const checkboxes = container.querySelectorAll('input[type="checkbox"]');
                checkboxes.forEach(cb => { newSettings[cb.dataset.commandId] = cb.checked; });
                vscode.postMessage({ command: 'saveSettings', payload: newSettings });
            });
            selectAllBtn.addEventListener('click', () => {
                container.querySelectorAll('input[type="checkbox"]').forEach(cb => cb.checked = true);
            });
            deselectAllBtn.addEventListener('click', () => {
                container.querySelectorAll('input[type="checkbox"]').forEach(cb => cb.checked = false);
            });
            vscode.postMessage({ command: 'webviewReady' });
        </script>
    </body>
    </html>`;
}

// Toggle between Light and Dark themes in VS Code (one-click)
// Purpose: Demonstrates how to trigger a built-in VS Code command from a script
// Note: This uses 'workbench.action.toggleLightDarkThemes' which toggles the current color theme

const vscode = require('vscode');
const { vm } = statusBarHelper.v1;

(async () => {
  try {
    // Execute the built-in toggle theme command
    await vscode.commands.executeCommand('workbench.action.toggleLightDarkThemes');
  } catch (e) {
    // Show an error message in VS Code if the command fails
    vscode.window.showErrorMessage('Toggle theme failed: ' + (e?.message || e));
  } finally {
    vm.stop();
  }
})();

// Minimal Log: VS Code + Node (read-only, one-click, dual output)
// Example: Demonstrates how to log messages in a VS Code extension
// 1. Create a custom Output Channel in VS Code
// 2. Define a helper function to log to both Output Channel and console
// 3. Show environment info (Node version, platform, architecture)
// 4. List a few files/directories from the current workspace
// 5. Show the currently active file and its language
// 6. Display a completion message in VS Code's UI

// Import VS Code API
const vscode = require('vscode');
// Import Node.js file system and path modules
const fs = require('fs');
const path = require('path');
// Inject VM API
const { vm } = statusBarHelper.v1; 

(function main(){
  // Create a new Output Channel named "SBH Minimal Log"
  const ch = vscode.window.createOutputChannel('SBH Minimal Log');

  // Helper function: log to both Output Channel and console
  const emit = (...a) => {
    const line = a.join(' ');
    ch.appendLine(line);
    console.log(line);
  };

  // Show the Output Channel immediately
  ch.show(true);

  // Start logging
  emit('‚ñ∂ Start');

  // Show Node.js version, platform, and architecture
  emit('Node: ' + process.version + '  Platform: ' + process.platform + '/' + process.arch);

  // Get current workspace root (or fallback to process.cwd())
  const ws = vscode.workspace.workspaceFolders;
  const root = ws && ws.length ? ws[0].uri.fsPath : process.cwd();
  emit('Workdir: ' + root);

  // Try listing the first 8 entries in the workspace folder
  try {
    const entries = fs.readdirSync(root, { withFileTypes: true }).slice(0, 8);
    entries.forEach(e => emit((e.isDirectory() ? '[D] ' : '[F] ') + e.name));
  } catch (e) {
    emit('readdir failed: ' + e.message);
  }

  // Show the active file name and language if available
  const ed = vscode.window.activeTextEditor;
  if (ed && ed.document.uri.scheme === 'file') {
    emit('Active file: ' + path.basename(ed.document.uri.fsPath) + ' (' + ed.document.languageId + ')');
  }

  // Final message
  emit('‚úî Done');

  // Show a success message in VS Code
  vscode.window.showInformationMessage('Log demo finished. Check "SBH Minimal Log" and the bottom run log.');

  // Stop VM explicitly after completion
  vm.stop();
})();

// Git Add: stage all in first workspace (one-click)
// Example: How to run a terminal command from a VS Code extension
// 1. Get the first opened workspace folder path
// 2. Use Node.js child_process.exec to execute a system command (git add .)
// 3. Convert the command output (stdout/stderr) to string and handle it
// 4. Show messages in the VS Code UI

// Import VS Code API
const vscode = require('vscode');
// Import exec from Node.js (used to run system commands)
const { exec } = require('child_process');
const { vm } = statusBarHelper.v1;

(function main(){
  // Get the opened workspace folders
  const ws = vscode.workspace.workspaceFolders;
  if (!ws || !ws.length) {
    console.log('[GitAdd] No workspace folder.');
    vscode.window.showWarningMessage('No workspace folder ‚Äî cannot run git add.');
    vm.stop();
    return;
  }

  // Get the first workspace's filesystem path
  const cwd = ws[0].uri.fsPath;
  console.log('[GitAdd] cwd:', cwd);

  // Execute the git add . command in the workspace directory
  exec('git add .', { cwd }, (err, stdout, stderr) => {
    if (err) {
      // stderr usually contains error messages
      console.error('[GitAdd] error:', stderr || err.message);
      vscode.window.showErrorMessage('Git add failed: ' + (stderr || err.message));
      vm.stop();
      return;
    }

    // stdout contains the command output (Buffer ‚Üí string)
    if (stdout && stdout.trim()) console.log(stdout.trim());
    console.log('[GitAdd] done.');

    // Show a success message in VS Code
    vscode.window.showInformationMessage('‚úÖ git add . done');

    vm.stop();
  });
})();

// Status Bar Helper v1: Demonstrates how to use the custom statusBarHelper API
// Purpose:
//  1. Show how to use custom storage (global & workspace) for key-value data
//  2. Show how to use custom file APIs to read/write text, JSON, and binary data
//  3. Show how to check existence, calculate file sizes, and log results
//  4. Intended for demo/testing inside a VS Code extension with SBH enabled

// Import Node.js Buffer (for binary and base64 handling)
const { Buffer } = require('buffer');

// Destructure storage and files API from statusBarHelper v1
const { secret, storage, files, vm } = statusBarHelper.v1;

function main() {

  // Helper: current timestamp in readable format
  const now = () => new Date().toISOString().replace('T', ' ').replace('Z', '');

  // Helper: pretty-print any value for logging
  const dump = (x) => {
    if (x instanceof Uint8Array)
      return `Uint8Array(${x.length}) [${Array.from(x).slice(0, 16).join(',')}${x.length > 16 ? ',‚Ä¶' : ''}]`;
    try { return JSON.stringify(x, null, 2); } catch { return String(x); }
  };

  // Helper: prefixed log output
  const log = (...a) => console.log('[SBH]', ...a.map(dump));

  (async () => {
    log('‚ñ∂ start', now());

    // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ secret ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    await secret.set('github.token', 'ghp_xxx...');
    const t = await secret.get('github.token');   // string | undefined
    console.log('token', t);

    // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ storage / global ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    log('‚Äî storage / global ‚Äî');
    const gPrev = await storage.global.get('demo.keep', { runs: 0 });
    await storage.global.set('demo.keep', { ...gPrev, runs: (gPrev.runs || 0) + 1, ts: now(), from: 'global' });
    log('get keep =', await storage.global.get('demo.keep', null));
    log('keys =', await storage.global.keys());

    // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ storage / workspace ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    log('‚Äî storage / workspace ‚Äî');
    try {
      const wPrev = await storage.workspace.get('demo.keep', { runs: 0 });
      await storage.workspace.set('demo.keep', { ...wPrev, runs: (wPrev.runs || 0) + 1, ts: now(), from: 'workspace' });
      log('get keep =', await storage.workspace.get('demo.keep', null));
      log('keys =', await storage.workspace.keys());
    } catch (e) {
      log('workspace storage not available:', e?.message || e);
    }

    // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ files API ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    const dirs = await files.dirs();
    log('dirs =', dirs);

    // Text file (global scope)
    const txtRel = 'demo/notes/hello.txt';
    const txtOld = (await files.exists('global', txtRel)) ? await files.readText('global', txtRel) : '';
    await files.writeText('global', txtRel, txtOld + `hello @ ${now()}\n`);
    log('readText:', await files.readText('global', txtRel));

    // JSON file (workspace if available, otherwise global)
    /** @type {'global'|'workspace'} */
    const jsonScope = dirs.workspace ? 'workspace' : 'global';
    const jsonRel = 'demo/data/sample.json';
    const cur = (await files.exists(jsonScope, jsonRel))
      ? await files.readJSON(jsonScope, jsonRel)
      : { a: 1, createdAt: now() };
    cur.updatedAt = now();
    cur.count = (cur.count || 0) + 1;
    await files.writeJSON(jsonScope, jsonRel, cur);
    log('readJSON:', await files.readJSON(jsonScope, jsonRel));

    // Binary file (Uint8Array)
    const binRel = 'demo/bin/demo.bin';
    await files.writeBytes('global', binRel, new Uint8Array([1, 2, 3, 4, 5, 254, 255]));
    log('readBytes demo.bin:', await files.readBytes('global', binRel));

    // Binary file (base64 string)
    const b64Rel = 'demo/bin/base64.bin';
    await files.writeBytes('global', b64Rel, Buffer.from([9, 8, 7, 6]).toString('base64'));
    log('exists base64.bin:', await files.exists('global', b64Rel));

    // Simulated list of known demo files + calculate size
    /** @type {Array<{scope:'global'|'workspace', rel:string, type:'text'|'json'|'bytes'}>} */
    const candidates = [
      { scope: 'global', rel: 'demo/notes/hello.txt', type: 'text' },
      { scope: jsonScope, rel: 'demo/data/sample.json', type: 'json' },
      { scope: 'global', rel: 'demo/bin/demo.bin', type: 'bytes' },
      { scope: 'global', rel: 'demo/bin/base64.bin', type: 'bytes' },
      { scope: 'global', rel: 'demo/tmp/kept.txt', type: 'text' },
    ];

    /** @type {Array<{scope:string, path:string, type:string, size:number}>} */
    const rows = [];
    for (const c of candidates) {
      if (await files.exists(c.scope, c.rel)) {
        let size = 0;
        if (c.type === 'text') {
          const s = await files.readText(c.scope, c.rel);
          size = Buffer.byteLength(s, 'utf8');
        } else if (c.type === 'json') {
          const obj = await files.readJSON(c.scope, c.rel);
          size = Buffer.byteLength(JSON.stringify(obj), 'utf8');
        } else {
          const b = await files.readBytes(c.scope, c.rel);
          size = b?.length || 0;
        }
        rows.push({ scope: c.scope, path: c.rel, type: c.type, size });
      }
    }
    log('files (simulated list):', rows);

    // // Optional cleanup ‚Äî user can delete via UI or uncomment below:
    // await secret.delete('github.token');
    // await storage.global.remove('demo.keep');
    // await storage.workspace.remove('demo.keep').catch(()=>{});
    // await files.remove('global', b64Rel);

    log('‚úî done', now());
    vm.stop();
  })().catch(e => {
    console.error('‚ùå', e);
    vm.stop();
  });
}

main();

// Bookmarks ‚Äî Sidebar version (using statusBarHelper.v1.sidebar)
// Same features as the original version: add current file / pick file / open / rename / edit (name+note) / copy path (in webview) / move up & down / search / import & export / repair path
// Important: do NOT use vscode.Uri to open file ‚Üí use string path with openTextDocument
// Storage: workspace first, fallback global; stop automatically when Sidebar is closed (or replaced)

const vscode = require('vscode');
const fs = require('fs');
const path = require('path');
const { storage, vm, sidebar } = statusBarHelper.v1;

(async function () {
  // Status bar text (optional)
  const setText = (t) => (typeof vm.setLabel === 'function' ? vm.setLabel(t) : vm.setText?.(t));
  const setTip  = (t) => vm.setTooltip?.(t);
  setText('Bookmarks'); setTip('Quick access to frequently used files');

  // Storage (workspace > global)
  const store = storage?.workspace || storage?.global;
  if (!store) throw new Error('statusBarHelper.v1.storage unavailable');
  const KEY = 'favorites.files.v6';
  const defState = { items: [] }; // {id,name,fsPath,note,ts}
  async function load(){ const s = await store.get(KEY, null); return (s && Array.isArray(s.items)) ? s : defState; }
  async function save(st){ await store.set(KEY, st); }

  // Remember last focused editor file path
  let lastFilePath = null;
  function rememberEditor(ed){ try{ const p = ed?.document?.uri?.fsPath; if(p) lastFilePath = String(p); }catch{} }
  rememberEditor(vscode.window.activeTextEditor);
  vscode.window.onDidChangeActiveTextEditor(rememberEditor);
  vscode.window.onDidChangeTextEditorSelection(e=>rememberEditor(e?.textEditor));

  // Path utilities
  function normalizeFsPath(p) {
    if (!p) return '';
    p = String(p).trim();
    if (process.platform === 'win32') {
      const isUNC = p.startsWith('\\\\');
      p = p.replace(/\//g, '\\');
      if (!isUNC) p = p.replace(/\\{2,}/g, '\\');
    } else {
      p = p.replace(/\\/g, '/').replace(/\/{2,}/g, '/');
    }
    return p;
  }
  function cleanPath(raw){
    if (!raw) return '';
    let p = String(raw)
      .replace(/^[\s'"]+|[\s'"]+$/g, '')
      .replace(/[\u200B-\u200D\uFEFF]/g, '');
    p = normalizeFsPath(p);
    try { p = path.resolve(p); } catch {}
    return p;
  }

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Sidebar Open ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  await sidebar.open({
    html: getHtml(),
    // Only stop VM when Sidebar is truly closed; if replaced by new 'open', ignore
    onClose: (reason) => { if (reason === 'closed') vm.stop(); }
  });
  // Reverse: when VM stops, restore Sidebar to default page
  vm.onStop(() => { try { sidebar.close(); } catch {} });

  // Webview ‚Üí Host messages
  sidebar.onMessage(async (msg) => {
    try {
      switch (msg?.type) {
        case 'ping': {
          sidebar.postMessage({ type: 'pong' });
          vscode.window.showInformationMessage('Bookmarks connected');
          break;
        }

        case 'req:init': {
          const st = await load();
          st.items = (st.items || []).map(x => ({
            id: String(x.id || ''),
            name: String(x.name || ''),
            fsPath: normalizeFsPath(String(x.fsPath || '')),
            note: String(x.note || ''),
            ts: Number(x.ts || Date.now())
          }));
          sidebar.postMessage({ type: 'init', state: st });
          break;
        }

        case 'save': {
          const st = msg.state;
          if (st && Array.isArray(st.items)){
            st.items = st.items.map(x=>({
              id: String(x.id||''),
              name: String(x.name||''),
              fsPath: normalizeFsPath(String(x.fsPath||'')),
              note: String(x.note||''),
              ts: Number(x.ts||Date.now())
            }));
            await save({ items: st.items });
            sidebar.postMessage({ type: 'saved' });
          }
          break;
        }

        case 'addCurrent': {
          const fp = normalizeFsPath(String(lastFilePath||'')) || null;
          sidebar.postMessage({ type: 'current', fsPath: fp });
          break;
        }

        case 'pickFile': {
          const picked = await vscode.window.showOpenDialog({
            canSelectFiles:true, canSelectFolders:false, canSelectMany:false, openLabel:'Select File'
          });
          const uri = picked?.[0];
          sidebar.postMessage({ type: 'picked', fsPath: normalizeFsPath(uri?.fsPath || '') || null });
          break;
        }

        // Open file using string path only (no Uri)
        case 'openFile': {
          const raw = (msg && msg.fsPath) ? String(msg.fsPath) : '';
          const fp  = cleanPath(raw);
          if (!fp || !fs.existsSync(fp)) {
            vscode.window.showWarningMessage('Cannot open file:\n' + (fp || raw || '(empty)'));
            break;
          }
          try {
            const doc = await vscode.workspace.openTextDocument(fp);
            await vscode.window.showTextDocument(doc, { preview: false });
          } catch (e) {
            vscode.window.showWarningMessage('Cannot open file:\n' + fp);
            console.log('[Bookmarks/openFile][fail]', String(e));
          }
          break;
        }

        case 'repath': {
          const id = String(msg.id || '');
          if (!id) break;
          const picked = await vscode.window.showOpenDialog({
            canSelectFiles: true, canSelectFolders: false, canSelectMany: false,
            openLabel: 'Select File (Repair Path)'
          });
          const uri = picked?.[0];
          const newPath = normalizeFsPath(uri?.fsPath || '');
          if (!newPath) break;

          const st = await load();
          const idx = st.items.findIndex(x => x.id === id);
          if (idx >= 0) {
            st.items[idx].fsPath = newPath;
            await save(st);
            sidebar.postMessage({ type: 'init', state: st });
            vscode.window.showInformationMessage('Path repaired');
          }
          break;
        }

        case 'copyPath': {
          const text = String(msg.fsPath || '');
          sidebar.postMessage({ type: 'doCopy', text });
          break;
        }

        case 'notify': {
          const m = msg.message || '';
          if (msg.level==='warn') vscode.window.showWarningMessage(m);
          else if (msg.level==='error') vscode.window.showErrorMessage(m);
          else vscode.window.showInformationMessage(m);
          break;
        }
      }
    } catch (e) {
      vscode.window.showErrorMessage('[Host Error] ' + (e?.message || String(e)));
    }
  });

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ HTML (Webview) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // No CSP meta, maximize compatibility (inline JS/CSS only; no external links)
  function getHtml(){
    return [
'<!doctype html><html lang="en"><head><meta charset="utf-8"><meta name="viewport" content="width=device-width, initial-scale=1"/>',
'<title>Bookmarks</title>',
'<style>',
'body{margin:0;background:#1e1e1e;color:#ddd;font:12px system-ui,-apple-system,Segoe UI,Roboto,Arial,sans-serif;}',
'.bar{display:flex;gap:8px;align-items:center;padding:8px;border-bottom:1px solid #3c3c3c;position:sticky;top:0;background:#1e1e1e;z-index:5;flex-wrap:wrap}',
'.btn{border:1px solid #3c3c3c;background:rgba(255,255,255,.03);color:#ddd;padding:6px 10px;border-radius:8px;cursor:pointer;font-size:12px}',
'.btn.primary{background:#0e639c;border-color:transparent;color:#fff}',
'.list{display:grid;grid-template-columns:1fr;gap:8px;padding:8px}',
'.card{border:1px solid #3c3c3c;border-radius:10px;background:rgba(255,255,255,.03);padding:8px;display:flex;flex-direction:column;gap:6px}',
'.row{display:flex;gap:6px;flex-wrap:wrap;align-items:center}',
'.title{margin:0;font-size:12px}',
'.path{margin:0;color:#999;font-size:11px;word-break:break-all}',
'.note{margin:0;color:#ccc;font-size:11px;white-space:pre-wrap;overflow:hidden;display:-webkit-box;-webkit-box-orient:vertical;-webkit-line-clamp:3}',
'input[type=text]{background:rgba(255,255,255,.03);border:1px solid #3c3c3c;color:#ddd;border-radius:8px;padding:6px 10px;font-size:12px;width:240px}',
'dialog{border:1px solid #3c3c3c;background:#1e1e1e;color:#ddd;border-radius:10px;padding:0;width:min(560px,90vw)}',
'dialog header{padding:8px;border-bottom:1px solid #3c3c3c}',
'dialog main{padding:8px}',
'textarea{width:100%;min-height:96px;background:rgba(255,255,255,.03);border:1px solid #3c3c3c;color:#ddd;border-radius:8px;padding:6px 10px;font-size:12px}',
'.flex-end{display:flex;gap:8px;justify-content:flex-end;margin-top:8px}',
'</style></head><body>',
