'<script>',
'var vscode = acquireVsCodeApi();',
'var state = {items:[]};',
'var listEl = document.getElementById("list"); var qEl = document.getElementById("q");',
'var editDlg=document.getElementById("editDlg"), fName=document.getElementById("fName"), fNote=document.getElementById("fNote"), editOk=document.getElementById("editOk"), editCancel=document.getElementById("editCancel");',
'var renameDlg=document.getElementById("renameDlg"), renameText=document.getElementById("renameText"), renameOk=document.getElementById("renameOk"), renameCancel=document.getElementById("renameCancel");',
'var pendingNewPath=null, editingId=null;',
'function send(t,d){ var o=d||{}; o.type=t; vscode.postMessage(o); }',
