  };
  document.getElementById('closeBoth').onclick = () => {
    vscode.postMessage({ type:'closeAll', from:'${who}' });
  };

  window.addEventListener('message', (e) => {
    const { type, text } = e.data || {};
    if (type === 'note') log(text);
  });
</script></body></html>`;

// 1) Open Sidebar
await sidebar.open({ 
  html: html('Sidebar'),
  onClose: ()=> vm.onStop()  
});

// 2) Open Tab (WebviewPanel)
const panel = vscode.window.createWebviewPanel(
  'sbh.dualDemo',
  'SBH Demo (Tab)',
  vscode.ViewColumn.Active,
  { enableScripts: true, retainContextWhenHidden: true }
);
panel.webview.html = html('Tab');

// Helpers to post messages
const sendToSidebar = (m) => sidebar.postMessage(m);
const sendToTab     = (m) => panel.webview.postMessage(m);

// 3) VM as a relay between both views
const offSidebarMsg = sidebar.onMessage((m) => {
  if (!m || typeof m !== 'object') return;
  if (m.type === 'relay')   sendToTab({ type:'note', text:`Sidebar ‚Üí ${m.text}` });
  if (m.type === 'closeAll') closeBoth();
});
const dTabMsg = panel.webview.onDidReceiveMessage((m) => {
  if (!m || typeof m !== 'object') return;
  if (m.type === 'relay')   sendToSidebar({ type:'note', text:`Tab ‚Üí ${m.text}` });
  if (m.type === 'closeAll') closeBoth();
});

// 4) Closing logic: if either closes, close both (guard against re-entry)
let closing = false;
function closeBoth() {
  if (closing) return;
  closing = true;
  try { panel.dispose(); } catch {}
  try { sidebar.close(); } catch {}
  try { vm.stop(); } catch {}
}
// If user closes one side manually, close the other too
const offSidebarClose = sidebar.onClose(() => {
  if (!closing) { closing = true; try { 
    vm.stop();
  } catch {} }
});
const dTabClose = panel.onDidDispose(() => {
  if (!closing) { closing = true; try { 
    vm.stop();
  } catch {} }
});

// 5) Initial notes
sendToSidebar({ type:'note', text:'Hello from VM ‚Üí Sidebar' });
sendToTab({ type:'note', text:'Hello from VM ‚Üí Tab' });

// 6) Cleanup when script stops
vm?.onStop?.(() => {
  try { offSidebarMsg?.dispose?.(); } catch {}
  try { offSidebarClose?.dispose?.(); } catch {}
  try { dTabMsg?.dispose?.(); } catch {}
  try { dTabClose?.dispose?.(); } catch {}
  try { panel.dispose(); } catch {}
  try { sidebar.close(); } catch {}
});

