  <script nonce="${nonce}">
    const CITIES = ${JSON.stringify(cities)};
    const vscodeApi = (typeof acquireVsCodeApi === 'function') ? acquireVsCodeApi() : null;

    const $ = (id)=>document.getElementById(id);
    const citySel=$('city'), btnRefresh=$('btnRefresh');
    const nowTemp=$('nowTemp'), nowWx=$('nowWx'), nowWind=$('nowWind'), updAt=$('updateAt');
    const dayMax=$('dayMax'), dayMin=$('dayMin'), list=$('list');

    // WMO weather code -> plain English text + an emoji helper
    const WMO={
      0:'Clear',1:'Partly cloudy',2:'Partly cloudy',3:'Overcast',45:'Fog',48:'Depositing rime fog',
      51:'Drizzle',53:'Drizzle',55:'Drizzle',56:'Freezing drizzle',57:'Freezing drizzle',
      61:'Rain',63:'Rain',65:'Heavy rain',66:'Freezing rain',67:'Freezing rain',
      71:'Snow',73:'Snow',75:'Heavy snow',77:'Snow grains',
      80:'Rain showers',81:'Rain showers',82:'Heavy showers',
      85:'Snow showers',86:'Heavy snow showers',
      95:'Thunderstorm',96:'Thunderstorm w/ hail',99:'Severe thunderstorm'
    };
    const ICON=(code)=>{ if([0].includes(code))return'‚òÄÔ∏è'; if([1,2].includes(code))return'‚õÖ';
      if([3].includes(code))return'‚òÅÔ∏è'; if([45,48].includes(code))return'üå´Ô∏è';
      if([51,53,55,56,57,61,63,65,66,67,80,81,82].includes(code))return'üåßÔ∏è';
      if([71,73,75,77,85,86].includes(code))return'‚ùÑÔ∏è'; if([95,96,99].includes(code))return'‚õàÔ∏è'; return'üå°Ô∏è'; };

    const LS_KEY='sbh_w_sidebar_pref_en';
    const loadPref=()=>{ try{return JSON.parse(localStorage.getItem(LS_KEY)||'{}');}catch{return{}}};
    const savePref=(o)=>localStorage.setItem(LS_KEY, JSON.stringify(o));
    const pref=loadPref(); citySel.value=String(pref.cityIndex??0);

    async function fetchWeather(lat,lon,tz){
      const url=new URL('https://api.open-meteo.com/v1/forecast');
      url.searchParams.set('latitude',lat); url.searchParams.set('longitude',lon);
      url.searchParams.set('timezone',tz||'auto');
      url.searchParams.set('current_weather','true');
      url.searchParams.set('hourly','temperature_2m,precipitation_probability,weathercode');
      url.searchParams.set('forecast_days','2'); // 2 days to ensure enough hours ahead
      const res=await fetch(url.toString(),{cache:'no-store'}); if(!res.ok) throw new Error('Network '+res.status);
      return res.json();
    }
    const hh=(d)=>String(new Date(d).getHours()).padStart(2,'0')+':00';
    const fmt=(d)=>new Date(d).toLocaleString();

    async function refresh(){
      const idx=Number(citySel.value)||0; const c=CITIES[idx]; savePref({cityIndex:idx});
      const data=await fetchWeather(c.lat,c.lon,c.tz);

      // Current
      const cw=data.current_weather||{};
      nowTemp.textContent=(cw.temperature!=null?cw.temperature.toFixed(1):'--')+'¬∞';
      nowWx.textContent=(ICON(cw.weathercode)+' '+(WMO[cw.weathercode]||'‚Äî'));
      nowWind.textContent=(cw.windspeed!=null?cw.windspeed:'--')+' m/s';
      updAt.textContent=fmt(new Date());
      const d=data.daily||{};
      if(d.temperature_2m_max){ dayMax.textContent=d.temperature_2m_max[0].toFixed(1)+'¬∞'; dayMin.textContent=d.temperature_2m_min[0].toFixed(1)+'¬∞'; }

      // Next 24 hours
      const h=data.hourly||{}; const times=h.time||[];
      let start=times.indexOf(cw.time);
      if(start<0){
        const now=new Date(); const y=now.getFullYear(),m=now.getMonth(),da=now.getDate(),hr=now.getHours();
        const isoHour=new Date(y,m,da,hr,0,0).toISOString().slice(0,13);
        start=times.findIndex(t=>t.startsWith(isoHour));
        if(start<0) start=0;
      }

      const items=[];
      for(let i=0;i<24 && start+i<times.length;i++){
        const k=start+i;
        items.push({
          hh: hh(times[k]),
          t:  h.temperature_2m?.[k],
          p:  Math.max(0, Math.min(100, h.precipitation_probability?.[k] ?? 0)),
          ic: ICON(h.weathercode?.[k])
        });
      }

      // Time | Temp | Precip progress | Icon + %
      list.innerHTML = items.map(it =>
        '<div class="item" role="listitem">' +
          '<div class="time">'+ it.hh +'</div>' +
          '<div class="tval">'+ (it.t!=null?it.t.toFixed(1):'--') +'¬∞</div>' +
          '<div class="bar" aria-label="Precip '+ it.p +'%"><div class="fill" style="width:'+ it.p +'%"></div></div>' +
          '<div class="emoji">'+ it.ic +' <span class="pct">'+ it.p +'%</span></div>' +
        '</div>'
      ).join('');
    }

    citySel.addEventListener('change',refresh);
    btnRefresh.addEventListener('click',refresh);

    refresh().catch(err=>{ updAt.textContent='Load failed: '+String(err).slice(0,80); });
  </script>
