}

// Gemini ‚Äî Copilot Chat (Status Bar Helper script)
// Storage: API Key is saved in secret; other fields are stored in storage (workspace > global).
// All keys use prefix gemini_chat.*; responses are rendered in Markdown.
// When calling Gemini, we include systemInstruction, conversation history, and attachments (text/binary).

const vscode = require('vscode');
const { secret, storage, vm, sidebar } = statusBarHelper.v1;

/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Storage Utilities ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
const store = (storage && storage.workspace) || (storage && storage.global) || null;
if (!store) throw new Error('statusBarHelper.v1.storage is not available');

const K = (name) => `gemini_chat.${name}`;
const SECRET_KEY = K('apiKey');

const FIELD_KEYS = [
  'customerServiceName', 'tone', 'verbosity', 'banned',
  'kb', 'kbFiles', 'model', 'temperature', 'maxTokens',
  'safety', 'history'
];

async function sget (name, def) {
  try { return await store.get(K(name), def); } catch { return def; }
}
async function sset (name, val) {
  try { await store.set(K(name), val); return null; } catch (e) { return String(e && e.message || e); }
}
async function sremove (name) {
  try { await store.remove(K(name)); } catch {}
}

/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Sidebar lifecycle ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
(async function main () {
  await sidebar.open({
    html: getHtml(),
    onClose: (reason) => { if (reason === 'closed') vm.stop(); }
  });
  vm.onStop(() => { try { sidebar.close(); } catch {} });
})();

/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Webview ‚Üî Host ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
sidebar.onMessage(async (msg) => {
  try {
    const t = msg && msg.type;

    if (t === 'ping') {
      sidebar.postMessage({ type: 'pong' });
      return;
    }

    if (t === 'req:init') {
      const apiKey = (await secret.get(SECRET_KEY)) || '';
      const [
        customerServiceName, tone, verbosity, banned,
        kb, kbFiles, model, temperature, maxTokens,
        safety, history
      ] = await Promise.all([
        sget('customerServiceName', 'Assistant'),
        sget('tone', 'Friendly & Professional'),
        sget('verbosity', 'Moderate'),
        sget('banned', ''),
        sget('kb', ''),
        sget('kbFiles', []),
        sget('model', 'gemini-1.5-flash'),
        sget('temperature', 0.3),
        sget('maxTokens', 2048),
        sget('safety', 'neutral'),
        sget('history', [])
      ]);

      sidebar.postMessage({
        type: 'init',
        data: {
          apiKey, customerServiceName, tone, verbosity, banned,
          kb, kbFiles: Array.isArray(kbFiles) ? kbFiles : [],
          model, temperature, maxTokens, safety,
          history: Array.isArray(history) ? history : []
        }
      });
      return;
    }

    if (t === 'save') {
      const d = (msg && msg.data) || {};
      const errors = {};

      // secret
      try { await secret.set(SECRET_KEY, String(d.apiKey || '')); }
      catch (e) { errors.apiKey = String(e && e.message || e); }

      // storage
      for (const k of FIELD_KEYS) {
        if (!(k in d)) continue;
        const err = await sset(k, d[k]);
        if (err) errors[k] = err;
      }

      sidebar.postMessage({ type: 'saved', errors });
      return;
    }

    if (t === 'history.set') {
      const arr = Array.isArray(msg.history) ? msg.history : [];
      await sset('history', arr);
      return;
    }

    if (t === 'openExternal') {
      const url = String(msg.url || '');
      if (url) {
        try { await vscode.env.openExternal(vscode.Uri.parse(url)); }
        catch (e) { vscode.window.showWarningMessage('Cannot open link: ' + url); }
      }
      return;
    }

    if (t === 'storage.clearAll') {
      await Promise.all(FIELD_KEYS.map(sremove));
      await secret.set(SECRET_KEY, '');
      sidebar.postMessage({ type: 'saved', errors: {} });
      return;
    }

  } catch (e) {
    try { sidebar.postMessage({ type: 'error', message: String(e && e.message || e) }); } catch {}
  }
});

