/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Webview script (data:URI) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
function getClientJs () {
  return `
(function(){
  // ===== Bridge & Polyfills =====
  var vscode = acquireVsCodeApi();
  function send(type, data){ var o=data||{}; o.type=type; vscode.postMessage(o); }
  function $(s){ return document.querySelector(s); }

  if (typeof window.URL === 'undefined' && typeof self !== 'undefined') { window.URL = self.URL; }
  var AbortC = (typeof AbortController !== 'undefined') ? AbortController : null;

  // ===== Elements =====
  var dlg=$("#dlg"), btnSave=$("#btnSave"), btnSettings=$("#btnSettings"), btnClear=$("#btnClear");
  var apiKeyEl=$("#apiKey"), agentNameEl=$("#agentName"), toneEl=$("#tone"), verbEl=$("#verbosity"), bannedEl=$("#banned"), kbEl=$("#kb");
  var kbList=$("#kbList"), kbFileEl=$("#kbFile"), btnKbAdd=$("#btnKbAdd");
  var modelSel=$("#model"), temp=$("#temp"), tempV=$("#tempV"), tokens=$("#tokens"), tokensV=$("#tokensV"), safetySel=$("#safety");
  var content=$("#content"), inputEl=$("#input"), btnSend=$("#btnSend"), btnStop=$("#btnStop");
  var attList=$("#attList"), fileEl=$("#file"), btnFile=$("#btnFile");

  var state={
    apiKey:"", customerServiceName:"Assistant", tone:"Friendly & Professional", verbosity:"Moderate", banned:"",
    kb:"", kbFiles:[],
    attFiles:[], // temporary attachments per-message
    model:"gemini-1.5-flash", temperature:0.3, maxTokens:2048, safety:"neutral", history:[]
  };

  // ===== Toast =====
  function toast(t){
    var d=document.createElement('div');
    d.textContent=t;
    d.style.cssText='position:fixed;right:12px;bottom:12px;background:#183a2e;border:1px solid #2f5d4b;color:#fff;padding:8px 10px;border-radius:8px;z-index:9;opacity:.98';
    document.body.appendChild(d);
    setTimeout(function(){d.remove();},2200);
  }

  // ===== Minimal Markdown renderer =====
  function escapeHtml(s){ return String(s).replace(/[&<>"]/g, function(c){return c==='&'?'&amp;':c==='<'?'&lt;':c==='>'?'&gt;':'&quot;';}); }
  function mdLinks(html){
    return html.replace(/\\[([^\\]]+)\\]\\(([^)\\s]+)\\)/g, function(_,text,url){
      var safe = escapeHtml(text); var u = escapeHtml(url);
      return '<a href="'+u+'" data-ext="'+u+'">'+safe+'</a>';
    });
  }
  function mdInline(html){
    html = html.replace(/\\\`([^\\\`]+)\\\`/g, '<code>$1</code>');
    html = html.replace(/\\*\\*([^*]+)\\*\\*/g, '<strong>$1</strong>');
    html = html.replace(/\\*([^*]+)\\*/g, '<em>$1</em>');
    return mdLinks(html);
  }
  function renderMarkdown(src){
    if(!src) return '';
    var s = escapeHtml(String(src));
    s = s.replace(/\\n?\\\`\\\`\\\`([a-zA-Z0-9_-]+)?\\n([\\s\\S]*?)\\n\\\`\\\`\\\`/g,
      function(_,lang,code){ return '<pre><code class="lang-'+(lang||'')+'">'+code.replace(/</g,'&lt;')+'</code></pre>'; });

    var out=[], lines=s.split(/\\r?\\n/), i=0;
    while(i<lines.length){
      var line=lines[i];
      var m=line.match(/^(#{1,6})\\s+(.*)$/);
      if(m){ out.push('<h'+m[1].length+'>'+mdInline(m[2])+'</h'+m[1].length+'>'); i++; continue; }
      if(/^>\\s?/.test(line)){ var buf=[]; while(i<lines.length && /^>\\s?/.test(lines[i])){ buf.push(lines[i].replace(/^>\\s?/, '')); i++; } out.push('<blockquote>'+mdInline(buf.join('\\n'))+'</blockquote>'); continue; }
      if(/^\\s*[-*+]\\s+/.test(line)){ var items=[]; while(i<lines.length && /^\\s*[-*+]\\s+/.test(lines[i])){ items.push('<li>'+mdInline(lines[i].replace(/^\\s*[-*+]\\s+/, ''))+'</li>'); i++; } out.push('<ul>'+items.join('')+'</ul>'); continue; }
      if(/^\\s*\\d+\\.\\s+/.test(line)){ var items2=[]; while(i<lines.length && /^\\s*\\d+\\.\\s+/.test(lines[i])){ items2.push('<li>'+mdInline(lines[i].replace(/^\\s*\\d+\\.\\s+/, ''))+'</li>'); i++; } out.push('<ol>'+items2.join('')+'</ol>'); continue; }
      if(/^\\s*([-*_])\\1{2,}\\s*$/.test(line)){ out.push('<hr/>'); i++; continue; }
      if(line.trim().length){ var buf2=[line]; i++; while(i<lines.length && lines[i].trim().length && !/^(#{1,6})\\s+|^>\\s?|^\\s*[-*+]\\s+|^\\s*\\d+\\.\\s+|^\\s*([-*_])\\1{2,}\\s*$/.test(lines[i])){ buf2.push(lines[i]); i++; } out.push('<p>'+mdInline(buf2.join(' '))+'</p>'); continue; }
      i++;
    }
    return '<div class="md">'+out.join('')+'</div>';
  }

  function hookExternalLinks(root){
    (root||document).querySelectorAll('a[data-ext]').forEach(function(a){
      a.addEventListener('click', function(ev){
        ev.preventDefault();
        var url = a.getAttribute('data-ext') || a.href;
        if(url){ send('openExternal', { url:url }); }
      });
    });
  }

  // ===== Chat bubbles =====
  function addBubble(role, html){
    var wrap=document.createElement('div'); wrap.className='bubble';
    var r=document.createElement('div'); r.className='role'; r.textContent = (role==='user'?'You':'Gemini'); wrap.appendChild(r);
    var b=document.createElement('div'); b.className='body'; b.innerHTML = html; wrap.appendChild(b);
    content.appendChild(wrap);
    content.scrollTop=content.scrollHeight;
    hookExternalLinks(b);
    return b;
  }
  function addUser(text){ return addBubble('user', renderMarkdown(text)); }
  function addBot(text){ return addBubble('assistant', renderMarkdown(text)); }
  function addBotThinking(){ return addBubble('assistant', '<div class="md"><em>‚Ä¶thinking</em></div>'); }
  function replaceBody(el, text){ if(!el) return; el.innerHTML = renderMarkdown(text); hookExternalLinks(el); }

  // ===== Init messages from host =====
  window.addEventListener('message', function(e){
    var m=e.data||{};
    if(m.type==='init'){
      Object.assign(state, m.data||{});
      apiKeyEl.value=state.apiKey||'';
      agentNameEl.value=state.customerServiceName||'';
      toneEl.value=state.tone||'Friendly & Professional';
      verbEl.value=state.verbosity||'Moderate';
      bannedEl.value=state.banned||'';
      kbEl.value=state.kb||'';
      modelSel.value=state.model||'gemini-1.5-flash';
      temp.value=String(state.temperature||0.3); tempV.textContent=temp.value;
      tokens.value=String(state.maxTokens||2048); tokensV.textContent=tokens.value;
      safetySel.value=state.safety||'neutral';
      renderKbList(); renderAttList(); renderHistory();
    }
    if(m.type==='saved'){
      if(m.errors && Object.keys(m.errors).length){ toast('Some fields failed to save (see Console).'); console.log('[saved errors]', m.errors); }
      else toast('Saved.');
      dlg.style.display='none';
    }
    if(m.type==='error'){ toast('Error: '+(m.message||'')); }
  });

  function renderHistory(){
    content.innerHTML='';
    (state.history||[]).forEach(function(x){ addBubble(x.role, renderMarkdown(x.content||'')); });
  }

  // ===== File helpers (shared) =====
  function isTextLike(file){
    if (file.type && file.type.indexOf('text/') === 0) return true;
    var n=file.name||''; return /\\.(txt|md|markdown|csv|json|log|yaml|yml|xml|html|css|js|ts|tsx|jsx|py|rb|php|java|kt|go|rs|c|cpp|h|hpp|cs|sql)$/i.test(n);
  }
  function toBase64FromDataURL(s){ var i=s.indexOf(','); return i>=0 ? s.slice(i+1) : s; }

  function readFilesTo(targetArr, files, onDone){
    var max=1024*1024, list=[].slice.call(files||[]), pending=list.length; if(!pending){ onDone&&onDone(); return; }
    for(var k=0;k<list.length;k++){
      (function(file){
        if(file.size>max){ pending--; if(!pending) onDone&&onDone(); return; }
        var item={ name:file.name, size:file.size, mime:(file.type||'application/octet-stream'), kind:'text', text:'', base64:'' };
        if(isTextLike(file)){
          var fr=new FileReader();
          fr.onload=function(){ item.kind='text'; item.text=String(fr.result||''); targetArr.push(item); pending--; if(!pending) onDone&&onDone(); };
          fr.onerror=function(){ pending--; if(!pending) onDone&&onDone(); };
          fr.readAsText(file);
        }else{
          var fr2=new FileReader();
          fr2.onload=function(){ item.kind='binary'; item.base64=toBase64FromDataURL(String(fr2.result||'')); targetArr.push(item); pending--; if(!pending) onDone&&onDone(); };
          fr2.onerror=function(){ pending--; if(!pending) onDone&&onDone(); };
          fr2.readAsDataURL(file);
        }
      })(list[k]);
    }
  }

  // ===== KB files (settings dialog) =====
  function renderKbList(){
    kbList.innerHTML=(state.kbFiles||[]).map(function(a,idx){
      var sz=a.size?(Math.round(a.size/1024)+'KB'):'';
      return '<span class="chip" data-i="'+idx+'">'+ (a.name||'file') +' <span class="muted">'+sz+'</span> <span class="x" title="Remove">‚úï</span></span>';
    }).join('');
    var xs=kbList.querySelectorAll('.chip .x');
    for(var i=0;i<xs.length;i++){
      xs[i].addEventListener('click', function(ev){
        var p=ev.target.closest('.chip'); var ii=Number(p.getAttribute('data-i'));
        state.kbFiles.splice(ii,1); renderKbList();
      });
    }
  }
  btnKbAdd.addEventListener('click', function(){ kbFileEl.click(); });
  kbFileEl.addEventListener('change', function(){ readFilesTo(state.kbFiles, kbFileEl.files, renderKbList); kbFileEl.value=''; });

  // ===== Temporary chat attachments =====
  function renderAttList(){
    attList.innerHTML=(state.attFiles||[]).map(function(a,idx){
      var sz=a.size?(Math.round(a.size/1024)+'KB'):'';
      return '<span class="chip" data-i="'+idx+'">'+ (a.name||'file') +' <span class="muted">'+sz+'</span> <span class="x" title="Remove">‚úï</span></span>';
    }).join('');
    var xs=attList.querySelectorAll('.chip .x');
    for(var i=0;i<xs.length;i++){
      xs[i].addEventListener('click', function(ev){
        var p=ev.target.closest('.chip'); var ii=Number(p.getAttribute('data-i'));
        state.attFiles.splice(ii,1); renderAttList();
      });
    }
  }
  btnFile.addEventListener('click', function(){ fileEl.click(); });
  fileEl.addEventListener('change', function(){ readFilesTo(state.attFiles, fileEl.files, renderAttList); fileEl.value=''; });

  // ===== Prompt builders =====
  function buildSystemText(state) {
    const name = state.customerServiceName || 'Assistant';
    const tone = state.tone || 'Friendly & Professional';
    const verbosity = state.verbosity || 'Moderate';
    const banned = String(state.banned || '').split(/[Ôºå,„ÄÅ\\s]+/).filter(Boolean);

    const lines = [
      'You are "' + name + '". Reply in English.',
      'Tone: ' + tone + '.',
      (verbosity === 'Concise'
        ? 'Keep answers concise (about 2‚Äì4 lines) with bullet points.'
        : (verbosity === 'Detailed'
          ? 'Answers can be more detailed. Include steps and small examples when useful.'
          : 'Keep a moderate length with clear bullet points.')),
      (banned.length ? ('Avoid the following words: ' + banned.join(', ') + '. If needed, paraphrase.') : '')
    ];
    if (state.kb) lines.push('Reference materials (quote when relevant):\\n' + state.kb);
    return lines.filter(Boolean).join('\\n\\n');
  }

  function mapHistoryToContents(history){
    return (history||[]).slice(-10).map(function(m){
      return { role: (m.role === 'assistant' ? 'model' : 'user'), parts: [{ text: String(m.content || '') }] };
    });
  }

  function buildUserPartsWithRefs(prompt, kbFiles, attFiles){
    var parts = [];
    // persistent kb files
    (kbFiles||[]).forEach(function(f){
      if (f && f.kind === 'text' && f.text) {
        parts.push({ text: '[KB: ' + (f.name||'file') + ']\\n' + String(f.text) });
      } else if (f && f.base64) {
        parts.push({ text: '[KB (non-text)]: ' + (f.name||'file') });
        parts.push({ inlineData: { mimeType: (f.mime||'application/octet-stream'), data: f.base64 } });
      }
    });
    // per-message attachments
    (attFiles||[]).forEach(function(f){
      if (f && f.kind === 'text' && f.text) {
        parts.push({ text: '[Attachment: ' + (f.name||'file') + ']\\n' + String(f.text) });
      } else if (f && f.base64) {
        parts.push({ text: '[Attachment (non-text)]: ' + (f.name||'file') });
        parts.push({ inlineData: { mimeType: (f.mime||'application/octet-stream'), data: f.base64 } });
      }
    });
    parts.push({ text: String(prompt||'') });
    return parts;
  }

  // ===== Call Gemini =====
  var inflight = null;
  function setBusy(on){
    btnSend.disabled = on;
    btnStop.classList.toggle('hidden', !on);
    inputEl.disabled = on;
  }

  async function callGemini(prompt){
    var apiKey = state.apiKey || '';
    if(!apiKey){ toast('Please set your Gemini API Key in Settings first.'); return '(error) API Key is not configured.'; }

    var url = 'https://generativelanguage.googleapis.com/v1beta/models/'
      + encodeURIComponent(state.model||'gemini-1.5-flash')
      + ':generateContent?key=' + encodeURIComponent(apiKey);

    var thr = (state.safety === 'strict')
      ? 'BLOCK_MEDIUM_AND_ABOVE'
      : (state.safety === 'relaxed' ? 'BLOCK_NONE' : 'BLOCK_ONLY_HIGH');

    var payload = {
      systemInstruction: { parts: [{ text: buildSystemText(state) }] },
      contents: [
        ...mapHistoryToContents(state.history),
        { role: 'user', parts: buildUserPartsWithRefs(prompt, state.kbFiles, state.attFiles) }
      ],
      generationConfig: {
        temperature: Number(state.temperature||0.3),
        maxOutputTokens: Number(state.maxTokens||2048)
      },
      safetySettings: [
        { category: 'HARM_CATEGORY_HATE_SPEECH',       threshold: thr },
        { category: 'HARM_CATEGORY_SEXUALLY_EXPLICIT', threshold: thr },
        { category: 'HARM_CATEGORY_DANGEROUS_CONTENT', threshold: thr },
        { category: 'HARM_CATEGORY_HARASSMENT',        threshold: thr },
        { category: 'HARM_CATEGORY_CIVIC_INTEGRITY',   threshold: thr }
      ]
    };

    var opts = { method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify(payload) };
    if (AbortC){ inflight = new AbortC(); opts.signal = inflight.signal; } else { inflight = { abort:function(){} }; }

    try{
      var res = await fetch(url, opts);
      if(!res.ok){ throw new Error('HTTP '+res.status+' : '+(await res.text())); }
      var j = await res.json();
      var text = j && j.candidates && j.candidates[0] && j.candidates[0].content && j.candidates[0].content.parts && j.candidates[0].content.parts[0] && j.candidates[0].content.parts[0].text || '';
      return text || '(no content)';
    }catch(e){
      return '(error) ' + (e && e.message || String(e));
    }finally{
      inflight = null;
    }
  }

  async function onSend(){
    var msg = (inputEl.value||'').trim();
    if(!msg) return;
    inputEl.value='';
    addUser(msg);
    var thinkingBody = addBotThinking();
    setBusy(true);
    var reply = await callGemini(msg);
    replaceBody(thinkingBody, reply);
    state.history.push({ role:'user', content:msg });
    state.history.push({ role:'assistant', content:reply });
    send('history.set', { history: state.history });
    setBusy(false);
    // If you prefer clearing attachments after each send, uncomment:
    // state.attFiles = []; renderAttList();
  }

  // ===== IME guard =====
  var __imeComposing = false;
  inputEl.addEventListener('compositionstart', function(){ __imeComposing = true; });
  inputEl.addEventListener('compositionend',   function(){ __imeComposing = false; });
  inputEl.addEventListener('keydown', function(e){
    if (__imeComposing && e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      e.stopImmediatePropagation();
    }
  }, true);
  (function(){ var __origOnSend = onSend; onSend = function(){ if(__imeComposing) return; return __origOnSend.apply(this, arguments); }; })();

  // ===== Events =====
  btnSend.addEventListener('click', onSend);
  inputEl.addEventListener('keydown', function(ev){
    if(ev.key==='Enter' && !ev.shiftKey){ ev.preventDefault(); onSend(); }
  });
  btnStop.addEventListener('click', function(){
    if(inflight && inflight.abort) try{ inflight.abort(); }catch{}
    setBusy(false);
  });

  // ===== Save settings =====
  btnSave.addEventListener('click', function(){
    state.apiKey=(apiKeyEl.value||'').trim();
    state.customerServiceName=(agentNameEl.value||'').trim();
    state.tone=toneEl.value||'Friendly & Professional';
    state.verbosity=verbEl.value||'Moderate';
    state.banned=(bannedEl.value||'').trim();
    state.kb=(kbEl.value||'').trim();
    state.model=modelSel.value||'gemini-1.5-flash';
    state.temperature=Number(temp.value||0.3);
    state.maxTokens=Number(tokens.value||2048);
    state.safety=safetySel.value||'neutral';
    send('save',{data:{
      apiKey:state.apiKey, customerServiceName:state.customerServiceName, tone:state.tone, verbosity:state.verbosity, banned:state.banned, kb:state.kb, kbFiles:state.kbFiles, model:state.model, temperature:state.temperature, maxTokens:state.maxTokens, safety:state.safety
    }});
  });

  // ===== Misc UI =====
  btnSettings.addEventListener('click', function(){ dlg.style.display='flex'; setTimeout(function(){ apiKeyEl.focus(); },0); });
  btnClear.addEventListener('click', function(){ state.history=[]; send('history.set',{history:[]}); content.innerHTML=''; });
  temp.addEventListener('input', function(){ tempV.textContent=temp.value; });
  tokens.addEventListener('input', function(){ tokensV.textContent=tokens.value; });

  // Handshake
  send('ping'); send('req:init');
})();
`;
}

// SBH ‚Äî System Monitor: Status Bar + Sidebar (Persistent mode/frequency, English labels, compact layout)
// - Status bar: CPU shows only $(pulse); RAM shows $(server)
// - Display mode: percent | detail (RAM used/total + percent), persisted via storage.global
// - Refresh interval: 0.5s / 1s / 2s / 5s, persisted via storage.global
// - Sidebar: sections "System Overview" (English labels), per-core bars, memory details; tighter spacing

const vscode = require("vscode");
const os = require("os");
const { monitorEventLoopDelay } = require("perf_hooks");
const { setInterval, clearInterval, setTimeout, clearTimeout } = require("timers");
const { vm, sidebar, storage } = statusBarHelper.v1;

(function main() {
  const K_MODE = "sbh.sys.monitor.mode";
  const K_INTERVAL = "sbh.sys.monitor.intervalMs";

  const item = vscode.window.createStatusBarItem(vscode.StatusBarAlignment.Right, 1000);
  item.name = "System Monitor";
  item.command = "sbh.sys.monitor.menu";
  item.tooltip = "System Monitor: click to toggle mode / adjust refresh rate / pause / open sidebar";
  item.show();

  let mode = "percent";          
  let intervalMs = 1000;         
  let paused = false;
  let timer = null, warmupT = null;

  const eld = monitorEventLoopDelay({ resolution: 10 });
  eld.enable();

  (async () => {
    try {
      const savedMode = await storage.global.get(K_MODE);
      if (savedMode === "percent" || savedMode === "detail") mode = savedMode;
    } catch {}
    try {
      const savedInterval = await storage.global.get(K_INTERVAL);
      if ([500, 1000, 2000, 5000].includes(savedInterval)) intervalMs = savedInterval;
    } catch {}
    startLoop();
  })();

  const disposables = [];
  disposables.push(
    vscode.commands.registerCommand("sbh.sys.monitor.menu", async () => {
      const pick = await vscode.window.showQuickPick(
        [
          { label: mode === "percent" ? "Switch to: Detail Mode" : "Switch to: Percent Mode", value: "toggleMode" },
          { label: "Open Sidebar (Details)", value: "openSidebar" },
          { label: intervalMs === 500 ? "‚úì Every 0.5s" : "Every 0.5s", value: 500 },
          { label: intervalMs === 1000 ? "‚úì Every 1s" : "Every 1s", value: 1000 },
          { label: intervalMs === 2000 ? "‚úì Every 2s" : "Every 2s", value: 2000 },
          { label: intervalMs === 5000 ? "‚úì Every 5s" : "Every 5s", value: 5000 },
          { label: paused ? "‚ñ∂ Resume" : "‚è∏ Pause", value: "toggle" },
          { label: "‚úñ Close (Stop Script)", value: "stop" },
        ],
        { placeHolder: "System Monitor: display mode / refresh rate / sidebar" }
      );
      if (!pick) return;

      if (pick.value === "toggleMode") {
        mode = mode === "percent" ? "detail" : "percent";
        try { await storage.global.set(K_MODE, mode); } catch {}
        tick();
        return;
      }
      if (pick.value === "openSidebar") { openSidebar(); return; }
      if (pick.value === "toggle") {
        paused = !paused;
        if (paused) { stopLoop(); tick({ paused: true }); item.tooltip = "Paused (click to resume or adjust)"; }
        else { item.tooltip = "System Monitor: click to toggle mode / adjust refresh rate / pause / open sidebar"; startLoop(); }
        return;
      }
      if (pick.value === "stop") { try { vm.stop(); } catch {} return; }

      if (typeof pick.value === "number") {
        intervalMs = pick.value;
        try { await storage.global.set(K_INTERVAL, intervalMs); } catch {}
        if (!paused) startLoop();
      }
    })
  );

  let prevTotal = snapshotCpuTotal();
  let prevCores = snapshotCpuCores();

  function snapshotCpuTotal() {
    const list = os.cpus();
    let idle = 0, total = 0;
    for (const c of list) {
      const t = c.times;
      idle += t.idle;
      total += t.user + t.nice + t.sys + t.irq + t.idle;
    }
    return { idle, total };
  }
  function snapshotCpuCores() {
    return os.cpus().map(c => {
      const t = c.times;
      return { idle: t.idle, total: t.user + t.nice + t.sys + t.irq + t.idle };
    });
  }
  function cpuPercentTotal() {
    const cur = snapshotCpuTotal();
    const idleDelta = cur.idle - prevTotal.idle;
    const totalDelta = cur.total - prevTotal.total;
    prevTotal = cur;
    return totalDelta > 0 ? clamp01(1 - idleDelta / totalDelta) : 0;
  }
  function cpuPercentPerCore() {
    const cur = snapshotCpuCores();
    const out = cur.map((c, i) => {
      const idleDelta = c.idle - prevCores[i].idle;
      const totalDelta = c.total - prevCores[i].total;
      return totalDelta > 0 ? clamp01(1 - idleDelta / totalDelta) : 0;
    });
    prevCores = cur;
    return out;
  }

  function memStats() {
    const total = os.totalmem();
    const free  = os.freemem();
    const used  = Math.max(0, total - free);
    const pct   = total > 0 ? used / total : 0;
    return { total, free, used, pct: clamp01(pct) };
  }

  function clamp01(x){ return x < 0 ? 0 : x > 1 ? 1 : x; }
  function pct0(p){ return (p * 100).toFixed(0) + "%"; }
  function pct1(p){ return (p * 100).toFixed(1) + "%"; }
  function bytesToGB(n){ return (n / (1024 ** 3)).toFixed(1) + " GB"; }
  function formatUptime(sec){
    const d = Math.floor(sec / 86400);
    const h = Math.floor((sec % 86400) / 3600);
    const m = Math.floor((sec % 3600) / 60);
    return `${d}d ${h}h ${m}m`;
  }

  function renderText(cpuPct, mem, opt = {}) {
    const pausedMark = opt.paused ? " ‚è∏" : "";
    if (mode === "detail") {
      return `$(pulse) ${pct0(cpuPct)} ¬∑ $(server) ${bytesToGB(mem.used)}/${bytesToGB(mem.total)} (${pct0(mem.pct)})${pausedMark}`;
    }
    return `$(pulse) ${pct0(cpuPct)} ¬∑ $(server) ${pct0(mem.pct)}${pausedMark}`;
  }

  function renderTooltip(cpuPct, mem) {
    const la = os.loadavg?.() || [];
    const pm = process.memoryUsage?.() || {};
    const pmStr = pm.rss ? `Process RSS: ${bytesToGB(pm.rss)}` : "";
    return [
      `CPU: ${pct1(cpuPct)}`,
      mode === "detail"
        ? `Memory: ${bytesToGB(mem.used)} / ${bytesToGB(mem.total)} (${pct1(mem.pct)})`
        : `Memory: ${pct1(mem.pct)} (Used ${bytesToGB(mem.used)} / Total ${bytesToGB(mem.total)})`,
      `Load Avg: ${la.map(n => n.toFixed(2)).join(" / ")}`,
      pmStr,
      `Uptime: ${formatUptime(os.uptime?.() || 0)}`,
      "Click to switch mode / adjust refresh rate / pause / open sidebar",
    ].filter(Boolean).join("\n");
  }

  let lastCpuPct = 0, lastMem = memStats(), lastCoresPct = [];
  function tick(opt={}) {
    const cpu = cpuPercentTotal();
    const cores = cpuPercentPerCore();
    const mem = memStats();
    lastCpuPct = cpu; lastMem = mem; lastCoresPct = cores;

    item.text = renderText(cpu, mem, opt);
    item.tooltip = renderTooltip(cpu, mem);

    if (_sidebarOpen) {
      const ldMean = Math.max(0, eld.mean / 1e6 || 0);
      const ldP95  = Math.max(0, eld.percentiles?.[95] / 1e6 || 0);
      sidebar.postMessage({
        type: "metrics",
        payload: {
          ts: Date.now(),
          cpuPct: cpu,
          coresPct: cores,
          mem,
          loadavg: os.loadavg?.() || [],
          uptime: os.uptime?.() || 0,
          arch: os.arch?.() || "",
          platform: os.platform?.() || "",
          release: os.release?.() || "",
          cpumodel: (os.cpus?.()[0]?.model) || "",
          elDelay: { meanMs: +ldMean.toFixed(2), p95Ms: +ldP95.toFixed(2) },
        },
      });
    }
  }
  function startLoop() {
    stopLoop();
    warmupT = setTimeout(() => { tick(); timer = setInterval(tick, intervalMs); }, 400);
  }
  function stopLoop() {
    if (timer) { clearInterval(timer); timer = null; }
    if (warmupT) { clearTimeout(warmupT); warmupT = null; }
  }

  let _sidebarOpen = false;
  async function openSidebar() {
    const html = buildSidebarHTML();
    await sidebar.open({
      html,
      focus: true,
      onClose: () => { _sidebarOpen = false; },
    });
    _sidebarOpen = true;
  }

  function buildSidebarHTML() {
    const nonce = Math.random().toString(36).slice(2);
    return `
<meta http-equiv="Content-Security-Policy"
  content="default-src 'none'; img-src data:; style-src 'unsafe-inline'; script-src 'nonce-${nonce}';">
<style>
  :root {
    --fg: var(--vscode-foreground, #ddd);
    --muted: #9aa0a6;
    --card: rgba(255,255,255,0.04);
    --barBg: rgba(255,255,255,0.10);
  }
  body { font: 13px/1.6 -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica,Arial; color: var(--fg);}
  h2 { margin: 0 0 8px; font-size: 16px; }
  .section { background: var(--card); border-radius: 12px; padding: 12px; margin-bottom: 12px; }
  .kv { display: grid; grid-template-columns: 90px 1fr; gap: 4px 8px; }
  .kv div:nth-child(odd){ color: var(--muted); white-space: nowrap; }
  .cores { display: grid; grid-template-columns: 1fr; gap: 6px; }
  .bar-row { position: relative; background: var(--barBg); border-radius: 8px; overflow: hidden; height: 16px; }
  .bar { height: 100%; transition: width 120ms linear; }
  .label { position: absolute; right: 6px; top: 0; bottom: 0; display:flex; align-items:center; font-size: 12px; text-shadow: 0 1px 2px rgba(0,0,0,.5); }
  .small { color: var(--muted); font-size: 12px; margin-top: 4px; }
</style>

<div class="section">
  <h2>System Overview</h2>
  <div class="kv" id="sys"></div>
</div>

<div class="section">
  <h2>Per-Core Usage</h2>
  <div class="cores" id="cores"></div>
  <div class="small" id="cores-foot"></div>
</div>

<div class="section">
