  <div class="kv" id="mem"></div>
</div>

<script nonce="${nonce}">
  const vscode = acquireVsCodeApi();
  const el = (id) => document.getElementById(id);

  function pct1(p){ return (p*100).toFixed(1) + '%'; }
  function bytesToGB(n){ return (n / (1024 ** 3)).toFixed(1) + " GB"; }
  function fmtUptime(sec){
    const d = Math.floor(sec / 86400);
    const h = Math.floor((sec % 86400) / 3600);
    const m = Math.floor((sec % 3600) / 60);
    return \`\${d}d \${h}h \${m}m\`;
  }

  function renderMetrics(m) {
    const { cpuPct, coresPct, mem, loadavg, uptime, arch, platform, release, cpumodel, elDelay } = m;

    el('sys').innerHTML = \`
      <div>CPU</div><div>\${(cpuPct*100).toFixed(1)}% ‚Äî \${cpumodel || ''}</div>
      <div>OS</div><div>\${platform} \${release} (\${arch})</div>
      <div>Load Avg</div><div>\${(loadavg||[]).map(n=>n.toFixed(2)).join(' / ')}</div>
      <div>Event Loop</div><div>mean \${(elDelay.meanMs||0).toFixed(2)} ms ¬∑ p95 \${(elDelay.p95Ms||0).toFixed(2)} ms</div>
      <div>Uptime</div><div>\${fmtUptime(uptime || 0)}</div>
      <div>Now</div><div>\${new Date(m.ts).toLocaleTimeString()}</div>
    \`;

    const cores = Array.isArray(coresPct) ? coresPct : [];
    el('cores').innerHTML = cores.map(p => {
      const pct = Math.max(0, Math.min(100, p*100));
      const hue = 120 - Math.round(pct * 1.2);
      return \`
        <div class="bar-row">
          <div class="bar" style="width:\${pct}%; background: hsl(\${hue},70%,50%);"></div>
          <div class="label">\${pct.toFixed(0)}%</div>
