    \`;
  }

  window.addEventListener('message', (ev) => {
    const msg = ev.data || {};
    if (msg.type === 'metrics') renderMetrics(msg.payload || {});
  });
</script>
`;
  }

  vm.onStop(() => {
    try { stopLoop(); } catch {}
    try { item.dispose(); } catch {}
    try { eld.disable(); } catch {}
    for (const d of disposables) { try { d.dispose(); } catch {} }
    try { sidebar.close?.(); } catch {}
  });
})();

// SBH ‚Äî JS version: inject editor background (supports large images via blob:URL) + sync workbench JS checksum
//
// ‚úÖ Goal: keep the editor‚Äôs original theme color outside the image; opacity only affects the image layer
// ‚úÖ Features: apply background image / adjust opacity (0‚Äì1, higher ‚Üí lighter) / restore original
// ‚úÖ Behavior: on first apply, create .orig backups (never overwritten); each change updates product.json checksum
// ‚úÖ Formats: PNG / JPG / GIF / WebP; long data:URL automatically switches to blob:URL to avoid Chromium limits
//
// Note: menu removed ‚Äúfix checksums / show path‚Äù; your other flow remains unchanged.

const vscode = require("vscode");
const { vm } = statusBarHelper.v1;
const fs = require("fs");
const fsp = fs.promises;
const path = require("path");
const crypto = require("crypto");

// Target paths (relative to appRoot)
const JS_REL_KEY  = "vs/workbench/workbench.desktop.main.js";
const JS_REL_PATH = "out/vs/workbench/workbench.desktop.main.js";

// Original backups
const JS_BAK_ORIG_EXT  = ".sbhbak.orig";
const PRODUCT_BAK_ORIG = ".sbhbak.product.orig";

// data:URL safety threshold (use blob if exceeded)
const DATAURL_SAFE_LIMIT = 1_800_000; // ~1.8 MB

const MARK_JS_BEGIN = "/* sbh-background:js-begin */";
const MARK_JS_END   = "/* sbh-background:js-end */";

/* ========================= CSS Generator ========================= */
/** Generate CSS that removes background-image but preserves theme background color;
 *  overlay and image share the same background-size so overlay only covers the image.
 */
const makeCssBlock = (
  dataUrl,
  {
    scale = 0.95,                  // visually shrink to 95%
    basis = "height",              // 'height' | 'width'
    align = "center center",
    // Opacity (0‚Äì1): higher ‚Üí lighter (image less prominent; only affects the image)
    overlayOpacity = 0.8,          // legacy compatibility
    overlayOpacityDark  = 0.8,     // default for dark theme
    overlayOpacityLight = 0.8,     // default for light theme
    overlayRGB = "0,0,0"           // legacy fallback only (coloring no longer used)
  } = {}
) => {
  const sizeForImg =
    basis === "height"
      ? "auto calc(100% * var(--sbh-bg-scale))"
      : "calc(100% * var(--sbh-bg-scale)) auto";

  return `
:root{
  /* sbh-background:vars-begin */
  --sbh-bg-scale: ${scale};

  /* legacy params */
  --sbh-bg-overlay-opacity: ${overlayOpacity};
  --sbh-bg-overlay-rgb: ${overlayRGB};

  /* theme-specific source (we use this as the ‚Äúfade amount‚Äù) */
  --sbh-bg-overlay-opacity-dark: ${overlayOpacityDark};
  --sbh-bg-overlay-opacity-light: ${overlayOpacityLight};

  /* actual background color comes from the theme; overridden by .monaco-workbench */
  --sbh-bg-theme: #1e1e1e;

  /* effective fade alpha (0‚Äì1; higher ‚Üí lighter) */
  --sbh-bg-overlay-alpha: var(--sbh-bg-overlay-opacity);

  /* convert fade alpha to image opacity (1 - alpha) */
  --sbh-bg-image-opacity: calc(1 - var(--sbh-bg-overlay-alpha));
  /* sbh-background:vars-end */
}

/* theme awareness (override within workbench scope) */
.monaco-workbench.vs-dark{
  --sbh-bg-overlay-alpha: var(--sbh-bg-overlay-opacity-dark);
  --sbh-bg-theme: var(--vscode-editor-background, #1e1e1e);
}
.monaco-workbench.vs{
  --sbh-bg-overlay-alpha: var(--sbh-bg-overlay-opacity-light);
  --sbh-bg-theme: var(--vscode-editor-background, #ffffff);
}
.hc-black{
  --sbh-bg-overlay-alpha: 0.7;
  --sbh-bg-theme: var(--vscode-editor-background, #000000);
}
.hc-light{
  --sbh-bg-overlay-alpha: 0.5;
  --sbh-bg-theme: var(--vscode-editor-background, #ffffff);
}

/* remove background images but keep theme background color */
.monaco-workbench,
.monaco-editor,
.monaco-editor-background,
.editor-instance,
.monaco-editor .view-lines,
.monaco-editor .lines-content,
.monaco-editor .margin,
.editor .lines-content,
.monaco-editor .overflow-guard,
.monaco-editor .monaco-scrollable-element.editor-scrollable{
  background-image: none !important;
}

/* outer containers always use theme color (avoid transparency) */
.monaco-workbench .part.editor .editor-group-container,
.monaco-workbench .part.editor .editor-group-container .monaco-editor,
.monaco-editor,
.monaco-editor .overflow-guard,
.monaco-editor .monaco-scrollable-element.editor-scrollable,
.monaco-workbench .part.editor .editor-group-container.empty,
.monaco-workbench .part.editor .editor-group-container .welcomePage,
.monaco-workbench .part.editor .editor-group-container .gettingStarted,
.monaco-workbench .part.editor .editor-group-container .gettingStartedContainer{
  background-color: var(--sbh-bg-theme) !important;
  position: relative !important; /* anchor ::before */
}

/* === image layer on ::before; control via opacity (higher ‚Üí lighter) === */
.monaco-workbench .part.editor
  .editor-group-container.active
  .monaco-editor .monaco-scrollable-element.editor-scrollable::before,
.monaco-workbench .part.editor .editor-group-container.empty::before,
.monaco-workbench .part.editor .editor-group-container.active .welcomePage::before,
.monaco-workbench .part.editor .editor-group-container.active .gettingStarted::before,
.monaco-workbench .part.editor .editor-group-container.active .gettingStartedContainer::before{
  content: "";
  position: absolute;
  inset: 0;
  pointer-events: none;
  background-image: url('${dataUrl}');
  background-repeat: no-repeat;
  background-position: ${align};
  background-size: ${sizeForImg};
  opacity: var(--sbh-bg-image-opacity);  /* ‚òÖ key: image-only opacity */
}

/* exclude non-target containers (avoid duplicates) */
.monaco-workbench .peekview-widget .monaco-editor .monaco-scrollable-element.editor-scrollable::before,
.monaco-workbench .search-editor .monaco-scrollable-element.editor-scrollable::before,
.monaco-workbench .diff-editor .editor.original .monaco-scrollable-element.editor-scrollable::before,
.monaco-workbench .part.editor
  .editor-group-container:not(.active)
  .monaco-editor .monaco-scrollable-element.editor-scrollable::before{
  content: none;
}

/* allow content layers to be transparent so the image shows through */
.monaco-editor .view-lines,
.monaco-editor .lines-content,
.monaco-editor .margin,
.monaco-editor-background{
  background-color: transparent !important;
  background-image: none !important;
}
`;
};


/* ========================= Utilities ========================= */
const sha256b64 = (data) =>
  crypto.createHash("sha256").update(data).digest("base64").replace(/=+$/g, "");

const fileSha256b64 = async (abs, isText = true) =>
  sha256b64(await fsp.readFile(abs, isText ? "utf8" : undefined));

const escapeRegExp = (s) => s.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");

function findInjectedBlock(text) {
  const re = new RegExp(
    `${escapeRegExp(MARK_JS_BEGIN)}[\\s\\S]*?${escapeRegExp(MARK_JS_END)}`,
    "m"
  );
  const m = text.match(re);
  if (m && m[0]) return { block: m[0] };

  const b = text.indexOf(MARK_JS_BEGIN);
  if (b === -1) return null;
  const e = text.indexOf(MARK_JS_END, b);
  if (e === -1) return null;
  return { block: text.slice(b, e + MARK_JS_END.length) };
}

const guessAppRoots = () => {
  const roots = new Set();
  if (vscode?.env?.appRoot) roots.add(vscode.env.appRoot);
  const exe = process.execPath || "";

  if (process.platform === "darwin") {
    roots.add(path.resolve(exe, "../../Resources/app"));
    roots.add("/Applications/Visual Studio Code.app/Contents/Resources/app");
    roots.add("/Applications/Visual Studio Code - Insiders.app/Contents/Resources/app");
    roots.add(path.resolve(exe, "../Resources/app"));
    roots.add(path.resolve(exe, "../../../Resources/app"));
  } else if (process.platform === "win32") {
    roots.add(path.join(path.dirname(exe), "resources", "app"));
    if (process.env.LOCALAPPDATA) {
      roots.add(path.join(process.env.LOCALAPPDATA, "Programs", "Microsoft VS Code", "resources", "app"));
      roots.add(path.join(process.env.LOCALAPPDATA, "Programs", "Microsoft VS Code - Insiders", "resources", "app"));
    }
  } else {
    roots.add(path.resolve(path.dirname(exe), "..", "resources", "app"));
    roots.add("/usr/share/code/resources/app");
    roots.add("/usr/lib/code/resources/app");
    roots.add("/snap/code/current/usr/share/code/current/usr/share/code/resources/app"); // snap variants
    roots.add("/snap/code/current/usr/share/code/resources/app");
  }
  return Array.from(roots);
};

const findEnv = () => {
  for (const r of guessAppRoots()) {
    const product = path.join(r, "product.json");
    const jsPath = path.join(r, JS_REL_PATH);
    if (fs.existsSync(product) && fs.existsSync(jsPath)) {
      return { appRoot: r, productJson: product, jsPath };
    }
  }
  return null;
};

const findEnvSmart = () => {
  const candidates = [];
  for (const r of guessAppRoots()) {
    const product = path.join(r, "product.json");
    const jsPath = path.join(r, JS_REL_PATH);
    if (fs.existsSync(product) && fs.existsSync(jsPath)) {
      candidates.push({ appRoot: r, productJson: product, jsPath });
    }
  }
  if (candidates.length <= 1) return candidates[0] || null;

  // prefer the instance that already has our injection
  for (const c of candidates) {
    try {
      const txt = fs.readFileSync(c.jsPath, "utf8");
      if (txt.includes(MARK_JS_BEGIN) && txt.includes(MARK_JS_END)) return c;
    } catch {}
  }
  return candidates[0] || null;
};

const hasInjectionMarks = (text) =>
  text.includes(MARK_JS_BEGIN) && text.includes(MARK_JS_END);

const makeInjectedIIFE = (dataUrl, opts = {}) => {
  const css = makeCssBlock(dataUrl, opts)
    .replace(/\\/g, "\\\\")
    .replace(/`/g, "\\`");
  return `
${MARK_JS_BEGIN}
(function(){
  function inject(){
    try{
      var id = 'sbh-background-style';
      var old = document.getElementById(id);
      if(old && old.parentNode) old.parentNode.removeChild(old);
      var s = document.createElement('style');
      s.id = id;
      s.type = 'text/css';
      s.appendChild(document.createTextNode(\`${css}\`));
      document.head.appendChild(s);
    }catch(e){ console && console.warn && console.warn('[sbh] inject failed', e); }
  }
  if(typeof document !== 'undefined'){
    if(document.readyState === 'complete' || document.readyState === 'interactive'){
      setTimeout(inject, 0);
    }else{
      document.addEventListener('DOMContentLoaded', inject, { once:true });
    }
  }
})();
${MARK_JS_END}
`;
};

// Big files ‚Üí use blob:URL (avoid data:URL length issues)
const makeInjectedIIFE_UsingBlob = (mime, b64, opts = {}) => {
  const cssTpl = makeCssBlock("__SBH_BG__", opts)
    .replace(/\\/g, "\\\\")
    .replace(/`/g, "\\`");
  return `
${MARK_JS_BEGIN}
(function(){
  var _url;
  function toBlobUrl(b64, mime){
    try{
      var bin = atob(b64);
      var len = bin.length;
      var arr = new Uint8Array(len);
      for (var i=0;i<len;i++) arr[i] = bin.charCodeAt(i);
      return URL.createObjectURL(new Blob([arr], { type: mime || 'application/octet-stream' }));
    }catch(e){ console && console.warn && console.warn('[sbh] blob fail', e); return ''; }
  }
  function inject(){
    try{
      if(!_url) _url = toBlobUrl('${b64}', '${mime}');
      var id = 'sbh-background-style';
      var old = document.getElementById(id);
      if(old && old.parentNode) old.parentNode.removeChild(old);
      var s = document.createElement('style');
      s.id = id; s.type = 'text/css';
      var css = \`${cssTpl}\`.replace(/__SBH_BG__/g, _url);
      s.appendChild(document.createTextNode(css));
      document.head.appendChild(s);
    }catch(e){ console && console.warn && console.warn('[sbh] inject failed', e); }
  }
  if(typeof document !== 'undefined'){
    if(document.readyState === 'complete' || document.readyState === 'interactive'){
      setTimeout(inject, 0);
    }else{
      document.addEventListener('DOMContentLoaded', inject, { once:true });
    }
  }
})();
${MARK_JS_END}
`;
};

const stripOldJsInjection = (text) => {
  const re = new RegExp(
    `${escapeRegExp(MARK_JS_BEGIN)}[\\s\\S]*?${escapeRegExp(MARK_JS_END)}\\s*`,
    "m"
  );
  return text.replace(re, "");
};

const appendBeforeSourceMap = (text, snippet) => {
  const i = text.lastIndexOf("//# sourceMappingURL=");
  if (i > -1) return text.slice(0, i) + "\n" + snippet + "\n" + text.slice(i);
  return text + "\n" + snippet + "\n";
};

/* ========================= Backups & checksum ========================= */
async function ensureCleanOriginalBackups(productJson, jsPath) {
  const jsOrig = jsPath + JS_BAK_ORIG_EXT;
  const productOrig = productJson + PRODUCT_BAK_ORIG;
  if (fs.existsSync(jsOrig) && fs.existsSync(productOrig)) return;

  const [productTxt, jsTxt] = await Promise.all([
    fsp.readFile(productJson, "utf8"),
    fsp.readFile(jsPath, "utf8"),
  ]);

  let productObj;
  try { productObj = JSON.parse(productTxt); }
  catch { return; }

  const expected = productObj?.checksums?.[JS_REL_KEY] || null;
  if (!expected) return;

  const currentHash = sha256b64(jsTxt);
  const isClean = currentHash === expected && !hasInjectionMarks(jsTxt);
  if (!isClean) return;

  try {
    if (!fs.existsSync(jsOrig))      await fsp.writeFile(jsOrig, jsTxt, "utf8");
    if (!fs.existsSync(productOrig)) await fsp.writeFile(productOrig, productTxt, "utf8");
  } catch {}
}

const updateProductChecksumKey = async (productJson, key, fileAbs) => {
  const src = await fsp.readFile(productJson, "utf8");
  let obj;
  try { obj = JSON.parse(src); }
  catch { throw new Error("product.json is not valid JSON"); }
  if (!obj.checksums || typeof obj.checksums !== "object")
    throw new Error("product.json is missing the 'checksums' field");

  const calc = await fileSha256b64(fileAbs, true);
  if (obj.checksums[key] === calc) return false;

  await fsp.writeFile(productJson + PRODUCT_BAK_ORIG, src, { flag: "wx" }).catch(() => {});
  obj.checksums[key] = calc;
  await fsp.writeFile(productJson, JSON.stringify(obj, null, 2), "utf8");
  return true;
};

/* ========================= Opacity-only update (no image change) ========================= */
function readCurrentOpacityFromJsText(injectedJsBlock){
  const pick = (re) => {
    const m = injectedJsBlock.match(re);
    return m ? parseFloat(m[1]) : null;
  };
  return (
    pick(/--sbh-bg-overlay-opacity-dark:\s*([0-9]*\.?[0-9]+)\s*;/) ??
    pick(/--sbh-bg-overlay-opacity:\s*([0-9]*\.?[0-9]+)\s*;/) ??
    pick(/--sbh-bg-overlay-opacity-light:\s*([0-9]*\.?[0-9]+)\s*;/) ??
    null
  );
}
function extractDataUrl(injectedJsBlock){
  const m = injectedJsBlock.match(/url\(['"](data:[^'"]+)['"]\)/);
  return m ? m[1] : null;
}
async function updateOpacityOnly(productJson, jsPath, newOpacity){
  const src = await fsp.readFile(jsPath, "utf8");
  if (!hasInjectionMarks(src)) throw new Error("No injected style found. Please 'Apply Background Image' first.");

  const found = findInjectedBlock(src);
  if (!found || !found.block) throw new Error("Unable to locate the injected block.");
  const block = found.block;

  // update three variables (dark / light / base)
  const rxs = [
    /(--sbh-bg-overlay-opacity-dark:\s*)([0-9]*\.?[0-9]+)(\s*;)/,
    /(--sbh-bg-overlay-opacity-light:\s*)([0-9]*\.?[0-9]+)(\s*;)/,
    /(--sbh-bg-overlay-opacity:\s*)([0-9]*\.?[0-9]+)(\s*;)/
  ];
  let replaced = block, hit = 0;
  for (const rx of rxs) {
    const before = replaced;
    replaced = replaced.replace(rx, (_, p1, _old, p3) => `${p1}${newOpacity}${p3}`);
    if (replaced !== before) hit++;
  }

  let nextSrc;
  if (hit > 0) {
    nextSrc = src.replace(block, replaced);
  } else {
    const dataUrl = extractDataUrl(block);
    if (!dataUrl) throw new Error("Failed to parse existing data:URL. Please re-apply the background image.");
    const rebuilt = makeInjectedIIFE(dataUrl, {
      overlayOpacity: newOpacity,
      overlayOpacityDark: newOpacity,
      overlayOpacityLight: newOpacity
    });
    nextSrc = src.replace(block, rebuilt);
  }

  await fsp.writeFile(jsPath, nextSrc, "utf8");
  await updateProductChecksumKey(productJson, JS_REL_KEY, jsPath);
}

/* ========================= Close VS Code ========================= */
const quitVSCode = async () => {
  try { await vscode.commands.executeCommand("workbench.action.files.saveAll"); } catch {}
  try { await vscode.commands.executeCommand("workbench.action.quit"); }
  catch { try { await vscode.commands.executeCommand("workbench.action.closeWindow"); } catch {} }
};
async function askToQuit(message) {
  const btn = await vscode.window.showInformationMessage(
    message,
    { modal: true },
    "Close VS Code now", "Later"
  );
  if (btn === "Close VS Code now") { await quitVSCode(); return true; }
  return false;
}

/* ========================= Main ========================= */
(async () => {
  try {
    const env = findEnvSmart() || findEnv();
    if (!env) { await vscode.window.showErrorMessage("Cannot find VS Code installation files (product.json / workbench JS)."); return; }
    const { productJson, jsPath } = env;

    const choice = await vscode.window.showQuickPick(
      [
        { label: "üìÇ Apply Background Image (pick a file)", description: "PNG/JPG/GIF/WebP; set opacity at the same time; large files use blob" },
        { label: "üéö Adjust Opacity (0‚Äì1; higher ‚Üí lighter)", description: "Change fade only; image not replaced" },
        { label: "üßπ Restore Background (revert to original)", description: "Restore from .orig if present; otherwise remove injection and resync checksum" }
      ],
      { title: "Editor Background (apply / opacity / restore)" }
    );
    if (!choice) return;

    const action =
      choice.label.includes("Apply")   ? "apply"   :
      choice.label.includes("Opacity") ? "opacity" :
      "restore";

    if (action === "restore") {
      const jsOrig = jsPath + JS_BAK_ORIG_EXT;
      const productOrig = productJson + PRODUCT_BAK_ORIG;

      if (fs.existsSync(jsOrig)) {
        await fsp.copyFile(jsOrig, jsPath);
        if (fs.existsSync(productOrig)) {
          await fsp.copyFile(productOrig, productJson);
        } else {
          await updateProductChecksumKey(productJson, JS_REL_KEY, jsPath);
        }
        const closed = await askToQuit("‚úÖ Restored original file and synced checksum. Close VS Code to take effect.");
        if (closed) return;
      } else {
        const txt = await fsp.readFile(jsPath, "utf8");
        const stripped = stripOldJsInjection(txt);
        if (stripped !== txt) {
          await fsp.writeFile(jsPath, stripped, "utf8");
          await updateProductChecksumKey(productJson, JS_REL_KEY, jsPath);
          const closed2 = await askToQuit("‚ÑπÔ∏è No .orig found. Removed injection and synced checksum. Close VS Code to take effect.");
          if (closed2) return;
        } else {
          await vscode.window.showWarningMessage("‚ö†Ô∏è No .orig backup and no injection marks found.");
        }
      }
      return;
    }

    if (action === "opacity") {
      const jsText = await fsp.readFile(jsPath, "utf8");
      const found = findInjectedBlock(jsText);
      const block = found?.block || null;
      const current = block ? (readCurrentOpacityFromJsText(block) ?? 0.8) : 0.8;

      const input = await vscode.window.showInputBox({
        title: "Set background opacity (0‚Äì1; higher ‚Üí lighter)",
        value: String(current),
        prompt: "e.g., 0.6 = lighter; 0 = no fade (image most visible).",
        validateInput: (v) => {
          const n = Number(v);
          return isFinite(n) && n >= 0 && n <= 1 ? null : "Please enter a number between 0 and 1.";
        }
      });
      if (!input) return;

      const val = Math.max(0, Math.min(1, Number(input)));
      await updateOpacityOnly(productJson, jsPath, Number(val.toFixed(3)));

      const closed = await askToQuit("‚úÖ Updated opacity and synced checksum. Close VS Code to take effect.");
      if (closed) return;
      return;
    }

    // === Apply Background Image ===
    await ensureCleanOriginalBackups(productJson, jsPath);

    const picked = await vscode.window.showOpenDialog({
      canSelectFiles: true, canSelectMany: false, openLabel: "Pick background image",
      filters: { Images: ["png", "jpg", "jpeg", "gif", "webp"] }
    });
    if (!picked || !picked.length) return;

    const buf = await fsp.readFile(picked[0].fsPath);
    const ext = path.extname(picked[0].fsPath).toLowerCase();
    const mime =
      ext === ".png"  ? "image/png"  :
      ext === ".jpg" || ext === ".jpeg" ? "image/jpeg" :
      ext === ".gif"  ? "image/gif"   :
      ext === ".webp" ? "image/webp"  :
      "application/octet-stream";

    const b64     = buf.toString("base64");
    const dataUrl = `data:${mime};base64,${b64}`;
    const USE_BLOB = dataUrl.length > DATAURL_SAFE_LIMIT;

    // read existing opacity as default (or 0.8 if none)
    const jsTextForDefault = await fsp.readFile(jsPath, "utf8").catch(() => "");
    const existedBlock = findInjectedBlock(jsTextForDefault)?.block || null;
    const defaultOpacity = existedBlock
      ? (readCurrentOpacityFromJsText(existedBlock) ?? 0.8)
      : 0.8;

    // ask for new opacity
    const inputOpacity = await vscode.window.showInputBox({
      title: "Set background opacity (0‚Äì1; higher ‚Üí lighter)",
      value: String(defaultOpacity),
      prompt: "e.g., 0.6 = lighter; 0 = no fade (image most visible).",
      validateInput: (v) => {
        const n = Number(v);
        return isFinite(n) && n >= 0 && n <= 1 ? null : "Please enter a number between 0 and 1.";
      }
    });
    if (!inputOpacity) return;

    const opacity = Math.max(0, Math.min(1, Number(inputOpacity)));
    const cssOpts = {
      overlayOpacity: opacity,
      overlayOpacityDark: opacity,
      overlayOpacityLight: opacity
    };

    const orig = await fsp.readFile(jsPath, "utf8");
    const injected = appendBeforeSourceMap(
      stripOldJsInjection(orig),
      USE_BLOB
        ? makeInjectedIIFE_UsingBlob(mime, b64, cssOpts)
        : makeInjectedIIFE(dataUrl, cssOpts)
    );
    await fsp.writeFile(jsPath, injected, "utf8");

    const changed = await updateProductChecksumKey(productJson, JS_REL_KEY, jsPath);

    const closed = await askToQuit(
      `‚úÖ Applied background (${USE_BLOB ? "blob:URL" : "data:URL"}, opacity=${opacity})${changed ? " and synced checksum" : ""}. Close VS Code to take effect.`
    );
    if (closed) return;

  } catch (err) {
    await vscode.window.showErrorMessage("‚ùå Error: " + (err?.message || err));
  } finally {
    vm.stop();
  }
})();

// Explorer Menu: Minimal example with 4 actions (multi-select supported)
// - $(file) Show selected file(s)/folder(s)
// - $(clippy) Copy path(s) to clipboard
// - $(terminal) Open containing folder in Terminal
// - $(info) Show count & total size (non-recursive)
//
// How to use: Right-click a file/folder in Explorer ‚Üí ‚ÄúStatus Bar Helper: Trigger Action Menu‚Äù

const vscode = require("vscode");
// Global object injected by Status Bar Helper
const { explorerAction } = sbh.v1;

(async function main() {
  // 1) Show selected file(s)/folder(s)
  await explorerAction.register({
    description: '$(file) Show selected item(s)',
    handler: async ({ uri, uris }) => {
      const targets = uris?.length ? uris : (uri ? [uri] : []);
      if (!targets.length) return vscode.window.showWarningMessage('No item selected.');
      if (targets.length === 1) {
        return vscode.window.showInformationMessage(`Selected: ${targets[0].fsPath}`);
      }
      const paths = targets.map(u => u.fsPath).join('\n');
      vscode.window.showInformationMessage(`Selected ${targets.length} items:\n${paths}`);
    }
  });

  // 2) Copy path(s) to clipboard (supports multi-select)
  await explorerAction.register({
    description: '$(clippy) Copy path(s)',
    handler: async ({ uri, uris }) => {
      const targets = uris?.length ? uris : (uri ? [uri] : []);
      if (!targets.length) return vscode.window.showWarningMessage('No item selected.');
      const text = targets.map(u => u.fsPath).join('\n');
      await vscode.env.clipboard.writeText(text);
      vscode.window.showInformationMessage(`Copied ${targets.length} path(s) to the clipboard.`);
    }
  });

  // 3) Open containing folder in Terminal
  await explorerAction.register({
    description: '$(terminal) Open in Terminal',
    handler: async ({ uri }) => {
      if (!uri) return vscode.window.showWarningMessage('Please select a file or folder.');
      const fs = require('fs');
      const path = require('path');
      let cwd = uri.fsPath;
      try {
        const stat = fs.statSync(uri.fsPath);
        if (!stat.isDirectory()) cwd = path.dirname(uri.fsPath);
      } catch {}
      const term = vscode.window.createTerminal('Explorer');
      term.show();
      term.sendText(`cd "${cwd}"`);
      vscode.window.showInformationMessage(`Opened in terminal: ${cwd}`);
    }
  });

  // 4) Show file info (count & total size, non-recursive)
  await explorerAction.register({
    description: '$(info) Show file info',
    handler: async ({ uri, uris }) => {
      const targets = uris?.length ? uris : (uri ? [uri] : []);
      if (!targets.length) return vscode.window.showWarningMessage('No item selected.');

      const fs = require('fs');
      let fileCount = 0, dirCount = 0, totalSize = 0;

      for (const u of targets) {
        try {
          const s = fs.statSync(u.fsPath);
          if (s.isDirectory()) dirCount++;
          else { fileCount++; totalSize += s.size; }
        } catch {}
      }

      const kb = (totalSize / 1024).toFixed(2);
      const mb = (totalSize / 1024 / 1024).toFixed(2);
      vscode.window.showInformationMessage(
        `üìä Summary\n` +
        `Files: ${fileCount}\n` +
        `Folders: ${dirCount}\n` +
        `Total size: ${kb} KB (${mb} MB)`
      );
    }
  });

  // Startup hint: tell users where to click
  vscode.window.showInformationMessage(
    [
      '‚úÖ Explorer context menu is now available (4 actions)',
      '',
      'How to use:',
      '1) Open the Explorer ‚Üí select file/folder(s)',
      '2) Right-click ‚Üí ‚ÄúStatus Bar Helper: Trigger Action Menu‚Äù',
      '3) Pick an action'
    ].join('\n')
  );
})();

// ==UserScript==
// @name Excel to Markdown Table Converter
// @description Paste data copied from Excel and convert it into a Markdown table in real-time.
// ==/UserScript==

const vscode = require("vscode");
const { vm } = statusBarHelper.v1;

(function main() {
  // Use a variable to hold the panel instance to avoid creating duplicates.
  let panel;

  // Register a cleanup function to automatically close the Webview Panel when the VM stops.
  vm.onStop(() => {
    if (panel) {
      panel.dispose();
    }
  });

  // Create or show the Webview Panel.
  function createOrShowPanel() {
    // If the panel already exists, show it instead of creating a new one.
    if (panel) {
      panel.reveal(vscode.ViewColumn.Active);
      return;
    }

    panel = vscode.window.createWebviewPanel(
      "excelToMarkdownConverter", // Identifier, used internally.
      "Excel to Markdown", // Title to display on the tab.
      vscode.ViewColumn.Active, // Show in the active column.
      {
        enableScripts: true, // Enable JavaScript.
        retainContextWhenHidden: true, // Retain context when the tab is hidden.
      }
    );

    // Generate and set the Webview's HTML content.
    const nonce = Math.random().toString(36).slice(2);
    panel.webview.html = getWebviewHtml(nonce);

    // Handle messages received from the webview.
    panel.webview.onDidReceiveMessage(
      (message) => {
        switch (message.command) {
          case "copySuccess":
            vscode.window.showInformationMessage("Markdown copied to clipboard successfully!");
            return;
        }
      },
      undefined
    );

    // Set up behavior for when the panel is closed by the user.
    panel.onDidDispose(
      () => {
        panel = undefined; // Clean up the panel reference.
        try {
          vm.stop(); // Stop the VM to release resources.
        } catch (e) {
          console.error("Failed to stop VM:", e);
        }
      },
      null
    );
  }

  // Execute immediately to open the panel.
  createOrShowPanel();
})();

/**
 * Generates the complete HTML content for the webview.
