            <div class="panel">
                <h3>Excel Data Input</h3>
                <textarea id="excelInput" placeholder="1. Copy cells from Excel (Ctrl+C)\n2. Paste here (Ctrl+V)"></textarea>
                <div class="button-group">
                    <button id="clearBtn">Clear All</button>
                </div>
            </div>
            <div class="panel">
                <h3>Markdown Output</h3>
                <textarea id="markdownOutput" readonly></textarea>
                <div class="button-group">
                    <button id="copyBtn">Copy Markdown</button>
                </div>
            </div>
        </div>

        <div class="quick-start">
            <h4>üöÄ Quick Start Guide</h4>
            <ul>
                <li>Select and copy a table from Excel (Ctrl+C)</li>
                <li>Click in the text area above</li>
                <li>Paste the data (Ctrl+V)</li>
                <li>View the converted Markdown table</li>
            </ul>
        </div>

        <script nonce="${nonce}">
            const vscode = acquireVsCodeApi();

            const excelInput = document.getElementById('excelInput');
            const markdownOutput = document.getElementById('markdownOutput');
            const clearBtn = document.getElementById('clearBtn');
            const copyBtn = document.getElementById('copyBtn');

            function convertToMarkdown(text) {
                if (!text.trim()) {
                    return "";
                }

                // Convert the text from TSV (Tab-separated values) format to a 2D array.
                const rows = text.trim().split('\\n').map(row => row.split('\\t'));
                if (rows.length === 0) {
                    return "";
                }

                const header = rows[0];
                const separator = header.map(() => '---').join(' | ');

                const formatRow = (row) => '| ' + row.join(' | ') + ' |';

                const markdownRows = [
                    formatRow(header),
                    '| ' + separator + ' |',
                    ...rows.slice(1).map(formatRow)
                ];

                return markdownRows.join('\\n');
            }

            excelInput.addEventListener('input', () => {
                const markdown = convertToMarkdown(excelInput.value);
                markdownOutput.value = markdown;
            });

            clearBtn.addEventListener('click', () => {
                excelInput.value = '';
                markdownOutput.value = '';
            });

            copyBtn.addEventListener('click', () => {
                if (markdownOutput.value) {
                    navigator.clipboard.writeText(markdownOutput.value).then(() => {
                        vscode.postMessage({ command: 'copySuccess' });
                    }).catch(err => {
                        console.error('Failed to copy: ', err);
                    });
                }
            });

        </script>
    </body>
    </html>
  `;
}

