            .jwt-output { 
                white-space: pre-wrap; 
                background-color: var(--input-bg); 
                padding: 10px; 
                border-radius: 4px; 
                overflow-x: auto; 
                flex-grow: 1;
                font-family: "Consolas", "Courier New", monospace;
                font-size: 14px;
            }
        </style>
    </head>
    <body>
        <div class="tabs">
            <button class="tab-button active" data-tab="timestamp">Timestamp</button>
            <button class="tab-button" data-tab="base64">Base64</button>
            <button class="tab-button" data-tab="url">URL</button>
            <button class="tab-button" data-tab="jwt">JWT Decoder</button>
        </div>

        <div id="timestamp" class="tab-content active">
             <div class="converter-grid">
                <div class="io-panel">
                    <h4>Unix Timestamp (s/ms)</h4>
                    <input type="text" id="timestamp-input">
                </div>
                <div class="arrow">‚Üî</div>
                <div class="io-panel">
                    <h4>Local Time (YYYY-MM-DD HH:mm:ss)</h4>
                    <input type="text" id="datetime-input">
                </div>
            </div>
        </div>

        <div id="base64" class="tab-content">
            <div class="converter-grid">
                <div class="io-panel">
                    <h4>Raw String (UTF-8)</h4>
                    <textarea id="base64-decode-input"></textarea>
                </div>
                <div class="arrow">‚Üî</div>
                <div class="io-panel">
                    <h4>Base64 Encoded</h4>
                    <textarea id="base64-encode-input"></textarea>
                </div>
            </div>
        </div>

        <div id="url" class="tab-content">
           <div class="converter-grid">
                <div class="io-panel">
                    <h4>Raw String</h4>
                    <textarea id="url-decode-input"></textarea>
                </div>
                <div class="arrow">‚Üî</div>
                <div class="io-panel">
                    <h4>URL Encoded (Component)</h4>
                    <textarea id="url-encode-input"></textarea>
                </div>
            </div>
        </div>

        <div id="jwt" class="tab-content">
            <div class="io-panel" style="width:100%; height: 40%;">
                <h4>JWT Token</h4>
                <textarea id="jwt-input"></textarea>
            </div>
            <div class="io-panel" style="width:100%; height: 60%;">
                <h4>Header</h4>
                <div id="jwt-header" class="jwt-output"></div>
                <h4>Payload</h4>
                <div id="jwt-payload" class="jwt-output"></div>
            </div>
        </div>

        <script nonce="${nonce}">
            const tabs = document.querySelectorAll('.tab-button');
            const contents = document.querySelectorAll('.tab-content');

            function openTab(tabName) {
                tabs.forEach(tab => tab.classList.remove('active'));
                contents.forEach(content => content.classList.remove('active'));
                document.querySelector(\`button[data-tab="\${tabName}"]\`).classList.add('active');
                document.getElementById(tabName).classList.add('active');
            }

            function convert(type) {
                try {
                    switch (type) {
                        case 'timestamp': {
                            const tsInput = document.getElementById('timestamp-input');
                            const dtInput = document.getElementById('datetime-input');
                            const val = tsInput.value.trim();
                            if (!val || !/^\\d+$/.test(val)) {
                                dtInput.value = ''; return;
                            }
                            const ts = parseInt(val, 10);
                            const date = new Date(val.length === 10 ? ts * 1000 : ts);
                            if (isNaN(date.getTime())) {
                                dtInput.value = ''; return;
                            }
                            const pad = (n) => n.toString().padStart(2, '0');
                            dtInput.value = \`\${date.getFullYear()}-\${pad(date.getMonth() + 1)}-\${pad(date.getDate())} \${pad(date.getHours())}:\${pad(date.getMinutes())}:\${pad(date.getSeconds())}\`;
                            break;
                        }
                        case 'datetime': {
                            const tsInput = document.getElementById('timestamp-input');
                            const dtInput = document.getElementById('datetime-input');
                            const val = dtInput.value.trim();
                            const date = new Date(val);
                            if (!val || isNaN(date.getTime())) {
                                tsInput.value = ''; return;
                            }
                            tsInput.value = Math.floor(date.getTime() / 1000);
                            break;
                        }
                        case 'base64-decode': {
                            const rawInput = document.getElementById('base64-decode-input');
                            const b64Input = document.getElementById('base64-encode-input');
                            // Use a robust method for UTF-8 encoding
                            b64Input.value = btoa(unescape(encodeURIComponent(rawInput.value)));
                            break;
                        }
                        case 'base64-encode': {
                            const rawInput = document.getElementById('base64-decode-input');
                            const b64Input = document.getElementById('base64-encode-input');
                            // Use a robust method for UTF-8 decoding
                            rawInput.value = decodeURIComponent(escape(atob(b64Input.value)));
                            break;
                        }
                        case 'url-decode': {
                            const rawInput = document.getElementById('url-decode-input');
                            const urlInput = document.getElementById('url-encode-input');
                            urlInput.value = encodeURIComponent(rawInput.value);
                            break;
                        }
                        case 'url-encode': {
                            const rawInput = document.getElementById('url-decode-input');
                            const urlInput = document.getElementById('url-encode-input');
                            rawInput.value = decodeURIComponent(urlInput.value);
                            break;
                        }
                        case 'jwt': {
                            const jwtInput = document.getElementById('jwt-input');
                            const headerOut = document.getElementById('jwt-header');
                            const payloadOut = document.getElementById('jwt-payload');
                            const token = jwtInput.value.trim();
                            if (!token || token.split('.').length < 2) {
                                headerOut.textContent = ''; payloadOut.textContent = ''; return;
                            }
                            const [header, payload] = token.split('.');
                            const decodedHeader = JSON.stringify(JSON.parse(atob(header.replace(/-/g, '+').replace(/_/g, '/'))), null, 2);
                            const decodedPayload = JSON.stringify(JSON.parse(atob(payload.replace(/-/g, '+').replace(/_/g, '/'))), null, 2);
                            headerOut.textContent = decodedHeader;
                            payloadOut.textContent = decodedPayload;
                            break;
                        }
                    }
                } catch (e) {
                    console.error('Conversion Error:', e);
                }
            }

            // --- CSP Fix: Register events using addEventListener ---

            // Register tab switching events
            tabs.forEach(tab => {
                tab.addEventListener('click', () => {
                    openTab(tab.dataset.tab);
                });
            });

            // Register real-time conversion events for all input fields
            document.getElementById('timestamp-input').addEventListener('input', () => convert('timestamp'));
            document.getElementById('datetime-input').addEventListener('input', () => convert('datetime'));
            document.getElementById('base64-decode-input').addEventListener('input', () => convert('base64-decode'));
            document.getElementById('base64-encode-input').addEventListener('input', () => convert('base64-encode'));
            document.getElementById('url-decode-input').addEventListener('input', () => convert('url-decode'));
            document.getElementById('url-encode-input').addEventListener('input', () => convert('url-encode'));
            document.getElementById('jwt-input').addEventListener('input', () => convert('jwt'));

        </script>
    </body>
    </html>
  `;
}

const vscode = require("vscode");
const { vm } = statusBarHelper.v1;

(function main() {
  let panel;

  vm.onStop(() => {
    try {
      panel?.dispose();
    } catch (e) {
      console.error("Error disposing panel on VM stop:", e);
    }
  });

  panel = vscode.window.createWebviewPanel(
    "sbhPianoVirtuosoPlus",
    "Mini Piano (Virtuoso+ Edition)",
    vscode.ViewColumn.One,
    {
      enableScripts: true,
      retainContextWhenHidden: true,
    }
  );

  panel.onDidDispose(() => {
    try {
      vm.stop();
    } catch (e) {
      console.error("Error stopping VM on panel dispose:", e);
    }
  });

  const nonce = Math.random().toString(36).slice(2);
  panel.webview.html = getWebviewHtml(nonce);

})();

