            const audioContext = new (window.AudioContext || window.webkitAudioContext)();
            const noteFrequencies = { 'C4': 261.63, 'C#4': 277.18, 'D4': 293.66, 'D#4': 311.13, 'E4': 329.63, 'F4': 349.23, 'F#4': 369.99, 'G4': 392.00, 'G#4': 415.30, 'A4': 440.00, 'A#4': 466.16, 'B4': 493.88 };
            const activeOscillators = {};
            function playNote(note) { if (!noteFrequencies[note] || activeOscillators[note]) return; const o = audioContext.createOscillator(), g = audioContext.createGain(); o.frequency.setValueAtTime(noteFrequencies[note], audioContext.currentTime); o.type = 'sine'; o.connect(g); g.connect(audioContext.destination); o.start(); activeOscillators[note] = { o, g }; }
            function stopNote(note) { const a = activeOscillators[note]; if (a) { a.g.gain.exponentialRampToValueAtTime(1e-4, audioContext.currentTime + .05); setTimeout(() => { a.o.stop(); a.o.disconnect(); a.g.disconnect(); }, 100); delete activeOscillators[note]; } }
            function loadSong() { const songIndex = parseInt(songSelector.value, 10); if (songIndex < 0) { currentSong = null; scoreDisplay.innerHTML = ''; return; } currentSong = songs[songIndex]; restartSong(); }
            function restartSong() { if (!currentSong) return; currentNoteIndex = 0; renderScore(); skipPauses(); updateScoreHighlight(); }
            function renderScore() { scoreDisplay.innerHTML = ''; if (!currentSong) return; currentSong.score.forEach((noteKey, index) => { const noteEl = document.createElement('span'); noteEl.className = 'note'; if (noteKey === ' ') { noteEl.classList.add('pause'); } else { noteEl.textContent = noteKey; } noteEl.dataset.index = index; scoreDisplay.appendChild(noteEl); }); scoreDisplay.scrollTop = 0; }
            function skipPauses() { if (!currentSong) return; while (currentNoteIndex < currentSong.score.length && currentSong.score[currentNoteIndex] === ' ') { currentNoteIndex++; } }
            function updateScoreHighlight() { document.querySelectorAll('.note').forEach(n => n.classList.remove('current')); if (currentNoteIndex < currentSong.score.length) { const currentNoteEl = scoreDisplay.querySelector(\`.note[data-index="\${currentNoteIndex}"]\`); if (currentNoteEl && !currentNoteEl.classList.contains('pause')) { currentNoteEl.classList.add('current'); } } }
            function checkScore(pressedKey) { if (!currentSong || currentNoteIndex >= currentSong.score.length) return; const expectedKey = currentSong.score[currentNoteIndex]; const currentNoteEl = scoreDisplay.querySelector(\`.note[data-index="\${currentNoteIndex}"]\`); if (pressedKey === expectedKey) { currentNoteEl.classList.remove('current'); currentNoteEl.classList.add('correct'); currentNoteIndex++; skipPauses(); if (currentNoteIndex >= currentSong.score.length) { setTimeout(() => { scoreDisplay.innerHTML = '<span class="completion-message">üéâ Congratulations, song complete! üéâ</span>'; }, 500); } else { updateScoreHighlight(); } } else { const keyEl = keyMap[pressedKey]; if (keyEl) { keyEl.classList.add('incorrect'); setTimeout(() => keyEl.classList.remove('incorrect'), 300); } } }
            function handleKeyDown(keyElement) { if (keyElement && !keyElement.classList.contains('active')) { keyElement.classList.add('active'); playNote(keyElement.dataset.note); if (currentMode === 'sheet') { checkScore(keyElement.dataset.key); } } }
            function handleKeyUp(keyElement) { if (keyElement) { keyElement.classList.remove('active'); stopNote(keyElement.dataset.note); } }
            songs.forEach((song, index) => { const option = document.createElement('option'); option.value = index; option.textContent = song.name; songSelector.appendChild(option); });
            document.querySelectorAll('input[name="mode"]').forEach(radio => { radio.addEventListener('change', e => { currentMode = e.target.value; sheetMusicArea.style.display = currentMode === 'sheet' ? 'block' : 'none'; if (currentMode === 'sheet') { loadSong(); } else { currentSong = null; scoreDisplay.innerHTML = ''; } }); });

            // --- ENHANCEMENT: Add blur() after UI interaction ---
            songSelector.addEventListener('change', () => {
                loadSong();
                songSelector.blur();
            });
            restartBtn.addEventListener('click', () => {
                restartSong();
                restartBtn.blur();
            });

            keys.forEach(key => { key.addEventListener('mousedown', () => handleKeyDown(key)); key.addEventListener('mouseup', () => handleKeyUp(key)); key.addEventListener('mouseleave', () => handleKeyUp(key)); });
            window.addEventListener('keydown', e => { if (e.repeat) return; const keyElement = keyMap[e.key.toUpperCase()]; if (keyElement) handleKeyDown(keyElement); });
            window.addEventListener('keyup', e => { const keyElement = keyMap[e.key.toUpperCase()]; if (keyElement) handleKeyUp(keyElement); });
        </script>
    </body>
    </html>
  `;
}

