// üõ†Ô∏è Core Fix: Cross-platform Chromium executable search (Supports Windows & macOS)
function findChromiumExecutable(browsersPath) {
  if (!fs.existsSync(browsersPath)) return null;

  // 1. Get all directories starting with chromium, sorted to ensure the latest version is used
  const dirs = fs.readdirSync(browsersPath)
    .filter(d => d.startsWith('chromium-') && !d.includes('headless'))
    .sort().reverse();

  if (dirs.length === 0) {
    console.log('No chromium directory found');
    return null;
  }

  const platform = process.platform; // 'win32' or 'darwin' (macOS)
  console.log(`Current OS: ${platform}, Search path: ${browsersPath}`);

  for (const chromiumDir of dirs) {
    const basePath = path.join(browsersPath, chromiumDir);
    let execPath = null;

    if (platform === 'win32') {
      // ü™ü Windows Logic
      // Path structure: chromium-vXXXX/chrome-win/chrome.exe
      const winPath = path.join(basePath, 'chrome-win', 'chrome.exe');
      if (fs.existsSync(winPath)) {
        execPath = winPath;
      }
    } else if (platform === 'darwin') {
      // üçé macOS Logic
      // Find chrome-mac directory first
      const macBaseContents = fs.readdirSync(basePath);
      const chromeMacDir = macBaseContents.find(d => d.startsWith('chrome-mac'));

      if (chromeMacDir) {
        const chromeMacPath = path.join(basePath, chromeMacDir);

        // Priority attempt: Google Chrome for Testing.app (Newer Playwright versions)
        const newPath = path.join(chromeMacPath, 'Google Chrome for Testing.app', 'Contents', 'MacOS', 'Google Chrome for Testing');
        if (fs.existsSync(newPath)) {
          execPath = newPath;
        } else {
          // Fallback attempt: Chromium.app (Older versions)
          const oldPath = path.join(chromeMacPath, 'Chromium.app', 'Contents', 'MacOS', 'Chromium');
          if (fs.existsSync(oldPath)) execPath = oldPath;
        }
      }
    } else {
      // üêß Linux (Fallback)
      const linuxPath = path.join(basePath, 'chrome-linux', 'chrome');
      if (fs.existsSync(linuxPath)) execPath = linuxPath;
    }

    // If a valid executable is found, return immediately
    if (execPath) {
      console.log(`‚úÖ Executable found (${platform}):`, execPath);
      return execPath;
    }
  }

  console.log('‚ùå Executable not found after traversing all directories');
  return null;
}

(async function main() {
