  if (!await packages.exists('playwright-core')) {
    vscode.window.showInformationMessage('üì¶ Installing playwright-core...');
    const result = await packages.install('playwright-core');
    if (!result.success) {
      vscode.window.showErrorMessage(`‚ùå Installation failed: ${result.error}`);
      return vm.stop();
    }
  }

  // 2. Install browser binaries
  if (!await packages.exists('@playwright/browser-chromium')) {
