      return vm.stop();
    }
    vscode.window.showInformationMessage('‚úÖ Chromium browser installation complete');
  }

  const { chromium } = packages.require('playwright-core');

  // 3. Get the correct executable path
  const execPath = findChromiumExecutable(browsersPath);

  if (!execPath || !fs.existsSync(execPath)) {
    vscode.window.showErrorMessage(`‚ùå Chromium executable not found (OS: ${process.platform})`);
