<script nonce="${nonce}">
const vscode = acquireVsCodeApi();
const rows = document.getElementById('rows');
const stats = document.getElementById('stats');
const titleEl = document.getElementById('title');
const dots = document.getElementById('dots');
let total = 0;

window.addEventListener('message', (e) => {
  const m = e.data || {};
  if (m.type === 'append') {
    total = m.count;
    const tr = document.createElement('tr');
    tr.innerHTML = \`
      <td>\${m.count}</td>
      <td>\${m.title.replace(/</g, '&lt;')}</td>
      <td><span class="url" data-url="\${m.url}">\${m.url}</span></td>
    \`;
    rows.appendChild(tr);
    document.getElementById('empty').style.display = rows.children.length ? 'none' : '';
    titleEl.textContent = \`üöÄ Playwright scraping, \${total} items fetched\`;
    stats.textContent = \`\${total} items\`;
  } else if (m.type === 'done') {
    titleEl.textContent = \`‚úÖ Playwright scraping complete, total \${m.count} items\`;
    dots.style.display = 'none';
  }
});

document.body.addEventListener("click", (e) => {
  const el = e.target.closest(".url[data-url]");
  if (el) {
    e.preventDefault();
    vscode.postMessage({ type: "openLink", href: el.dataset.url });
  }
});
</script>
`;

  const post = (m) => panel.webview.postMessage(m);

  panel.webview.onDidReceiveMessage((msg) => {
    if (msg.type === "openLink" && msg.href) {
      vscode.env.openExternal(vscode.Uri.parse(msg.href));
    }
  });

  console.log('Launching browser:', execPath);
  const browser = await chromium.launch({ 
    headless: false,
    executablePath: execPath
  });

  try {
    const ctx = await browser.newContext({ userAgent: UA });
    const page = await ctx.newPage();
    const found = new Map();

    await page.goto(TAG_URL, { waitUntil: "domcontentloaded", timeout: 60000 });
    await page.waitForTimeout(1200);

    for (let i = 0; i < SCROLL_TIMES; i++) {
      await page.evaluate((y) => window.scrollBy(0, y), 900);
      await sleep(DELAY_MS);

      const partial = await page.evaluate(() => {
        const clean = (t='') => t.replace(/\s+/g,' ').trim();
        const anchors = Array.from(document.querySelectorAll("a[href]")).filter(a => /\/public\/(resources\/|clubs\/[^/]+\/resources\/)/.test(a.href) && !/\/events\//.test(a.href) && !/\/resources\/?$/.test(a.href) );
        return anchors.map(a => {
          const url = a.href.split('#')[0];
          let title = clean(a.textContent || '');
          if (!title && a.parentElement) title = clean(a.parentElement.textContent || '');
          return { title, url };
        });
      });

      for (const c of partial) {
        if (!found.has(c.url) && /ChatGPT/i.test(c.title)) {
          found.set(c.url, c);
          post({ type: "append", count: found.size, title: c.title, url: c.url });
        }
      }
    }

    post({ type: "done", count: found.size });
    await ctx.close();
  } catch (e) {
    vscode.window.showErrorMessage("Scraping failed: " + (e?.message || e));
  } finally {
    await browser.close();
  }
})();

const vscode = require("vscode");
const { packages, vm } = statusBarHelper.v1;

(async () => {
  if (!await packages.exists("axios")) await packages.install("axios");

  // Axios v1.x compatibility fix
  let axios = packages.require("axios");
  if (axios.default) axios = axios.default;

  // Define city coordinates (Open-Meteo requires latitude and longitude)
  const cities = {
    "Taipei": { name: "Taipei", lat: 25.0330, lon: 121.5654 },
    "Tokyo": { name: "Tokyo", lat: 35.6762, lon: 139.6503 },
    "NewYork": { name: "New York", lat: 40.7128, lon: -74.0060 },
    "London": { name: "London", lat: 51.5074, lon: -0.1278 }
  };

  // 1. Let user select a city
  const selected = await vscode.window.showQuickPick(Object.keys(cities), {
    placeHolder: "Select a city to check the weather"
  });

  if (!selected) return vm.stop(); // User cancelled

  const city = cities[selected];
  // API params: current_weather=true gets current weather only
  const apiUrl = `https://api.open-meteo.com/v1/forecast?latitude=${city.lat}&longitude=${city.lon}&current_weather=true`;

  try {
    vscode.window.setStatusBarMessage(`Checking weather for ${city.name}...`, 2000);

    // 2. Call API
    const res = await axios.get(apiUrl);
    const weather = res.data.current_weather;

    // 3. Simple weather code conversion (WMO Weather interpretation codes)
    let icon = "üå§Ô∏è";
    if (weather.weathercode >= 61) icon = "üåßÔ∏è"; // Rain
    else if (weather.weathercode === 0) icon = "‚òÄÔ∏è"; // Clear sky
    else if (weather.weathercode <= 3) icon = "‚òÅÔ∏è"; // Cloudy

    // 4. Display result
    vscode.window.showInformationMessage(
      `${icon} Current Weather in ${city.name}`,
      {
        modal: true,
        detail: `Temperature: ${weather.temperature}¬∞C\nWind Speed: ${weather.windspeed} km/h\nCode: ${weather.weathercode}`
      }
    );

  } catch (error) {
    vscode.window.showErrorMessage("Weather check failed: " + error.message);
  }

  vm.stop();
})();

