      try { vm.stop({ type: 'selfStop', from: SELF, at: Date.now() }); } catch {}
    }
  }

  // Receive messages from peer
  vm.onMessage((from, payload) => {
    try {
      if (from !== PEER) return; // Only show peer messages
      const text = (payload && payload.text != null) ? String(payload.text) : JSON.stringify(payload);
      const ts = new Date(payload?.at || Date.now()).toLocaleTimeString();
