      const $ = id => document.getElementById(id);
      const gc = $('game').getContext('2d');
      const hc = $('hold').getContext('2d');
      const nc = $('next').getContext('2d');

      const scoreEl=$('score'), linesEl=$('lines'), levelEl=$('level'), bestEl=$('best');
      const btnPause=$('btnPause'), btnRestart=$('btnRestart');
      const startLevelInput=$('startLevel'), startLevelLabel=$('startLevelLabel');
      const btnApply=$('btnApply');

      const SETTINGS_KEY = 'sbh_tetris_settings_v3';
      function loadSettings(){
        try{
          const v = JSON.parse(localStorage.getItem(SETTINGS_KEY)||'{}');
          return { startLevel: Number.isFinite(v.startLevel)? v.startLevel : 1 };
        }catch{ return { startLevel:1 }; }
      }
      function saveSettings(s){ localStorage.setItem(SETTINGS_KEY, JSON.stringify(s)); }

      const S = loadSettings();
      startLevelInput.value = String(S.startLevel);
      startLevelLabel.textContent = S.startLevel;

      // 固定參數
      const BASE_MS  = 700; // 基礎重力（ms/格）
      const SOFT_MS  = 60;  // 按住↓ 的連續 soft drop 間隔（ms/格）
      const MIN_MS   = 80;  // 最快重力
      const LOCK_DELAY_MS = 500; // ★ 落地後的「延遲鎖定」時間，可在這裡調整

      // ====== Game Constants ======
      const W = 10, H = 20, CELL = 24;
      const BEST_KEY = 'sbh_tetris_high';
      const COLORS = {
        I:'#00f0f0', O:'#f0f000', T:'#a020f0', S:'#00d000', Z:'#d00000', J:'#0000d0', L:'#f0a000', GHOST:'rgba(255,255,255,0.15)'
      };
      const ROT = {
        I: [
          [[0,1],[1,1],[2,1],[3,1]],
          [[2,0],[2,1],[2,2],[2,3]],
          [[0,2],[1,2],[2,2],[3,2]],
          [[1,0],[1,1],[1,2],[1,3]],
        ],
        O: [[[1,0],[2,0],[1,1],[2,1]],[[1,0],[2,0],[1,1],[2,1]],[[1,0],[2,0],[1,1],[2,1]],[[1,0],[2,0],[1,1],[2,1]]],
        T: [
          [[1,0],[0,1],[1,1],[2,1]],
          [[1,0],[1,1],[2,1],[1,2]],
          [[0,1],[1,1],[2,1],[1,2]],
          [[1,0],[0,1],[1,1],[1,2]],
        ],
        S: [
          [[1,0],[2,0],[0,1],[1,1]],
          [[1,0],[1,1],[2,1],[2,2]],
          [[1,1],[2,1],[0,2],[1,2]],
          [[0,0],[0,1],[1,1],[1,2]],
        ],
        Z: [
          [[0,0],[1,0],[1,1],[2,1]],
          [[2,0],[1,1],[2,1],[1,2]],
          [[0,1],[1,1],[1,2],[2,2]],
          [[1,0],[0,1],[1,1],[0,2]],
        ],
        J: [
          [[0,0],[0,1],[1,1],[2,1]],
          [[1,0],[2,0],[1,1],[1,2]],
          [[0,1],[1,1],[2,1],[2,2]],
          [[1,0],[1,1],[0,2],[1,2]],
        ],
        L: [
          [[2,0],[0,1],[1,1],[2,1]],
          [[1,0],[1,1],[1,2],[2,2]],
          [[0,1],[1,1],[2,1],[0,2]],
          [[0,0],[1,0],[1,1],[1,2]],
        ]
      };
      const TYPES = Object.keys(ROT);

      // ====== Game State ======
      let board, queue, cur, hold=null, canHold=true;
      let score=0, lines=0, level=S.startLevel, best=Number(localStorage.getItem(BEST_KEY)||0);
      let paused=false, over=false, timer=null;

      // Soft drop
      let softHeld=false, softTimer=null;

      // ★ Lock Delay 狀態
      let grounded=false;      // 是否已經「接地」（下一格下不去）
      let lockExpire=0;        // 何時到期鎖定（time ms）

      bestEl.textContent = String(best);

      // ====== Helpers ======
      function emptyBoard(){ return Array.from({length:H},()=>Array(W).fill(null)); }
      function newBag(){ const a=[...TYPES]; for(let i=a.length-1;i>0;i--){ const j=(Math.random()*(i+1))|0; [a[i],a[j]]=[a[j],a[i]] } return a; }
      function ensureQueue(){ if(queue.length<7) queue.push(...newBag()); }
      function spawn(){
        const type = queue.shift(); ensureQueue();
        cur = { type, r:0, x:3, y:0 };
        grounded=false; lockExpire=0;
        if (!canPlace(cur, cur.x, cur.y)){ gameOver(); }
        canHold = true;
      }
      function cells(p){ return ROT[p.type][p.r]; }
      function canPlace(p, nx, ny, nr=p.r){
        const shape = ROT[p.type][nr];
        for(const [dx,dy] of shape){
          const x = nx+dx, y = ny+dy;
          if (x<0 || x>=W || y<0 || y>=H) return false;
          if (board[y][x]) return false;
        }
        return true;
      }
      function startLockDelay(){ grounded=true; lockExpire = Date.now() + LOCK_DELAY_MS; }
      function clearLock(){ grounded=false; lockExpire=0; }

      function lock(){
        for(const [dx,dy] of cells(cur)){
          const x=cur.x+dx, y=cur.y+dy;
          if (y>=0 && y<H) board[y][x] = cur.type;
        }
        grounded=false; lockExpire=0;
        clearLines(); spawn();
      }
      function clearLines(){
        let cleared=0;
        for(let y=H-1;y>=0;){
          if (board[y].every(Boolean)){ board.splice(y,1); board.unshift(Array(W).fill(null)); cleared++; }
          else y--;
        }
        if (cleared>0){
          score += [0,100,300,500,800][cleared] * level;
          lines += cleared;
          const newLevel = Math.max(1, S.startLevel + Math.floor(lines/10));
          if (newLevel!==level){ level=newLevel; restartGravity(); }
          updateHUD();
        }
      }
      function holdSwap(){
        if (!canHold || over) return;
        if (hold===null){ hold=cur.type; spawn(); }
        else { const t=hold; hold=cur.type; cur={type:t, r:0, x:3, y:0}; if(!canPlace(cur,cur.x,cur.y)){ gameOver(); return; } }
        canHold=false; drawSide();
      }

      // 成功移動/旋轉後，更新接地狀態：如果仍接地就重置 lock delay
      function refreshLockAfterAction(){
        if (canPlace(cur, cur.x, cur.y+1)) {
          clearLock();
        } else {
          startLockDelay(); // still grounded → reset timer
        }
      }

      function rotate(dir){ // +1 CW / -1 CCW
        if (over) return;
        const nr = (cur.r + (dir>0?1:3)) % 4;
        const kicks = [[0,0],[1,0],[-1,0],[0,-1],[2,0],[-2,0]];
        for (const [kx,ky] of kicks){
          const nx = cur.x+kx, ny=cur.y+ky;
          if (canPlace(cur,nx,ny,nr)){ cur.r=nr; cur.x=nx; cur.y=ny; refreshLockAfterAction(); draw(); return; }
        }
      }

      function move(dx){
        if (over) return;
        const nx = cur.x + dx;
        if (canPlace(cur, nx, cur.y)){ cur.x = nx; refreshLockAfterAction(); draw(); }
      }

      function softDropStep(){
        if (paused || over) return;
        if (canPlace(cur, cur.x, cur.y+1)){
          cur.y++; score += 1; updateHUD(false);
          clearLock(); // 一旦往下成功，取消接地狀態
          draw();
        } else {
          if (!grounded) { startLockDelay(); }
          // 已接地：等到時間到才 lock
          if (Date.now() >= lockExpire){ lock(); }
          draw();
        }
      }

      function hardDrop(){
        if (over) return;
        let dist=0; while (canPlace(cur,cur.x,cur.y+1)){ cur.y++; dist++; }
        score += dist*2; updateHUD(false);
        lock(); draw(); // Hard drop 立即鎖定
      }

      // ====== Gravity ======
      function gravityMs(lv){
        return Math.max(MIN_MS, BASE_MS - (lv-1)*60);
      }
      function restartGravity(){
        clearInterval(timer);
        timer = setInterval(()=>{
          if (paused || over) return;
          if (canPlace(cur, cur.x, cur.y+1)){
            cur.y++;
            clearLock();
          } else {
            if (!grounded) { startLockDelay(); }
            else if (Date.now() >= lockExpire) { lock(); }
          }
          draw();
        }, gravityMs(level));
      }

      // ====== Drawing ======
      function drawCell(ctx,x,y,color){
        ctx.fillStyle=color;
        ctx.fillRect(x*CELL, y*CELL, CELL, CELL);
        ctx.fillStyle='rgba(255,255,255,0.07)';
        ctx.fillRect(x*CELL, y*CELL, CELL, Math.max(2, CELL*0.18));
      }
      function ghostY(){ let gy=cur.y; while (canPlace(cur,cur.x,gy+1)) gy++; return gy; }
      function draw(){
        const c = gc.canvas; gc.clearRect(0,0,c.width,c.height);
        for(let y=0;y<H;y++){ for(let x=0;x<W;x++){ gc.fillStyle=((x+y)%2===0)?'#0a0a0a':'#0c0c0c'; gc.fillRect(x*CELL,y*CELL,CELL,CELL); } }
        for(let y=0;y<H;y++){ for(let x=0;x<W;x++){ const t=board[y][x]; if(t) drawCell(gc,x,y,COLORS[t]); } }
        const gy = ghostY();
        for(const [dx,dy] of cells(cur)){ const x=cur.x+dx,y=gy+dy; gc.fillStyle=COLORS.GHOST; gc.fillRect(x*CELL,y*CELL,CELL,CELL); }
        for(const [dx,dy] of cells(cur)){ const x=cur.x+dx,y=cur.y+dy; drawCell(gc,x,y,COLORS[cur.type]); }

        // 如果接地中，可在底部畫一圈淡邊提示（可選）
        // if (grounded) { gc.strokeStyle='rgba(255,255,255,.15)'; gc.lineWidth=2; gc.strokeRect(0, (H-1)*CELL+1, W*CELL, CELL-2); }

        drawSide();
        if (over) drawGameOver();
      }
      function drawGameOver(){
        const c = gc.canvas; gc.fillStyle='rgba(0,0,0,0.6)'; gc.fillRect(0,0,c.width,c.height);
        gc.fillStyle='#fff'; gc.textAlign='center'; gc.font='bold 22px system-ui'; gc.fillText('Game Over', c.width/2, c.height/2 - 10);
        gc.font='14px system-ui'; gc.fillText('Press R to Restart', c.width/2, c.height/2 + 16);
      }
      function drawMini(ctx,type,cell=16){
        ctx.clearRect(0,0,ctx.canvas.width,ctx.canvas.height);
        if(!type) return;
        const shape=ROT[type][0]; const ox=2, oy=2;
        for(const [dx,dy] of shape){
          ctx.fillStyle=COLORS[type]; ctx.fillRect((ox+dx)*cell,(oy+dy)*cell,cell,cell);
          ctx.fillStyle='rgba(255,255,255,0.08)'; ctx.fillRect((ox+dx)*cell,(oy+dy)*cell,cell,Math.max(2,cell*0.18));
        }
      }
      function drawSide(){
        drawMini(hc, hold, 16);
        nc.clearRect(0,0,nc.canvas.width,nc.canvas.height);
        const show = queue.slice(0,5), cell=14;
        show.forEach((t,i)=>{
          const offY = i*46 + 4;
          const shape = ROT[t][0];
          for(const [dx,dy] of shape){
            nc.fillStyle=COLORS[t];
            nc.fillRect((2+dx)*cell, (1+dy)*cell + offY, cell, cell);
            nc.fillStyle='rgba(255,255,255,0.08)';
            nc.fillRect((2+dx)*cell, (1+dy)*cell + offY, cell, Math.max(2,cell*0.18));
          }
        });
      }

      // ====== HUD ======
      function updateHUD(updateBest=true){
        scoreEl.textContent = String(score);
        linesEl.textContent = String(lines);
        levelEl.textContent = String(level);
        if (updateBest){
          const b = Math.max(Number(localStorage.getItem(BEST_KEY)||0), score);
          localStorage.setItem(BEST_KEY, b);
          bestEl.textContent = String(b);
        }
      }
      function gameOver(){ over=true; clearInterval(timer); stopSoftHeld(); updateHUD(); draw(); }

      // ====== Reset / Init ======
      function resetGame(){
        board = emptyBoard();
        queue = [...newBag(), ...newBag()];
        cur=null; hold=null; canHold=true;
        score=0; lines=0; level=S.startLevel; paused=false; over=false;
        grounded=false; lockExpire=0;
        updateHUD();
        spawn();
        restartGravity();
        draw();
      }

      // ====== Input ======
      function stopSoftHeld(){ softHeld=false; clearInterval(softTimer); softTimer=null; }
      window.addEventListener('keydown', (e)=>{
        if (e.key===' '){ e.preventDefault(); }
        if (over){ if (e.key==='r'||e.key==='R'){ resetGame(); } return; }

        if (e.key==='p'||e.key==='P'){ paused=!paused; btnPause.textContent = paused?'▶️ Resume':'⏸️ Pause'; return; }
        if (paused) return;

        if (e.key==='ArrowLeft'){ move(-1); }
        else if (e.key==='ArrowRight'){ move(1); }
        else if (e.key==='ArrowDown'){
          if (!softHeld){ softHeld=true; softDropStep(); softTimer=setInterval(softDropStep, Math.max(10, SOFT_MS)); }
        }
        else if (e.key==='ArrowUp' || e.key==='x' || e.key==='X'){ rotate(+1); }
        else if (e.key==='z' || e.key==='Z'){ rotate(-1); }
        else if (e.key==='c' || e.key==='C' || e.key==='Shift'){ holdSwap(); }
        else if (e.key===' '){ hardDrop(); }
        else if (e.key==='r' || e.key==='R'){ resetGame(); }
      });
      window.addEventListener('keyup', (e)=>{ if (e.key==='ArrowDown'){ stopSoftHeld(); } });
      window.addEventListener('blur', ()=>{ if(!over){ paused=true; btnPause.textContent='▶️ Resume'; stopSoftHeld(); } });

      // Buttons
      btnPause.addEventListener('click', ()=>{ if(over) return; paused=!paused; btnPause.textContent = paused?'▶️ Resume':'⏸️ Pause'; });
      btnRestart.addEventListener('click', resetGame);

      // Settings UI
      startLevelInput.addEventListener('input', ()=> startLevelLabel.textContent = startLevelInput.value);
      btnApply.addEventListener('click', ()=>{
        S.startLevel = Number(startLevelInput.value);
        saveSettings(S);
        resetGame();
      });

      // Init
      resetGame();
    </script>
  </body>
  </html>`;
}

// 2048 — for status-bar-helper (VSCode Webview)
// Controls: ←↑→↓ / WASD · U Undo · R Restart
// Features: 4x4、分數/最高分、Undo(一步)、新局、自動聚焦、簡單動畫

const vscode = require('vscode');
const { vm } = statusBarHelper.v1;

(function main(){
  const panel = vscode.window.createWebviewPanel(
    'sbh2048',
    '🧩 2048',
    vscode.ViewColumn.Active,
    { enableScripts: true, retainContextWhenHidden: true }
  );
  vm.onStop(() => { try { panel.dispose(); } catch {} });
  panel.onDidDispose(() => vm.stop());
  const nonce = Math.random().toString(36).slice(2);
  panel.webview.html = getHtml(nonce);
})();

function getHtml(nonce){
  return /* html */`
  <!doctype html>
  <html lang="zh-Hant">
  <head>
    <meta charset="utf-8">
    <meta http-equiv="Content-Security-Policy"
      content="default-src 'none'; img-src data:; style-src 'unsafe-inline'; script-src 'nonce-${nonce}';">
    <meta name="viewport" content="width=device-width, initial-scale=1" />
    <title>2048</title>
    <style>
      :root{
        --bg:#0f1115; --fg:#e6e6e6; --muted:#9aa2b1; --card:#161a22; --line:#222838;
        --accent:#7bd88f; --board:#111722; --cell:#0e1219; --gap:10px; --size:360px; --radius:12px;
      }
      html,body{height:100%} body{margin:0;background:var(--bg);color:var(--fg);font-family:ui-sans-serif,system-ui,-apple-system,"Segoe UI",Roboto,Arial}
      .wrap{max-width:860px;margin:0 auto;padding:14px}
      .top{display:flex;align-items:center;gap:10px;flex-wrap:wrap}
      .sp{flex:1}
      .btn{background:#1d2330;border:1px solid #2a3247;border-radius:10px;color:var(--fg);padding:8px 12px;cursor:pointer}
      .btn:hover{filter:brightness(1.08)}
      .card{background:var(--card);border:1px solid var(--line);border-radius:14px;padding:12px;margin-top:12px}
      .kpis{display:flex;gap:12px;flex-wrap:wrap}
      .kpi{background:#121620;border:1px solid var(--line);border-radius:12px;padding:8px 12px;min-width:120px}
      .legend{font-size:12px;color:var(--muted)}
      .boardWrap{display:flex;justify-content:center}
      .board{position:relative;width:var(--size);height:var(--size);background:var(--board);border-radius:16px;padding:var(--gap);box-sizing:border-box}
      .grid{position:absolute;left:var(--gap);top:var(--gap);right:var(--gap);bottom:var(--gap);display:grid;grid-template-columns:repeat(4,1fr);grid-template-rows:repeat(4,1fr);gap:var(--gap)}
      .bgcell{background:var(--cell);border-radius:var(--radius)}
      .tiles{position:absolute;left:var(--gap);top:var(--gap);right:var(--gap);bottom:var(--gap)}
      .tile{
        position:absolute; display:flex;align-items:center;justify-content:center;
        font-weight:800; border-radius:var(--radius); user-select:none;
        transition: transform 120ms ease, background 120ms ease, color 120ms ease;
        will-change: transform;
      }
      .pop{animation:pop .12s ease}
      @keyframes pop{0%{transform:scale(.6)}100%{transform:scale(1)}}
      .bump{animation:bump .12s ease}
      @keyframes bump{0%{transform:scale(1)}50%{transform:scale(1.08)}100%{transform:scale(1)}}

      /* 顏色表（可自行微調） */
      .v2 {background:#2b364d;color:#e6eefc}
      .v4 {background:#31405b;color:#eef4ff}
      .v8 {background:#3a6eea;color:#fff}
      .v16{background:#2aa3ef;color:#fff}
      .v32{background:#22c6b8;color:#102a2a}
      .v64{background:#7bd88f;color:#0e2414}
      .v128{background:#ffd34d;color:#40320a}
      .v256{background:#ffb14d;color:#3b2510}
      .v512{background:#ff8a5c;color:#3a1710}
      .v1024{background:#ff6b6b;color:#3a0f12}
      .v2048{background:#b892ff;color:#1e123a}
      .v4096,.v8192,.v16384{background:#64d7d6;color:#0c1e21}

      /* 小畫面時可改尺寸：把 --size 改 320px 或 300px */
      @media (max-width:480px){ :root{ --size:320px } }
    </style>
  </head>
  <body>
    <div class="wrap">
      <div class="top">
        <div style="font-weight:700">🧩 2048</div>
        <div class="sp"></div>
        <button id="btnUndo" class="btn" title="Undo (U)">↩️ Undo</button>
        <button id="btnNew" class="btn" title="Restart (R)">🔄 New Game</button>
      </div>

      <div class="card">
        <div class="kpis">
          <div class="kpi">Score: <b id="score">0</b></div>
          <div class="kpi">Best: <b id="best">0</b></div>
        </div>
        <div class="legend" style="margin-top:6px">Use ←↑→↓ / WASD 移動；U 悔一步；R 重新開始。</div>
      </div>

      <div class="card">
        <div class="boardWrap">
          <div id="board" class="board" tabindex="0" title="Use Arrow Keys / WASD to move">
            <div class="grid" id="grid"></div>
            <div class="tiles" id="tiles"></div>
          </div>
        </div>
      </div>
    </div>

    <script nonce="${nonce}">
      const $ = id => document.getElementById(id);
      const boardEl = $('board'), gridEl = $('grid'), tilesEl = $('tiles');
      const scoreEl=$('score'), bestEl=$('best'), btnNew=$('btnNew'), btnUndo=$('btnUndo');

      const N = 4;
      const LS_KEY = 'sbh_2048_state_v1';
      const BEST_KEY = 'sbh_2048_best';
      const SIZE = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--size')) || 360;
      const GAP = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--gap')) || 10;
      const inner = SIZE - GAP*2;
      const cell = Math.floor((inner - GAP*3)/4); // 4 cols => 3 gaps

      // 背景格
      function buildBg(){
        gridEl.innerHTML='';
        for(let i=0;i<N*N;i++){
          const c=document.createElement('div');
          c.className='bgcell';
          gridEl.appendChild(c);
        }
      }

      // 狀態
      let grid, score=0, best=Number(localStorage.getItem(BEST_KEY)||0);
      let prevState=null; // Undo 專用：{grid,score}

      // 工具
      function cloneGrid(g){ return g.map(r=>r.slice()); }
      function emptyGrid(){ return Array.from({length:N}, ()=>Array(N).fill(0)); }
      function randChoice(a){ return a[(Math.random()*a.length)|0]; }

      function saveState(){
        localStorage.setItem(LS_KEY, JSON.stringify({ grid, score }));
        localStorage.setItem(BEST_KEY, String(best));
      }
      function loadState(){
        try{
          const v = JSON.parse(localStorage.getItem(LS_KEY)||'null');
          if (v && v.grid && Array.isArray(v.grid) && v.grid.length===4) {
            grid = v.grid; score = v.score||0;
          } else {
            newGame(true);
          }
        }catch{
          newGame(true);
        }
        best = Math.max(best, Number(localStorage.getItem(BEST_KEY)||0));
      }

      // 方塊座標轉換到 px（用 transform 定位）
      function posToTransform(r,c){
        const x = c*(cell+GAP);
        const y = r*(cell+GAP);
        return \`translate(\${x}px, \${y}px)\`;
      }

      // 產生新方塊（2 的機率 90%，4 的機率 10%）
      function spawn(pop=true){
        const empties=[];
        for(let r=0;r<N;r++) for(let c=0;c<N;c++) if(grid[r][c]===0) empties.push([r,c]);
        if (empties.length===0) return false;
        const [r,c]=randChoice(empties);
        grid[r][c] = Math.random()<0.9 ? 2 : 4;
        draw(true, [{r,c,pop}]);
        return true;
      }

      // 合併與移動
      function slideRowLeft(row){
        // 壓縮非 0
        const a = row.filter(v=>v!==0);
        let moved=false, gained=0, mergedIndex=new Set();
        for(let i=0;i<a.length-1;i++){
          if (a[i]!==0 && a[i]===a[i+1] && !mergedIndex.has(i)){
            a[i]*=2; gained+=a[i]; a.splice(i+1,1); mergedIndex.add(i);
            moved=true;
          }
        }
        while(a.length<N) a.push(0);
        for(let i=0;i<N;i++) if(a[i]!==row[i]) moved=true;
        return {row:a, moved, gained};
      }

      function rotateGrid(g){
        // 旋轉 90 度（順時針）
        const r = emptyGrid();
        for(let i=0;i<N;i++) for(let j=0;j<N;j++) r[j][N-1-i]=g[i][j];
        return r;
      }

      function move(dir){ // 'left'|'right'|'up'|'down'
        // 保存 Undo
        prevState = { grid: cloneGrid(grid), score };

        let g = cloneGrid(grid);
        let totalGained=0, movedAny=false;

        function applyLeft(){
          for(let r=0;r<N;r++){
            const res = slideRowLeft(g[r]);
            g[r]=res.row;
            if (res.moved) movedAny=true;
            totalGained+=res.gained;
          }
        }

        if (dir==='left'){ applyLeft(); }
        else if (dir==='right'){ // 反轉 -> 左移 -> 反轉
          g = g.map(row=>row.slice().reverse());
          applyLeft();
          g = g.map(row=>row.slice().reverse());
        }
        else if (dir==='up'){ // 旋轉 -> 左移 -> 反旋
          g = rotateGrid(g);      // CW
          g = rotateGrid(g);      // 180
          g = rotateGrid(g);      // CCW => 等於轉到左移對齊
          applyLeft();
          g = rotateGrid( rotateGrid( rotateGrid(g) ) ); // 回正：CW
        }
        else if (dir==='down'){
          g = rotateGrid(g); // CW
          applyLeft();
          g = rotateGrid( rotateGrid( rotateGrid(g) ) ); // CCW
        }

        if (!movedAny){
          prevState=null; // 動不了就不保留 undo
          return false;
        }

        grid = g;
        score += totalGained;
        best = Math.max(best, score);
        draw(totalGained>0, [], totalGained>0); // bump 合併的格
        saveState();
        setTimeout(()=>{
          if (!spawn(true)) checkGameOver();
          saveState();
        }, 80);
        return true;
      }

      function checkGameOver(){
        // 有空位就沒結束
        for(let r=0;r<N;r++) for(let c=0;c<N;c++) if(grid[r][c]===0) return false;
        // 檢查相鄰可合併
        for(let r=0;r<N;r++){
          for(let c=0;c<N;c++){
            const v=grid[r][c];
            if (r+1<N && grid[r+1][c]===v) return false;
            if (c+1<N && grid[r][c+1]===v) return false;
          }
        }
        overlay('Game Over 😵  按 R 重來');
        return true;
      }

      // 介面繪製
      function tileClass(v){
        if (v<=2048) return 'v'+v;
        if (v<=4096) return 'v4096';
        if (v<=8192) return 'v8192';
        return 'v16384';
      }
      function fontSizeFor(v){
        const len=String(v).length;
        if (len<=2) return Math.floor(cell*0.42)+'px';
        if (len===3) return Math.floor(cell*0.36)+'px';
        if (len===4) return Math.floor(cell*0.30)+'px';
        return Math.floor(cell*0.26)+'px';
      }

      function draw(popNew=false, newTiles=[], bump=false){
        tilesEl.innerHTML='';

        // 背景格大小由 CSS Grid 控制，這裡只畫動態方塊
        for(let r=0;r<N;r++){
          for(let c=0;c<N;c++){
            const v=grid[r][c];
            if (!v) continue;
            const t=document.createElement('div');
            t.className='tile '+tileClass(v);
            t.style.width=cell+'px'; t.style.height=cell+'px';
            t.style.transform=posToTransform(r,c);
            t.style.fontSize=fontSizeFor(v);
            t.textContent=String(v);
            // 新方塊彈出
            if (newTiles.some(p=>p.r===r && p.c===c)) t.classList.add('pop');
            if (bump) t.classList.add('bump');
            tilesEl.appendChild(t);
          }
        }
        scoreEl.textContent = String(score);
        bestEl.textContent  = String(best);
      }

      // 遮罩訊息
      let overlayDiv=null;
      function overlay(text){
        if (overlayDiv){ overlayDiv.remove(); overlayDiv=null; }
        overlayDiv=document.createElement('div');
        overlayDiv.style.position='absolute';
        overlayDiv.style.inset='0';
        overlayDiv.style.background='rgba(0,0,0,.55)';
        overlayDiv.style.borderRadius='16px';
        overlayDiv.style.display='flex';
        overlayDiv.style.alignItems='center';
        overlayDiv.style.justifyContent='center';
        overlayDiv.style.flexDirection='column';
        overlayDiv.style.color='#fff';
        overlayDiv.style.font='bold 22px system-ui';
        overlayDiv.innerHTML = \`<div>\${text}</div><div style="font:14px system-ui;margin-top:8px">按 R 重新開始</div>\`;
        boardEl.appendChild(overlayDiv);
      }
      function clearOverlay(){ if (overlayDiv){ overlayDiv.remove(); overlayDiv=null; } }

      // 操作
      function handleKey(e){
        const k = e.key;
        if (['ArrowLeft','ArrowRight','ArrowUp','ArrowDown',' '].includes(k)) e.preventDefault();
        if (k==='ArrowLeft' || k==='a' || k==='A') move('left');
        else if (k==='ArrowRight' || k==='d' || k==='D') move('right');
        else if (k==='ArrowUp' || k==='w' || k==='W') move('up');
        else if (k==='ArrowDown' || k==='s' || k==='S') move('down');
        else if (k==='u' || k==='U') undo();
        else if (k==='r' || k==='R') newGame();
      }
      window.addEventListener('keydown', handleKey);
      boardEl.addEventListener('click', ()=>boardEl.focus());
      boardEl.addEventListener('mouseenter', ()=>boardEl.focus());

      // Undo（回到上一步）
      function undo(){
        if (!prevState) return;
        grid = cloneGrid(prevState.grid);
        score = prevState.score;
        prevState = null;
        clearOverlay();
        draw();
        saveState();
      }

      // 新局
      function newGame(first=false){
        grid = emptyGrid();
        score=0; prevState=null; clearOverlay();
        // 起手 2 塊
        spawn(false); spawn(false);
        draw();
        if (!first) saveState();
      }

      // 初始化
      buildBg();
      loadState();
      draw();
      if (!grid || grid.flat().every(v=>v===0)){ newGame(); }
      btnNew.addEventListener('click', newGame);
      btnUndo.addEventListener('click', undo);

      // 自動聚焦，確保鍵盤可用
      setTimeout(()=>boardEl.focus(), 0);
    </script>
  </body>
  </html>`;
}

// Gomoku (五子棋) — PvE only with AI levels 1..6
// For: status-bar-helper (VSCode Webview)
// 操作：滑鼠點格落子；New Game 開新局；Undo 悔棋（會回到你的回合）；可選你下黑/白、AI 難度 1..6
// 難度：1=超休閒(隨機+威脅)、2=貪婪、3=強貪婪(即時堵/殺)、4=搜尋2層、5=搜尋2層(更多候選)、6=搜尋3層(限時)

const vscode = require('vscode');
const { vm } = statusBarHelper.v1;

(function main(){
  const panel = vscode.window.createWebviewPanel(
    'sbhGomoku',
    '⚫️⚪️ Gomoku — 五子棋 (PvE)',
    vscode.ViewColumn.Active,
    { enableScripts: true, retainContextWhenHidden: true }
  );
  vm.onStop(() => { try{ panel.dispose(); } catch{} });
  panel.onDidDispose(() => vm.stop());
  const nonce = Math.random().toString(36).slice(2);
  panel.webview.html = getHtml(nonce);
})();

function getHtml(nonce){
  return /* html */`
  <!doctype html>
  <html lang="zh-Hant">
  <head>
    <meta charset="utf-8">
    <meta http-equiv="Content-Security-Policy"
      content="default-src 'none'; img-src data:; style-src 'unsafe-inline'; script-src 'nonce-${nonce}';">
    <meta name="viewport" content="width=device-width, initial-scale=1"/>
    <title>Gomoku</title>
    <style>
      :root{ --bg:#0f1115; --fg:#e6e6e6; --muted:#9aa2b1; --card:#161a22; --line:#222838; --btn:#1d2330; --btnb:#2a3247; --accent:#7bd88f; }
      html,body{height:100%}
      body{margin:0;background:var(--bg);color:var(--fg);font-family:ui-sans-serif,system-ui,-apple-system,"Segoe UI",Roboto,Arial}
      .wrap{max-width:860px;margin:0 auto;padding:14px}
      .top{display:flex;align-items:center;gap:10px;flex-wrap:wrap}
      .sp{flex:1}
      .btn{background:var(--btn);border:1px solid var(--btnb);border-radius:10px;padding:8px 12px;color:var(--fg);cursor:pointer}
      .btn:hover{filter:brightness(1.06)}
      .card{background:var(--card);border:1px solid var(--line);border-radius:14px;padding:12px;margin-top:12px}
      .grid{display:grid;grid-template-columns:repeat(6,auto);gap:10px;align-items:center}
      .legend{font-size:12px;color:var(--muted)}
      .board{display:flex;justify-content:center}
      canvas{display:block;background:#0b0f18;border-radius:12px;outline:none}
      select,input[type=range]{background:#101522;border:1px solid var(--line);color:var(--fg);padding:6px 8px;border-radius:10px}
      input[type=range]{accent-color:var(--accent)}
      .num{min-width:24px;display:inline-block;text-align:right}
      .kpis{display:flex;gap:12px;flex-wrap:wrap}
      .kpi{background:#121620;border:1px solid var(--line);border-radius:12px;padding:8px 12px;min-width:120px}
    </style>
  </head>
  <body>
    <div class="wrap">
      <div class="top">
        <div class="title" style="font-weight:700">⚫️⚪️ Gomoku — 五子棋（人機對戰）</div>
        <div class="sp"></div>
        <button id="btnUndo" class="btn">↩️ Undo</button>
        <button id="btnNew" class="btn">🔄 New Game</button>
      </div>

      <div class="card">
        <div class="kpis">
          <div class="kpi">回合：<b id="turnLabel">你（黑）</b></div>
          <div class="kpi">AI 難度：<b id="lvlLabel">3</b>/6</div>
        </div>
        <div class="grid" style="margin-top:8px">
          <div>你要下</div>
          <select id="selSide">
            <option value="black">黑子 (先手)</option>
            <option value="white">白子 (後手)</option>
          </select>

          <div>AI 難度</div>
          <div style="display:flex;align-items:center;gap:8px">
            <input id="level" type="range" min="1" max="6" step="1" value="3">
            <span id="lvl" class="num">3</span>
          </div>

          <div class="legend" style="grid-column:1/-1">提示：點棋盤下子；Undo 會回退「你與 AI 的最後兩手」，確保回到你的回合。</div>
        </div>
      </div>

      <div class="card">
        <div class="board">
          <!-- 420×420，15×15 盤，格距會自算 -->
          <canvas id="board" width="420" height="420" tabindex="0"></canvas>
        </div>
      </div>
    </div>

    <script nonce="${nonce}">
      const $ = id => document.getElementById(id);
      const cvs = $('board'), ctx = cvs.getContext('2d');
      const selSide=$('selSide'), levelEl=$('level'), lvlText=$('lvl'), lvlLabel=$('lvlLabel'), turnLabel=$('turnLabel');
      const btnNew=$('btnNew'), btnUndo=$('btnUndo');

      // ----- 盤面設定 -----
      const N = 15;               // 15×15
      const PAD = 20;             // 內距
      const W = cvs.width, H = cvs.height;
      const GAP = Math.floor((W - PAD*2) / (N-1));  // 格距
      const R = Math.floor(GAP * 0.42);             // 棋子半徑
      const EMPTY=0, BLACK=1, WHITE=2;

      // 狀態
      let board, turn, human=BLACK, ai=WHITE, lastMove=null, over=false;
      const history=[]; // push {x,y,side}

      // 難度對應（候選數、搜尋深度、時間上限）
      function cfgFor(level){
        switch(+level){
          case 1: return { topK:8, depth:0, limitMs:40 };            // 隨機於前幾名 + 威脅處理
          case 2: return { topK:8, depth:0, limitMs:80 };            // 貪婪
          case 3: return { topK:10, depth:1, limitMs:120 };          // 強貪婪（即時堵/殺）
          case 4: return { topK:10, depth:2, limitMs:180 };          // 2 層
          case 5: return { topK:14, depth:2, limitMs:220 };          // 2 層更多候選
          case 6: return { topK:10, depth:3, limitMs:350 };          // 3 層限時
          default: return { topK:10, depth:1, limitMs:120 };
        }
      }

      // ----- 初始化 -----
      function newBoard(){
        board = Array.from({length:N}, ()=>Array(N).fill(EMPTY));
        history.length=0; lastMove=null; over=false;
      }
      function setSides(){
        if (selSide.value==='black'){ human=BLACK; ai=WHITE; }
        else { human=WHITE; ai=BLACK; }
        turn = BLACK; // 黑先
        updateLabels();
      }
      function updateLabels(){
        lvlText.textContent = String(levelEl.value);
        lvlLabel.textContent = String(levelEl.value);
        const t = (turn===human)?'你':'AI';
        const tside = (turn===BLACK)?'黑':'白';
        turnLabel.textContent = \`\${t}（\${tside}）\`;
      }

      // ----- 繪製 -----
      function drawBoard(){
        ctx.clearRect(0,0,W,H);
        // 棋盤底
        for(let y=0;y<H;y+=20){
          for(let x=0;x<W;x+=20){
            ctx.fillStyle = ((x+y)%40===0)?'#0b0f18':'#0c111a';
            ctx.fillRect(x,y,20,20);
          }
        }
        // 線
        ctx.strokeStyle='#2b3144'; ctx.lineWidth=1.2;
        for(let i=0;i<N;i++){
          const x=PAD+i*GAP, y=PAD+i*GAP;
          ctx.beginPath(); ctx.moveTo(PAD, PAD+i*GAP); ctx.lineTo(W-PAD, PAD+i*GAP); ctx.stroke();
          ctx.beginPath(); ctx.moveTo(PAD+i*GAP, PAD); ctx.lineTo(PAD+i*GAP, H-PAD); ctx.stroke();
        }
        // 星位 (3,3)(3,11)(7,7)(11,3)(11,11)
        const stars=[[3,3],[3,11],[7,7],[11,3],[11,11]];
        ctx.fillStyle='#9aa2b1';
        for(const [gx,gy] of stars){
          const {cx,cy}=cellXY(gx,gy);
          ctx.beginPath(); ctx.arc(cx,cy,3,0,Math.PI*2); ctx.fill();
        }
        // 棋子
        for(let y=0;y<N;y++){
          for(let x=0;x<N;x++){
            const v=board[y][x];
            if (v!==EMPTY){
              const {cx,cy}=cellXY(x,y);
              const grad=ctx.createRadialGradient(cx,cy,2,cx,cy,R);
              if (v===BLACK){ grad.addColorStop(0,'#555'); grad.addColorStop(1,'#111'); }
              else { grad.addColorStop(0,'#fff'); grad.addColorStop(1,'#d9d9d9'); }
              ctx.fillStyle=grad; ctx.beginPath(); ctx.arc(cx,cy,R,0,Math.PI*2); ctx.fill();
            }
          }
        }
        // 最後一步高亮
        if (lastMove){
          const {cx,cy}=cellXY(lastMove.x,lastMove.y);
          ctx.strokeStyle='#ffd34d'; ctx.lineWidth=2;
          ctx.beginPath(); ctx.arc(cx,cy,R+3,0,Math.PI*2); ctx.stroke();
        }
        // 勝負提示
        if (over){
          ctx.fillStyle='rgba(0,0,0,.55)'; ctx.fillRect(0,0,W,H);
          ctx.fillStyle='#fff'; ctx.font='bold 22px system-ui'; ctx.textAlign='center';
          ctx.fillText((winnerSide===human?'你贏了 🎉':'AI 勝出 🤖'), W/2, H/2 - 12);
          ctx.font='14px system-ui'; ctx.fillText('按 New Game 重新開始', W/2, H/2 + 16);
        }
      }

      function cellXY(x,y){ return { cx: PAD + x*GAP, cy: PAD + y*GAP }; }
      function posToCell(px,py){
        // 將滑鼠座標 snap 到最近格點
        function snap(v){ return Math.round((v - PAD) / GAP); }
        const x=snap(px), y=snap(py);
        if (x<0||x>=N||y<0||y>=N) return null;
        const {cx,cy}=cellXY(x,y);
        const dist=Math.hypot(px-cx, py-cy);
        if (dist<=GAP*0.5) return {x,y};
        return null;
      }

      // ----- 勝負判定 -----
      function countDir(x,y,dx,dy,side){
        let c=0, i=1;
        while(true){
          const nx=x+dx*i, ny=y+dy*i;
          if (nx<0||ny<0||nx>=N||ny>=N) break;
          if (board[ny][nx]!==side) break;
          c++; i++;
        }
        return c;
      }
      function isFive(x,y,side){
        const dirs=[[1,0],[0,1],[1,1],[1,-1]];
        for (const [dx,dy] of dirs){
          const c = 1 + countDir(x,y,dx,dy,side) + countDir(x,y,-dx,-dy,side);
          if (c>=5) return true;
        }
        return false;
      }
      let winnerSide=null;

      // ----- 評分（啟發式） -----
      // 以「連子數 + 雙邊是否開放」評分；AI 分數 - 對手分數
      function evaluate(sideView=ai){
        let scoreAI=0, scoreHu=0;
        const dirs=[[1,0],[0,1],[1,1],[1,-1]];

        function lineScore(x,y,dx,dy,side){
          // 只在「此方向的起點」計分：前一格不是同色
          const px=x-dx, py=y-dy;
          if (px>=0&&py>=0&&px<N&&py<N && board[py][px]===side) return 0;

          let i=0, len=0;
          while(true){
            const nx=x+dx*i, ny=y+dy*i;
            if (nx<0||ny<0||nx>=N||ny>=N) break;
            if (board[ny][nx]!==side) break;
            len++; i++;
          }
          const leftOpen = (px>=0&&py>=0&&px<N&&py<N && board[py][px]===EMPTY);
          const rx=x+dx*len, ry=y+dy*len;
          const rightOpen = (rx>=0&&ry>=0&&rx<N&&ry<N && board[ry][rx]===EMPTY);
          const open = (leftOpen?1:0)+(rightOpen?1:0);

          // 基礎權重
          if (len>=5) return 1000000;
          if (len===4 && open===2) return 100000;
          if (len===4 && open===1) return 8000;
          if (len===3 && open===2) return 1500;
          if (len===3 && open===1) return 200;
          if (len===2 && open===2) return 80;
          if (len===2 && open===1) return 20;
          if (len===1 && open===2) return 6;
          return 0;
        }

        for(let y=0;y<N;y++){
          for(let x=0;x<N;x++){
            const v=board[y][x];
            if (v===EMPTY) continue;
            for(const [dx,dy] of dirs){
              const s = lineScore(x,y,dx,dy,v);
              if (v===ai) scoreAI += s; else scoreHu += s;
            }
          }
        }
        return scoreAI - scoreHu;
      }

      // 局部評分：把 (x,y) 當作 side 下，回傳此點的威脅值
      function pointScore(x,y,side){
        if (board[y][x]!==EMPTY) return -Infinity;
        let score=0;
        const dirs=[[1,0],[0,1],[1,1],[1,-1]];
        for(const [dx,dy] of dirs){
          // 往兩邊延伸，計算連子與空位情況
          let a=0,b=0,blockA=false,blockB=false;
          for(let i=1;i<5;i++){
            const nx=x+dx*i, ny=y+dy*i;
            if (nx<0||ny<0||nx>=N||ny>=N){ blockA=true; break; }
            const v=board[ny][nx];
            if (v===side) a++; else { if (v!==EMPTY) blockA=true; break; }
          }
          for(let i=1;i<5;i++){
            const nx=x-dx*i, ny=y-dy*i;
            if (nx<0||ny<0||nx>=N||ny>=N){ blockB=true; break; }
            const v=board[ny][nx];
            if (v===side) b++; else { if (v!==EMPTY) blockB=true; break; }
          }
          const len=a+b+1;
          const open = (blockA?0:1)+(blockB?0:1);
          if (len>=5){ score += 1e6; continue; }
          if (len===4 && open===2) score += 1e5;
          else if (len===4 && open===1) score += 6e3;
          else if (len===3 && open===2) score += 1200;
          else if (len===3 && open===1) score += 150;
          else if (len===2 && open===2) score += 60;
          else if (len===2 && open===1) score += 16;
        }
        return score;
      }

      // 候選走法：只取「距離任一棋子 2 格內」的空點
      function hasNeighbor2(x,y){
        for(let dy=-2; dy<=2; dy++){
          for(let dx=-2; dx<=2; dx++){
            if (dx===0 && dy===0) continue;
            const nx=x+dx, ny=y+dy;
            if (nx<0||ny<0||nx>=N||ny>=N) continue;
            if (board[ny][nx]!==EMPTY) return true;
          }
        }
        return false;
      }
      function genCandidates(topK, biasForSide=ai){
        const list=[];
        let hasStone=false;
        for(let y=0;y<N;y++) for(let x=0;x<N;x++) if(board[y][x]!==EMPTY){ hasStone=true; break; }
        if (!hasStone){
          // 空盤 → 取中心
          const c = Math.floor(N/2);
          return [{x:c,y:c,score:0}];
        }
        for(let y=0;y<N;y++){
          for(let x=0;x<N;x++){
            if (board[y][x]!==EMPTY) continue;
            if (!hasNeighbor2(x,y)) continue;
            const off = pointScore(x,y,biasForSide);
            const def = pointScore(x,y,biasForSide===ai?human:ai) * 0.96; // 防守稍微弱化
            const sc = Math.max(off, def);
            list.push({x,y,score:sc, offensive:off, defensive:def});
          }
        }
        list.sort((a,b)=>b.score-a.score);
        return list.slice(0, topK);
      }

      // 即時殺/堵：若有直接五連，或對手可五連，先處理
      function immediateMove(){
        const cand = genCandidates(30, ai);
        // 1) 我方直接取勝
        for(const c of cand){
          board[c.y][c.x]=ai;
          const win=isFive(c.x,c.y,ai);
          board[c.y][c.x]=EMPTY;
          if (win) return c;
        }
        // 2) 擋對手致勝
        for(const c of cand){
          board[c.y][c.x]=human;
          const oppWin=isFive(c.x,c.y,human);
          board[c.y][c.x]=EMPTY;
          if (oppWin) return c; // 放在這裡堵
        }
        return null;
      }

      // Minimax + Alpha-Beta + 限時
      function search(depth, alpha, beta, curSide, cfg, deadline){
        if (performance.now() > deadline) return { score:evaluate(), move:null, cutoff:true };
        if (depth===0) return { score:evaluate(), move:null };

        // 即時勝負快速判
        if (lastMove && isFive(lastMove.x,lastMove.y,lastMove.side)){
          const s = (lastMove.side===ai)? 999999 : -999999;
          return { score:s, move:null };
        }

        const cand = genCandidates(cfg.topK, curSide);
        if (cand.length===0) return { score:evaluate(), move:null };

        let bestMove=null;

        if (curSide===ai){
          let best=-Infinity;
          for(const c of cand){
            board[c.y][c.x]=curSide;
            const prev=lastMove; lastMove={x:c.x,y:c.y,side:curSide};
            let val;
            // 快速終止：放下即勝
            if (isFive(c.x,c.y,curSide)){
              val=999999 - (cfg.depth-depth); // 偏好更快勝
            }else{
              const r = search(depth-1, alpha, beta, human, cfg, deadline);
              val=r.score;
              if (r.cutoff) { board[c.y][c.x]=EMPTY; lastMove=prev; return { score:best, move:bestMove, cutoff:true }; }
            }
            board[c.y][c.x]=EMPTY; lastMove=prev;

            if (val>best){ best=val; bestMove={x:c.x,y:c.y}; }
            if (val>alpha) alpha=val;
            if (beta<=alpha) break; // beta-cut
          }
          return { score:best, move:bestMove };
        }else{
          let best=Infinity;
          for(const c of cand){
            board[c.y][c.x]=curSide;
            const prev=lastMove; lastMove={x:c.x,y:c.y,side:curSide};
            let val;
            if (isFive(c.x,c.y,curSide)){
              val=-999999 + (cfg.depth-depth);
            }else{
              const r = search(depth-1, alpha, beta, ai, cfg, deadline);
              val=r.score;
              if (r.cutoff) { board[c.y][c.x]=EMPTY; lastMove=prev; return { score:best, move:bestMove, cutoff:true }; }
            }
            board[c.y][c.x]=EMPTY; lastMove=prev;

            if (val<best){ best=val; bestMove={x:c.x,y:c.y}; }
            if (val<beta) beta=val;
            if (beta<=alpha) break; // alpha-cut
          }
          return { score:best, move:bestMove };
        }
      }

      // AI 下子
      async function aiMove(){
        if (over || turn!==ai) return;

        const level = +levelEl.value;
        const cfg = cfgFor(level);
        const deadline = performance.now() + cfg.limitMs;

        // Lv1/Lv2/Lv3 都先檢查即時殺/堵
        if (level<=3){
          const imm = immediateMove();
          if (imm){ place(imm.x,imm.y,ai); return; }
        }

        let move=null;
        if (cfg.depth===0){
          // 貪婪／隨機於前K
          const cand = genCandidates(cfg.topK, ai);
          if (cand.length===0){ // 幾乎不會
            const c = Math.floor(N/2); move={x:c,y:c};
          }else{
            if (level===1){
              const k = Math.max(1, Math.min(6, cand.length));
              move = cand[(Math.random()*k)|0];
            }else{
              move = cand[0];
            }
          }
        }else{
          const res = search(cfg.depth, -Infinity, Infinity, ai, cfg, deadline);
          move = res.move;
          if (!move){
            // 超時或沒有更好 → 用即時或貪婪做保底
            const imm = immediateMove();
            if (imm) move=imm; else move = genCandidates(1, ai)[0];
          }
        }

        if (move) place(move.x, move.y, ai);
      }

      // 落子（含勝負檢查）
      function place(x,y,side){
        if (board[y][x]!==EMPTY || over) return false;
        board[y][x]=side;
        history.push({x,y,side});
        lastMove={x,y,side};
        // 勝負
        if (isFive(x,y,side)){
          over=true; winnerSide=side;
        }else{
          // 換手
          turn = (side===BLACK)? WHITE : BLACK;
        }
        updateLabels(); drawBoard();
        // 若輪到 AI，排一下
        if (!over && turn===ai){
          setTimeout(aiMove, 30);
        }
        return true;
      }

      // 悔棋：回到玩家回合（一次吃掉 AI 與玩家各一步，如果 AI 還沒下就只退玩家一步）
      function undo(){
        if (history.length===0 || over) return;
        // 如果最後一步是 AI，就先退 AI；再看玩家
        let popped = history.pop();
        board[popped.y][popped.x]=EMPTY;

        if (popped.side===ai && history.length>0){
          popped = history.pop();
          board[popped.y][popped.x]=EMPTY;
        }
        over=false; winnerSide=null;
        // 設定回合：若有棋則相反於最後步，否則黑先
        const last = history[history.length-1];
        if (last){ turn = (last.side===BLACK)? WHITE : BLACK; lastMove=last; }
        else { turn = BLACK; lastMove=null; }
        // 確保是玩家回合，如不是就再退一步
        if (turn!==human && history.length>0){
          const p = history.pop(); board[p.y][p.x]=EMPTY;
          const last2 = history[history.length-1];
          turn = last2 ? ((last2.side===BLACK)? WHITE : BLACK) : BLACK;
          lastMove = last2 || null;
        }
        updateLabels(); drawBoard();
      }

      // 事件：下子
      cvs.addEventListener('mousedown', (e)=>{
        if (over || turn!==human) return;
        const rect=cvs.getBoundingClientRect();
        const pos = posToCell(e.clientX - rect.left, e.clientY - rect.top);
        if (!pos) return;
        place(pos.x,pos.y,human);
      });

      // 控制列
      btnNew.addEventListener('click', ()=>{
        newBoard(); setSides(); drawBoard();
        // 若 AI 先手
        if (turn===ai) setTimeout(aiMove, 60);
        cvs.focus();
      });
      btnUndo.addEventListener('click', ()=>{ undo(); cvs.focus(); });
      selSide.addEventListener('change', ()=>{
        const wasHuman=human;
        newBoard(); setSides(); drawBoard();
        if (turn===ai) setTimeout(aiMove, 60);
      });
      levelEl.addEventListener('input', ()=>{ lvlText.textContent=String(levelEl.value); lvlLabel.textContent=String(levelEl.value); });

      // 啟動
      newBoard(); setSides(); drawBoard();
      if (turn===ai) setTimeout(aiMove, 80);
      setTimeout(()=>cvs.focus(), 0);
    </script>
  </body>
  </html>`;
}

// Clipboard Tabs — 分頁式剪貼簿（使用 statusBarHelper.v1.storage 永久保存）
// 小尺寸 / 可編輯 / toolbar 會自動換行 / 關閉面板自動 vm.stop
const vscode = require('vscode');
const { storage, vm } = statusBarHelper.v1;

(async function main () {
  const setText = (t) => (typeof vm.setLabel === 'function' ? vm.setLabel(t) : (vm.setText?.(t)));
  const setTip  = (t) => vm.setTooltip?.(t);
  setText('📋 Tabs');
  setTip('Clipboard Tabs：執行即開啟/聚焦面板（資料存於 SBH storage）');

  // ------------- storage（workspace 優先，否則用 global） -------------
  const store = storage?.workspace || storage?.global;
  if (!store) throw new Error('statusBarHelper.v1.storage 不可用');

  const STORE_KEY = 'clipboard.tabs.v1';
  const defaultState = { active: 'default', tabs: [ { id: 'default', name: 'Default', items: [] } ] };

  async function loadState() {
    const s = await store.get(STORE_KEY, null);
    return s && s.tabs ? s : defaultState;
  }
  async function saveState(state) {
    await store.set(STORE_KEY, state);
  }

  // ------------- 單例面板 -------------
  const g = globalThis;
  const panel = await createOrRevealPanel();

  vm.onStop?.(() => {
    try { panel?.dispose(); } catch {}
  });

  function safeStop(reason){
    try{
      if (g.__sbhStopping) return;
      g.__sbhStopping = true;
      vm.stop?.(reason);
    } finally { setTimeout(()=>{ g.__sbhStopping = false; }, 0); }
  }

  async function createOrRevealPanel(){
    if (g.__sbhClipboardTabsPanel && !g.__sbhClipboardTabsPanel._disposed) {
      try { g.__sbhClipboardTabsPanel.reveal(); return g.__sbhClipboardTabsPanel; } catch {}
    }
    const p = vscode.window.createWebviewPanel(
      'sbhClipboardTabs', 'Clipboard Tabs', vscode.ViewColumn.Active,
      { enableScripts: true, retainContextWhenHidden: true }
    );
    const nonce = Math.random().toString(36).slice(2);
    p.webview.html = getHtml(nonce);

    // 初始資料：等 webview 要（req:init）再給，並回填 storage 的資料
    p.webview.onDidReceiveMessage(async (msg) => {
      switch (msg.type) {
        case 'req:init': {
          const state = await loadState();
          p.webview.postMessage({ type: 'init', state });
          break;
        }
        case 'save': {
          // Webview 通知資料改變 → 寫入 storage
          if (msg.state && msg.state.tabs) {
            await saveState(msg.state);
            // 可回個 ACK（不一定要用）
            p.webview.postMessage({ type: 'saved' });
          }
          break;
        }
        case 'getSelection': {
          const ed = vscode.window.activeTextEditor;
          const text = ed ? ed.document.getText(ed.selection) : '';
          p.webview.postMessage({ type: 'selection', text });
          break;
        }
        case 'getClipboard': {
          const text = await vscode.env.clipboard.readText();
          p.webview.postMessage({ type: 'clipboard', text });
          break;
        }
        case 'writeClipboard': {
          await vscode.env.clipboard.writeText(String(msg.text || ''));
          vscode.window.showInformationMessage('已複製到系統剪貼簿');
          break;
        }
        case 'notify': {
          if (msg.level === 'warn') vscode.window.showWarningMessage(msg.message || '');
          else if (msg.level === 'error') vscode.window.showErrorMessage(msg.message || '');
          else vscode.window.showInformationMessage(msg.message || '');
          break;
        }
      }
    });

    p.onDidDispose(() => {
      p._disposed = true;
      // 關閉分頁 → 結束腳本
      safeStop('webview-closed');
    });

    g.__sbhClipboardTabsPanel = p;
    return p;
  }

  // ---------------- Webview ----------------
  function getHtml(nonce) {
    const csp = [
      "default-src 'none'",
      "img-src https:",
      "style-src 'unsafe-inline'",
      `script-src 'nonce-${nonce}'`,
    ].join('; ');
    return /*html*/`
<!DOCTYPE html>
<html lang="zh-Hant">
<head>
<meta charset="UTF-8" />
<meta http-equiv="Content-Security-Policy" content="${csp}">
<meta name="viewport" content="width=device-width, initial-scale=1.0" />
<title>Clipboard Tabs</title>
<style>
  :root{
    --bg: var(--vscode-editor-background, #1e1e1e);
    --fg: var(--vscode-foreground, #ddd);
    --muted: var(--vscode-descriptionForeground, #999);
    --accent: var(--vscode-button-background, #0e639c);
    --accentHover: var(--vscode-button-hoverBackground, #1177bb);
    --border: var(--vscode-editorWidget-border, #3c3c3c);
    --card: rgba(255,255,255,0.03);
    --shadow: rgba(0,0,0,0.2);
    --fs: 12px; --fs-sm: 11px; --pad: 8px; --pad-sm: 6px; --gap: 8px; --radius: 8px;
  }
  *{ box-sizing: border-box; }
  body{ margin:0; background:var(--bg); color:var(--fg); font-family: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Arial, "Noto Sans TC", "PingFang TC", sans-serif; font-size:var(--fs); }
  .toolbar{ display:flex; align-items:center; gap:var(--gap); padding:var(--pad); border-bottom:1px solid var(--border); position:sticky; top:0; background:var(--bg); z-index:5; flex-wrap:wrap; }
  .actions{ display:flex; gap:var(--gap); flex-wrap:wrap; }
  .search{ margin-left:auto; }
  .search input{ background:var(--card); border:1px solid var(--border); color:var(--fg); border-radius:var(--radius); padding:var(--pad-sm) 10px; width:220px; font-size:var(--fs); }
  @media (max-width: 560px){ .search{ flex-basis:100%; order:2; margin-left:0; } .search input{ width:100%; } .actions{ order:1; } }
  .btn{ border:1px solid var(--border); background:var(--card); color:var(--fg); padding:var(--pad-sm) 10px; border-radius:var(--radius); cursor:pointer; box-shadow:0 1px 2px var(--shadow); font-size:var(--fs); }
  .btn.primary{ background:var(--accent); border-color:transparent; color:white; }
  .btn.primary:hover{ background:var(--accentHover); }
  .btn:hover{ filter:brightness(1.08); }
  .btn.danger{ border-color:#a11; }
  .tabs{ display:flex; gap:6px; padding:8px var(--pad); border-bottom:1px solid var(--border); flex-wrap:wrap; }
  .tab{ padding:4px 10px; border:1px solid var(--border); border-radius:999px; background:var(--card); cursor:pointer; font-size:var(--fs); }
  .tab.active{ background:var(--accent); color:white; border-color:transparent; }
  .grid{ display:grid; grid-template-columns:repeat(auto-fill, minmax(220px,1fr)); gap:8px; padding:8px; }
  .card{ border:1px solid var(--border); border-radius:10px; background:var(--card); padding:8px; display:flex; flex-direction:column; gap:6px; }
  .card h4{ margin:0; font-size:var(--fs); }
  .card pre{ margin:0; font-size:var(--fs-sm); white-space:pre-wrap; word-break:break-word; font-family: ui-monospace,"SFMono-Regular",Menlo,Consolas,monospace; max-height:120px; overflow:auto; }
  .row{ display:flex; align-items:center; gap:6px; flex-wrap:wrap; }
  .muted{ color:var(--muted); font-size:var(--fs-sm); }
  footer{ padding:6px var(--pad); border-top:1px solid var(--border); font-size:var(--fs-sm); color:var(--muted); }
  dialog{ border:1px solid var(--border); background:var(--bg); color:var(--fg); border-radius:10px; padding:0; width:min(560px, 90vw); }
  dialog header{ padding:8px var(--pad); border-bottom:1px solid var(--border); font-size:var(--fs); }
  dialog main{ padding:var(--pad); }
  textarea, input[type="text"].full{ width:100%; background:var(--card); color:var(--fg); border:1px solid var(--border); border-radius:8px; padding:8px; font-size:var(--fs); }
</style>
</head>
<body>
  <header class="toolbar">
    <div class="actions">
      <button id="add-snippet" class="btn primary">＋ 新增片段</button>
      <button id="add-tab" class="btn">➕ 新分頁</button>
      <button id="rename-tab" class="btn">✏️ 重新命名</button>
      <button id="delete-tab" class="btn danger">🗑 刪除分頁</button>
    </div>
    <div class="search"><input id="q" type="text" placeholder="搜尋標題/內容（即時）"/></div>
  </header>

  <div class="tabs" id="tabs"></div>
  <div class="grid" id="grid"></div>
  <footer>提示：按「複製」只放到剪貼簿。</footer>

  <!-- 新增/編輯片段 -->
  <dialog id="dlg">
    <header><strong id="dlg-title">新增片段</strong></header>
    <main>
      <div class="row" style="margin-bottom:6px">
        <button id="from-selection" class="btn">從選取文字</button>
        <button id="from-clipboard" class="btn">從系統剪貼簿</button>
      </div>
      <input id="f-title" class="full" type="text" placeholder="標題（可空白）" />
      <div style="height:6px"></div>
      <textarea id="f-text" rows="8" placeholder="貼上或輸入內容..."></textarea>
      <div style="height:6px"></div>
      <div class="row" style="justify-content:flex-end">
        <button id="dlg-cancel" class="btn">取消</button>
        <button id="dlg-ok" class="btn primary">確定</button>
      </div>
    </main>
  </dialog>

  <!-- 輸入（新分頁 / 重新命名） -->
  <dialog id="input-dlg">
    <header><strong id="input-title">輸入</strong></header>
    <main>
      <input id="input-text" class="full" type="text" />
      <div style="height:6px"></div>
      <div class="row" style="justify-content:flex-end">
        <button id="input-cancel" class="btn">取消</button>
        <button id="input-ok" class="btn primary">確定</button>
      </div>
    </main>
  </dialog>

  <!-- 自製 confirm -->
  <dialog id="confirm-dlg">
    <header><strong>確認</strong></header>
    <main>
      <p id="confirm-msg" style="margin:0 0 8px"></p>
      <div class="row" style="justify-content:flex-end">
        <button id="confirm-cancel" class="btn">取消</button>
        <button id="confirm-ok" class="btn primary">確定</button>
      </div>
    </main>
  </dialog>

  <script nonce="${nonce}">
    const vscode = acquireVsCodeApi();

    // ------- State（由外層 storage 提供；webview 只維持記憶體，持久化靠 postMessage） -------
    let state = null; // { active, tabs }

    // 要求初始化
    window.addEventListener('load', () => {
      vscode.postMessage({ type: 'req:init' });
    });

    // ------- Refs -------
    const tabsEl = document.getElementById('tabs');
    const gridEl = document.getElementById('grid');
    const qEl = document.getElementById('q');
    const btnAddSnippet = document.getElementById('add-snippet');
    const btnAddTab = document.getElementById('add-tab');
    const btnRenameTab = document.getElementById('rename-tab');
    const btnDeleteTab = document.getElementById('delete-tab');

    const dlg = document.getElementById('dlg');
    const dlgTitle = document.getElementById('dlg-title');
    const fTitle = document.getElementById('f-title');
    const fText = document.getElementById('f-text');
    const btnOk = document.getElementById('dlg-ok');
    const btnCancel = document.getElementById('dlg-cancel');
    const btnFromSel = document.getElementById('from-selection');
    const btnFromClip = document.getElementById('from-clipboard');

    const inputDlg = document.getElementById('input-dlg');
    const inputTitle = document.getElementById('input-title');
    const inputText = document.getElementById('input-text');
    const inputOk = document.getElementById('input-ok');
    const inputCancel = document.getElementById('input-cancel');

    const confirmDlg = document.getElementById('confirm-dlg');
    const confirmMsg = document.getElementById('confirm-msg');
    const confirmOk = document.getElementById('confirm-ok');
    const confirmCancel = document.getElementById('confirm-cancel');

    let editingItem = null; // {tabId, itemId} | null

    // ------- Helpers：save / confirm / prompt -------
    function persist(){ vscode.postMessage({ type: 'save', state }); }
    function notify(message, level){ vscode.postMessage({ type:'notify', message, level }); }

    function confirmWeb(message){
      return new Promise((resolve) => {
        confirmMsg.textContent = message; confirmDlg.showModal();
        const onOk = () => { cleanup(); resolve(true); };
        const onCancel = () => { cleanup(); resolve(false); };
        const onKey = (e)=>{ if(e.key==='Escape') onCancel(); if(e.key==='Enter') onOk(); };
        function cleanup(){ confirmOk.removeEventListener('click',onOk); confirmCancel.removeEventListener('click',onCancel); confirmDlg.removeEventListener('keydown',onKey); confirmDlg.close(); }
        confirmOk.addEventListener('click', onOk, {once:true});
        confirmCancel.addEventListener('click', onCancel, {once:true});
        confirmDlg.addEventListener('keydown', onKey);
      });
    }
    function promptWeb(title, def=''){
      return new Promise((resolve)=>{
        inputTitle.textContent = title; inputText.value = def; inputDlg.showModal();
        inputText.focus(); inputText.select();
        const onOk = () => { cleanup(); resolve(inputText.value.trim() || null); };
        const onCancel = () => { cleanup(); resolve(null); };
        const onKey = (e)=>{ if(e.key==='Escape') onCancel(); if(e.key==='Enter') onOk(); };
        function cleanup(){ inputOk.removeEventListener('click',onOk); inputCancel.removeEventListener('click',onCancel); inputDlg.removeEventListener('keydown',onKey); inputDlg.close(); }
        inputOk.addEventListener('click', onOk, {once:true});
        inputCancel.addEventListener('click', onCancel, {once:true});
        inputDlg.addEventListener('keydown', onKey);
      });
    }

    // ------- Render -------
    function render(){
      // tabs
      tabsEl.innerHTML = '';
      for (const t of state.tabs) {
        const el = document.createElement('button');
        el.className = 'tab' + (t.id === state.active ? ' active' : '');
        el.textContent = t.name;
        el.onclick = () => { state.active = t.id; persist(); render(); };
        tabsEl.appendChild(el);
      }

      // items
      const tab = activeTab();
      const q = (qEl.value || '').toLowerCase().trim();
      const items = tab.items.filter(it =>
        !q || (it.title || '').toLowerCase().includes(q) || (it.text || '').toLowerCase().includes(q)
      );

      gridEl.innerHTML = '';
      if (!items.length) {
        const empty = document.createElement('div'); empty.className = 'muted';
        empty.style.padding = '6px var(--pad)'; empty.textContent = q ? '找不到符合的片段' : '尚無片段，點「新增片段」開始吧！';
        gridEl.appendChild(empty); return;
      }
      for (const it of items) gridEl.appendChild(card(tab.id, it));
    }

    function card(tabId, it){
      const card = document.createElement('div'); card.className = 'card';
      const title = document.createElement('h4'); title.textContent = it.title || preview(it.text); card.appendChild(title);
      const pre = document.createElement('pre'); pre.textContent = it.text; card.appendChild(pre);
      const row = document.createElement('div'); row.className = 'row';
      const bCopy = button('複製', async () => {
        try { await navigator.clipboard.writeText(it.text); notify('已複製到剪貼簿'); }
        catch { vscode.postMessage({ type:'writeClipboard', text: it.text }); }
      });
      const bEdit = button('編輯', () => openEdit(tabId, it.id));
      const bDel  = button('刪除', async () => { const ok = await confirmWeb('確定刪除此片段？'); if(!ok) return; removeItem(tabId, it.id); });
      row.append(bCopy, bEdit, bDel); card.appendChild(row);
      const ts = document.createElement('div'); ts.className = 'muted'; ts.textContent = new Date(it.ts).toLocaleString(); card.appendChild(ts);
      return card;
    }

    function button(text, onClick){ const b=document.createElement('button'); b.className='btn'; b.textContent=text; b.onclick=onClick; return b; }
    function preview(s){ s=(s||'').replace(/\\s+/g,' ').trim(); return s.length>30? s.slice(0,30)+'…' : s || '(無標題)'; }
    function activeTab(){ let t = state.tabs.find(t=>t.id===state.active); if(!t){ state.active = state.tabs[0]?.id || 'default'; t = state.tabs[0] || {id:'default',name:'Default',items:[]}; } return t; }

    // ------- Actions: toolbar -------
    btnAddTab.onclick = async () => {
      const name = await promptWeb('新分頁名稱', 'New Tab'); if(!name) return;
      const id = uid(); state.tabs.push({ id, name, items:[] }); state.active = id; persist(); render();
    };
    btnRenameTab.onclick = async () => {
      const tab = activeTab(); const name = await promptWeb('分頁新名稱', tab.name); if(!name) return;
      tab.name = name; persist(); render();
    };
    btnDeleteTab.onclick = async () => {
      const tab = activeTab();
      const ok = await confirmWeb(\`確定刪除分頁「\${tab.name}」？（片段將一併刪除）\`);
      if(!ok) return;
      state.tabs = state.tabs.filter(t => t.id !== tab.id);
      if (!state.tabs.length) state = { active:'default', tabs:[{id:'default', name:'Default', items:[]}] };
      else if (!state.tabs.find(t => t.id === state.active)) state.active = state.tabs[0].id;
      persist(); render();
    };
    qEl.oninput = () => render();

    // ------- Actions: items -------
    btnAddSnippet.onclick = () => openCreate();

    function openCreate(){
      editingItem = null;
      dlgTitle.textContent = '新增片段';
      fTitle.value = ''; fText.value = '';
      dlg.showModal(); fTitle.focus();
    }
    function openEdit(tabId, itemId){
      editingItem = { tabId, itemId };
      const tab = state.tabs.find(t => t.id === tabId);
      const item = tab.items.find(i => i.id === itemId);
      dlgTitle.textContent = '編輯片段'; fTitle.value = item.title || ''; fText.value = item.text || '';
      dlg.showModal(); fTitle.focus(); fTitle.select();
    }

    btnFromSel.onclick  = () => vscode.postMessage({ type:'getSelection' });
    btnFromClip.onclick = () => vscode.postMessage({ type:'getClipboard' });
    btnCancel.onclick   = () => dlg.close();

    btnOk.onclick = () => {
      const title = fTitle.value.trim(); const text = fText.value;
      if(!text){ notify('內容不可為空', 'warn'); return; }
      if (editingItem) {
        const tab = state.tabs.find(t => t.id === editingItem.tabId);
        const item = tab.items.find(i => i.id === editingItem.itemId);
        item.title = title || undefined; item.text = text; item.ts = Date.now();
      } else {
        const tab = activeTab();
        tab.items.unshift({ id: uid(), title: title || undefined, text, ts: Date.now() });
      }
      persist(); render(); dlg.close();
    };

    function removeItem(tabId, itemId){
      const tab = state.tabs.find(t => t.id === tabId);
      tab.items = tab.items.filter(i => i.id !== itemId);
      persist(); render();
    }

    // ------- Messages from extension -------
    window.addEventListener('message', (e) => {
      const msg = e.data || {};
      if (msg.type === 'init') {
        state = msg.state;
        render();
      }
      if (msg.type === 'selection' && dlg.open) {
        if (fText.value) fText.value += '\\n';
        fText.value += msg.text || '';
      }
      if (msg.type === 'clipboard' && dlg.open) {
        if (fText.value) fText.value += '\\n';
        fText.value += msg.text || '';
      }
      // msg.type === 'saved' 可忽略或顯示 toast
    });

    function uid(){ return Math.random().toString(36).slice(2) + Date.now().toString(36); }

  </script>
</body>
</html>
    `;
  }
})();

