          background:var(--vscode-button-secondaryBackground,transparent);
          color:var(--vscode-button-secondaryForeground,var(--vscode-editor-foreground)); }
 #log { margin-top:10px; font-family: ui-monospace, SFMono-Regular, Menlo, monospace; }
 #log div { margin:4px 0; opacity:.9; }
</style></head>
<body>
  <h3>${who}</h3>
  <div class="row">
    <input id="msg" placeholder="type something…"/>
    <button id="send">Send → Other</button>
    <button id="closeBoth">Close Both</button>
  </div>
  <div id="log"></div>
<script>
  const vscode = acquireVsCodeApi();
  const log = (t) => { const d=document.createElement('div'); d.textContent=t; document.getElementById('log').prepend(d); };

  document.getElementById('send').onclick = () => {
    const v = document.getElementById('msg').value || '(empty)';
    vscode.postMessage({ type:'relay', from:'${who}', text:v });
    document.getElementById('msg').value = '';
  };
  document.getElementById('closeBoth').onclick = () => {
    vscode.postMessage({ type:'closeAll', from:'${who}' });
  };

  window.addEventListener('message', (e) => {
    const { type, text } = e.data || {};
    if (type === 'note') log(text);
  });
</script></body></html>`;

// 1) 開 Sidebar
await sidebar.open({ 
  html: html('Sidebar'),
  onClose: () => vm.stop()
});

// 2) 開 Tab（WebviewPanel）
const panel = vscode.window.createWebviewPanel(
  'sbh.dualDemo',
  'SBH Demo (Tab)',
  vscode.ViewColumn.Active,
  { enableScripts: true, retainContextWhenHidden: true }
);
panel.webview.html = html('Tab');

// 傳訊工具
const sendToSidebar = (m) => sidebar.postMessage(m);
const sendToTab = (m) => panel.webview.postMessage(m);

// 3) VM 當中繼：兩邊 relay
const offSidebarMsg = sidebar.onMessage((m) => {
  if (!m || typeof m !== 'object') return;
  if (m.type === 'relay') sendToTab({ type:'note', text:`Sidebar → ${m.text}` });
  if (m.type === 'closeAll') closeBoth();
});
const dTabMsg = panel.webview.onDidReceiveMessage((m) => {
  if (!m || typeof m !== 'object') return;
  if (m.type === 'relay') sendToSidebar({ type:'note', text:`Tab → ${m.text}` });
  if (m.type === 'closeAll') closeBoth();
});

// 4) 關閉邏輯：任一邊關都一起關（避免重入）
let closing = false;
function closeBoth() {
  if (closing) return;
  closing = true;
  try { panel.dispose(); } catch {}
  try { sidebar.close(); } catch {}
  try { vm.stop(); } catch {}
}
// 使用者手動關其中之一時也要關另一邊
const offSidebarClose = sidebar.onClose(() => { if (!closing) { closing = true; try { 
  vm.stop();
} catch {} } });
const dTabClose = panel.onDidDispose(() => { if (!closing) { closing = true; try { 
  vm.stop();
} catch {} } });

// 5) 簡單歡迎訊息
sendToSidebar({ type:'note', text:'Hello from VM → Sidebar' });
sendToTab({ type:'note', text:'Hello from VM → Tab' });

// 6) 腳本停止時清理
vm?.onStop?.(() => {
  try { offSidebarMsg?.dispose?.(); } catch {}
  try { offSidebarClose?.dispose?.(); } catch {}
  try { dTabMsg?.dispose?.(); } catch {}
  try { dTabClose?.dispose?.(); } catch {}
  try { panel.dispose(); } catch {}
  try { sidebar.colse(); } catch {}
});

// Sidebar — 天氣（未來 24 小時；列表版；含降雨率進度條・繁中）
const vscode = require('vscode');
const { vm, sidebar } = statusBarHelper.v1;

(async function main () {
  const nonce = Math.random().toString(36).slice(2);
  await sidebar.open({ 
    html: getHtml(nonce),
    onClose: () => vm.stop()
  });
  vm.onStop(() => { try { sidebar.close(); } catch {} });
})();

function getHtml (nonce) {
  const cities = [
    { name: '台北', lat: 25.0375, lon: 121.5637, tz: 'Asia/Taipei' },
    { name: '新竹', lat: 24.8066, lon: 120.9686, tz: 'Asia/Taipei' },
    { name: '台中', lat: 24.1477, lon: 120.6736, tz: 'Asia/Taipei' },
    { name: '台南', lat: 22.9997, lon: 120.2270, tz: 'Asia/Taipei' },
    { name: '高雄', lat: 22.6273, lon: 120.3014, tz: 'Asia/Taipei' },
    { name: '花蓮', lat: 23.9872, lon: 121.6015, tz: 'Asia/Taipei' },
    { name: '台東', lat: 22.7972, lon: 121.0717, tz: 'Asia/Taipei' },
    { name: '東京', lat: 35.6762, lon: 139.6503, tz: 'Asia/Tokyo' },
    { name: '首爾', lat: 37.5665, lon: 126.9780, tz: 'Asia/Seoul' },
    { name: '紐約', lat: 40.7128, lon: -74.0060, tz: 'America/New_York' },
  ];
  const options = cities.map((c,i)=>`<option value="${i}">${c.name}</option>`).join('');

  return `<!doctype html>
<html lang="zh-Hant">
<head>
  <meta charset="UTF-8"/>
  <meta http-equiv="Content-Security-Policy"
        content="default-src 'none'; img-src https: data:; style-src 'unsafe-inline'; script-src 'nonce-${nonce}'; connect-src https:;">
  <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
  <title>天氣 — 未來 24 小時</title>
  <style>
    :root{
      --fg:#e7e7e7; --muted:#a7a7a7; --bg:#1e1e1e; --card:#232323; --line:#2f2f2f;
      --pill:#1a1a1a; --green:#2ed39a;
    }
    *{box-sizing:border-box}
    body{margin:0;font-family:ui-sans-serif,system-ui,-apple-system,"Segoe UI",Roboto,"Noto Sans TC",Arial;background:var(--bg);color:var(--fg)}
    .wrap{padding:16px;max-width:860px;margin:0 auto}
    .row{display:flex;align-items:center;gap:10px;flex-wrap:wrap}
    h1{font-size:28px;margin:0 8px 0 0;line-height:1.2}
    .muted{color:var(--muted)}
    select,button{background:var(--pill);color:var(--fg);border:1px solid var(--line);border-radius:12px;padding:8px 12px;cursor:pointer}

    .card{background:var(--card);border:1px solid var(--line);border-radius:18px;padding:16px;margin-top:16px;box-shadow:0 10px 26px rgba(0,0,0,.22)}
    .big{font-size:44px;font-weight:800;letter-spacing:1px;line-height:1}
    .wx{font-size:20px;font-weight:700;margin-left:12px}
    .when{margin-top:8px;color:var(--muted)}
    .windRow{margin-top:6px;color:var(--muted);display:flex;align-items:center;gap:6px;font-size:14px}

    .kpis{display:flex;gap:12px;flex-wrap:wrap;margin-top:12px}
    .kpi{background:var(--pill);border:1px solid var(--line);border-radius:14px;padding:10px 14px;display:flex;align-items:center;gap:10px;font-weight:700}

    .legend{display:flex;align-items:center;gap:12px;margin-bottom:8px}
    .lg{background:var(--pill);border:1px solid var(--line);border-radius:999px;padding:8px 12px;display:flex;align-items:center;gap:8px}
    .dot{width:10px;height:10px;border-radius:50%}
    .blue{background:#5da5ff}.green{background:var(--green)}

    /* 列表 + 降雨率進度條（恢復） */
    .list{margin-top:10px}
    .item{padding:14px 6px;border-top:1px solid var(--line);display:grid;grid-template-columns:70px 90px 1fr 74px;gap:14px;align-items:center}
    .time{color:var(--muted);width:70px}
    .tval{font-weight:800;font-size:18px;min-width:90px}

    .bar{position:relative;height:8px;border-radius:999px;background:#181818;border:1px solid #2a2a2a;overflow:hidden}
    .fill{position:absolute;top:-1px;left:-1px;bottom:-1px;width:0;border-radius:999px;background:var(--green)}
    .emoji{text-align:right;opacity:.95}
    .pct{font-weight:700;text-align:right}

    @media (max-width:560px){
      .big{font-size:38px}
      .wx{font-size:18px}
      .item{grid-template-columns:62px 82px 1fr 60px}
      .tval{font-size:17px}
    }
  </style>
</head>
<body>
  <div class="wrap">
    <div class="row">
      <h1 style="font-size:16px;">🌤️ 天氣 — 未來 24 小時</h1>
      <div style="flex:1"></div>
      <button id="btnRefresh">重新整理</button>
      <select id="city">${options}</select>
    </div>

    <div class="card" id="cardNow">
      <div class="row" style="align-items:flex-end;">
        <div class="big" id="nowTemp">--°</div>
        <div class="wx" id="nowWx">—</div>
      </div>
      <div class="windRow"><span>💨</span><span id="nowWind">-- m/s</span></div>
      <div class="when" id="updateAt">—</div>
      <div class="kpis">
        <div class="kpi">🔺 <span id="dayMax">--°</span></div>
        <div class="kpi">🔻 <span id="dayMin">--°</span></div>
      </div>
    </div>

    <div class="card">
      <div class="legend">
        <div class="lg"><span class="dot blue"></span><b>溫度</b></div>
        <div class="lg"><span class="dot green"></span><b>降雨%</b></div>
      </div>
      <div class="list" id="list"></div>
    </div>

    <p class="muted" style="margin-top:14px;">
      資料來源：<a href="https://open-meteo.com/" target="_blank" rel="noreferrer" style="color:#79a9ff;text-decoration:none;">Open-Meteo</a>（免金鑰）
    </p>
  </div>

  <script nonce="${nonce}">
    const CITIES = ${JSON.stringify(cities)};
    const vscodeApi = acquireVsCodeApi?.() || null;

    const $ = (id)=>document.getElementById(id);
    const citySel=$('city'), btnRefresh=$('btnRefresh');
    const nowTemp=$('nowTemp'), nowWx=$('nowWx'), nowWind=$('nowWind'), updAt=$('updateAt');
    const dayMax=$('dayMax'), dayMin=$('dayMin'), list=$('list');

    const WMO={0:'晴朗',1:'多雲',2:'多雲',3:'陰',45:'霧',48:'霧霜',51:'毛毛雨',53:'毛毛雨',55:'毛毛雨',56:'凍霧雨',57:'凍霧雨',
               61:'小雨',63:'中雨',65:'大雨',66:'凍雨',67:'凍雨',71:'小雪',73:'中雪',75:'大雪',77:'霰',
               80:'短暫小雨',81:'短暫中雨',82:'短暫大雨',85:'短暫小雪',86:'短暫大雪',95:'雷雨',96:'雷雨夾雹',99:'強雷雨夾雹'};
    const ICON=(code)=>{ if([0].includes(code))return'☀️'; if([1,2].includes(code))return'⛅';
      if([3].includes(code))return'☁️'; if([45,48].includes(code))return'🌫️';
      if([51,53,55,56,57,61,63,65,66,67,80,81,82].includes(code))return'🌧️';
      if([71,73,75,77,85,86].includes(code))return'❄️'; if([95,96,99].includes(code))return'⛈️'; return'🌡️'; };

    const LS_KEY='sbh_w_sidebar_pref';
    const loadPref=()=>{ try{return JSON.parse(localStorage.getItem(LS_KEY)||'{}');}catch{return{}}};
    const savePref=(o)=>localStorage.setItem(LS_KEY, JSON.stringify(o));
    const pref=loadPref(); citySel.value=String(pref.cityIndex??0);

    async function fetchWeather(lat,lon,tz){
      const url=new URL('https://api.open-meteo.com/v1/forecast');
      url.searchParams.set('latitude',lat); url.searchParams.set('longitude',lon);
      url.searchParams.set('timezone',tz||'auto');
      url.searchParams.set('current_weather','true');
      url.searchParams.set('hourly','temperature_2m,precipitation_probability,weathercode');
      url.searchParams.set('forecast_days','2'); // 兩天，確保足夠的未來時段
      const res=await fetch(url.toString(),{cache:'no-store'}); if(!res.ok) throw new Error('Network '+res.status);
      return res.json();
    }
    const hh=(d)=>String(new Date(d).getHours()).padStart(2,'0')+':00';
    const fmt=(d)=>new Date(d).toLocaleString();

    async function refresh(){
      const idx=Number(citySel.value)||0; const c=CITIES[idx]; savePref({cityIndex:idx});
      const data=await fetchWeather(c.lat,c.lon,c.tz);

      // 現在
      const cw=data.current_weather||{};
      nowTemp.textContent=(cw.temperature!=null?cw.temperature.toFixed(1):'--')+'°';
      nowWx.textContent=(ICON(cw.weathercode)+' '+(WMO[cw.weathercode]||'—'));
      nowWind.textContent=(cw.windspeed!=null?cw.windspeed:'--')+' m/s';
      updAt.textContent=fmt(new Date());
      const d=data.daily||{};
      if(d.temperature_2m_max){ dayMax.textContent=d.temperature_2m_max[0].toFixed(1)+'°'; dayMin.textContent=d.temperature_2m_min[0].toFixed(1)+'°'; }

      // 未來 24 小時
      const h=data.hourly||{}; const times=h.time||[];
      let start=times.indexOf(cw.time);
      if(start<0){
        const now=new Date(); const y=now.getFullYear(),m=now.getMonth(),da=now.getDate(),hr=now.getHours();
        const isoHour=new Date(y,m,da,hr,0,0).toISOString().slice(0,13);
        start=times.findIndex(t=>t.startsWith(isoHour));
        if(start<0) start=0;
      }

      const items=[];
      for(let i=0;i<24 && start+i<times.length;i++){
        const k=start+i;
        items.push({
          hh: hh(times[k]),
          t:  h.temperature_2m?.[k],
          p:  Math.max(0, Math.min(100, h.precipitation_probability?.[k] ?? 0)),
          ic: ICON(h.weathercode?.[k])
        });
      }

      // 生成：時間 / 溫度 / 降雨率進度條 / 右側圖示+百分比
      list.innerHTML = items.map(it =>
        '<div class="item" role="listitem">' +
          '<div class="time">'+ it.hh +'</div>' +
          '<div class="tval">'+ (it.t!=null?it.t.toFixed(1):'--') +'°</div>' +
          '<div class="bar" aria-label="降雨機率 '+ it.p +'%"><div class="fill" style="width:'+ it.p +'%"></div></div>' +
          '<div class="emoji">'+ it.ic +' <span class="pct">'+ it.p +'%</span></div>' +
        '</div>'
      ).join('');
    }

    citySel.addEventListener('change',refresh);
    btnRefresh.addEventListener('click',refresh);

    refresh().catch(err=>{ updAt.textContent='載入失敗：'+String(err).slice(0,80); });
  </script>
</body>
</html>`;
}
