    <script nonce="${nonce}">
      // --- å·¥å…· & å¸¸æ•¸ ---
      const CITIES = ${JSON.stringify(cities)};
      const $ = (id) => document.getElementById(id);
      const fmtTime = (d) => new Date(d).toLocaleString();
      const WMO = {
        // ç°¡æ˜“å°ç…§ (WMO weathercode)
        0:'æ™´æœ—',1:'å¤šé›²',2:'å¤šé›²',3:'é™°',45:'éœ§',48:'éœ§éœœ',
        51:'æ¯›æ¯›é›¨',53:'æ¯›æ¯›é›¨',55:'æ¯›æ¯›é›¨',56:'å‡éœ§é›¨',57:'å‡éœ§é›¨',
