        95:'é›·é›¨',96:'é›·é›¨å¤¾é›¹',99:'å¼·é›·é›¨å¤¾é›¹'
      };
      const ICON = (code) => {
        if ([0].includes(code)) return 'â˜€ï¸';
        if ([1,2].includes(code)) return 'â›…';
        if ([3].includes(code)) return 'â˜ï¸';
        if ([45,48].includes(code)) return 'ğŸŒ«ï¸';
        if ([51,53,55,56,57,61,63,65,66,67,80,81,82].includes(code)) return 'ğŸŒ§ï¸';
        if ([71,73,75,77,85,86].includes(code)) return 'â„ï¸';
        if ([95,96,99].includes(code)) return 'â›ˆï¸';
        return 'ğŸŒ¡ï¸';
      };

      // å„²å­˜åå¥½
      const LS_KEY = 'sbh_weather_pref';
      function savePref(obj){ localStorage.setItem(LS_KEY, JSON.stringify(obj)); }
      function loadPref(){ try { return JSON.parse(localStorage.getItem(LS_KEY)||'{}'); } catch { return {}; } }

      // ç•«ç°¡æ˜“æŠ˜ç·š (ä¸ä¾è³´å¤–éƒ¨å¥—ä»¶)
      function drawLine(canvas, values, labels){
        const ctx = canvas.getContext('2d');
        const W = canvas.width, H = canvas.height;
        ctx.clearRect(0,0,W,H);
        if (!values || !values.length) return;
        const min = Math.min(...values), max = Math.max(...values);
        const padL=40, padR=10, padT=20, padB=24;
        const X = (i)=> padL + (W - padL - padR) * (i/(values.length-1));
        const Y = (v)=> padT + (H - padT - padB) * (1 - (v - min) / Math.max(0.0001, (max - min)));
        // è»¸ç·š
        ctx.globalAlpha=0.5; ctx.strokeStyle = '#444'; ctx.beginPath();
        ctx.moveTo(padL, padT); ctx.lineTo(padL, H - padB); ctx.lineTo(W - padR, H - padB); ctx.stroke();
        ctx.globalAlpha=1;
        // y æ¨™ç±¤
        ctx.fillStyle = '#a7a7a7'; ctx.font='12px system-ui';
        [min, (min+max)/2, max].forEach((v, idx) => {
          const y = Y(v);
          ctx.fillText(v.toFixed(1)+'Â°', 6, y+4);
          ctx.globalAlpha=0.15; ctx.strokeStyle='#888'; ctx.beginPath();
          ctx.moveTo(padL, y); ctx.lineTo(W-padR, y); ctx.stroke(); ctx.globalAlpha=1;
        });
        // æŠ˜ç·š
        ctx.beginPath(); ctx.lineWidth=2; ctx.strokeStyle='#5da5ff';
        values.forEach((v,i)=>{ const x=X(i), y=Y(v); if(i===0) ctx.moveTo(x,y); else ctx.lineTo(x,y); });
        ctx.stroke();
        // ç¯€é»
        ctx.fillStyle='#5da5ff';
        values.forEach((v,i)=>{ const x=X(i), y=Y(v); ctx.beginPath(); ctx.arc(x,y,2.5,0,Math.PI*2); ctx.fill(); });
        // x æ¨™ç±¤ï¼ˆæ¯ 3 å°æ™‚ï¼‰
        ctx.fillStyle='#a7a7a7'; ctx.textAlign='center';
        labels.forEach((t,i)=>{ if(i%3===0){ const x=X(i); ctx.fillText(t, x, H-6); } });
        ctx.textAlign='left';
      }

      // API è«‹æ±‚
      async function fetchWeather(lat, lon, tz){
        const url = new URL('https://api.open-meteo.com/v1/forecast');
        url.searchParams.set('latitude', lat);
        url.searchParams.set('longitude', lon);
        url.searchParams.set('timezone', tz || 'auto');
        url.searchParams.set('current_weather', 'true');
        url.searchParams.set('hourly', 'temperature_2m');
        url.searchParams.set('daily', 'weathercode,temperature_2m_max,temperature_2m_min,precipitation_sum');
        url.searchParams.set('forecast_days', '5');
        const res = await fetch(url.toString(), { cache:'no-store' });
        if (!res.ok) throw new Error('Network error: ' + res.status);
        return res.json();
      }

      // UI ç¶å®š
      const citySel = $('city');
      const useCustomBtn = $('useCustom');
      const latIn = $('lat'), lonIn = $('lon'), saveCustomBtn = $('saveCustom');
      const refreshBtn = $('refresh');
      const nowTemp = $('nowTemp'), nowText = $('nowText');
      const nowWind = $('nowWind'), dayRain = $('dayRain'), dayMax = $('dayMax'), dayMin = $('dayMin'), updAt = $('updateAt');
      const hourlyChart = $('hourlyChart');
      const daysWrap = $('days');

      // åˆå§‹åŒ–ï¼šè¼‰å…¥åå¥½
      const pref = loadPref();
      if (pref.mode === 'custom' && typeof pref.lat === 'number' && typeof pref.lon === 'number') {
        citySel.value = String(Math.max(0, Math.min(CITIES.length-1, pref.cityIndex||0))); // UI ä»æŒ‡å‘ä¸€å€‹åŸå¸‚ï¼Œä½†å¯¦éš›ç”¨è‡ªè¨‚
        latIn.value = String(pref.lat);
        lonIn.value = String(pref.lon);
      } else {
        citySel.value = String(pref.cityIndex ?? 0);
      }

      let timer = null;
      async function refresh(){
        try {
          const mode = (pref.mode || 'city');
          const idx = Number(citySel.value) || 0;
          const city = CITIES[idx];
          let lat = city.lat, lon = city.lon, tz = city.tz;
          if (mode === 'custom') {
            const cla = parseFloat(latIn.value), clo = parseFloat(lonIn.value);
            if (!Number.isFinite(cla) || !Number.isFinite(clo)) throw new Error('è‡ªè¨‚åº§æ¨™æ ¼å¼éŒ¯èª¤');
            lat = cla; lon = clo;
          }
          const data = await fetchWeather(lat, lon, tz);
          render(data);
        } catch (err) {
          console.error(err);
          vscode?.postMessage?.({ type:'error', message: String(err) });
          updAt.textContent = 'æ›´æ–°å¤±æ•—ï¼š' + String(err).slice(0, 80);
        }
      }

      function render(data){
        // ç›®å‰
        const cw = data.current_weather || {};
        nowTemp.textContent = (cw.temperature != null ? cw.temperature.toFixed(1) : '--') + 'Â°';
        nowText.textContent = (ICON(cw.weathercode) + ' ' + (WMO[cw.weathercode] || 'â€”'));
        nowWind.textContent = (cw.windspeed != null ? cw.windspeed : '--') + ' m/s';
        updAt.textContent = 'æ›´æ–°æ–¼ ' + fmtTime(new Date());

        // ä»Šæ—¥æ¯æ—¥è³‡è¨Š
        const d = data.daily || {};
        if (d.temperature_2m_max && d.temperature_2m_max.length) {
          dayMax.textContent = d.temperature_2m_max[0].toFixed(1) + 'Â°';
          dayMin.textContent = d.temperature_2m_min[0].toFixed(1) + 'Â°';
          dayRain.textContent = (d.precipitation_sum?.[0] ?? 0).toFixed(1) + ' mm';
        }

        // 24 å°æ™‚
        const h = data.hourly || {};
        const nowISO = data.current_weather?.time || new Date().toISOString();
        const startIdx = Math.max(0, (h.time||[]).indexOf(nowISO));
        const range = 24;
        const temps = (h.temperature_2m||[]).slice(startIdx, startIdx+range);
        const labels = (h.time||[]).slice(startIdx, startIdx+range).map(t => {
          const dt = new Date(t); return dt.getHours().toString().padStart(2,'0') + ':00';
        });
        drawLine(hourlyChart, temps, labels);

        // æœªä¾† 5 æ—¥
        const days = [];
        for (let i=0; i<Math.min(5, (d.time||[]).length); i++){
          const code = d.weathercode?.[i];
          days.push({
            date: d.time[i],
            icon: ICON(code),
            text: WMO[code] || 'â€”',
            max: d.temperature_2m_max?.[i],
            min: d.temperature_2m_min?.[i],
            rain: d.precipitation_sum?.[i] ?? 0
          });
        }
        daysWrap.innerHTML = days.map(x => {
          const dd = new Date(x.date);
          const md = (dd.getMonth()+1) + '/' + dd.getDate();
          return \`
            <div class="day">
              <div class="muted">\${md}</div>
              <div style="font-size:24px">\${x.icon}</div>
              <div style="margin:2px 0;">\${x.text}</div>
              <div>ğŸ”º \${x.max?.toFixed?.(1)??'--'}Â°</div>
              <div>ğŸ”» \${x.min?.toFixed?.(1)??'--'}Â°</div>
              <div>ğŸŒ§ï¸ \${x.rain?.toFixed?.(1)??'--'} mm</div>
            </div>\`;
        }).join('');
      }

      // äº‹ä»¶
      citySel.addEventListener('change', () => {
        const idx = Number(citySel.value)||0;
        const city = CITIES[idx];
        // åˆ‡æ›åŸå¸‚ â†’ è‡ªå‹•æ”¹ç‚º city æ¨¡å¼
        const p = loadPref();
        p.cityIndex = idx;
        p.mode = 'city';
        savePref(p);
        latIn.style.display='none'; lonIn.style.display='none'; saveCustomBtn.style.display='none';
        refresh();
      });

      useCustomBtn.addEventListener('click', () => {
        // æ‰“é–‹è‡ªè¨‚åº§æ¨™è¼¸å…¥
        latIn.style.display='inline-block';
        lonIn.style.display='inline-block';
        saveCustomBtn.style.display='inline-block';
        if (!latIn.value || !lonIn.value) {
          // é å¡«ç›®å‰é¸ä¸­åŸå¸‚åº§æ¨™
          const c = CITIES[Number(citySel.value)||0];
          latIn.value = c.lat; lonIn.value = c.lon;
        }
      });

      saveCustomBtn.addEventListener('click', () => {
        const la = parseFloat(latIn.value), lo = parseFloat(lonIn.value);
        if (!Number.isFinite(la) || !Number.isFinite(lo)) {
          alert('è«‹è¼¸å…¥æ­£ç¢ºçš„ç¶“ç·¯åº¦');
          return;
        }
        const p = loadPref();
        p.mode = 'custom';
        p.lat = la; p.lon = lo;
        savePref(p);
        refresh();
      });

      refreshBtn.addEventListener('click', refresh);

      // å®šæ™‚æ›´æ–°ï¼ˆæ¯ 20 åˆ†é˜ï¼‰
      clearInterval(timer);
      timer = setInterval(refresh, 20 * 60 * 1000);

      // é¦–æ¬¡è¼‰å…¥
      refresh();
    </script>
  </body>
  </html>`;
}

// Snake â€” Webview Game (English UI, for status-bar-helper)
// Features: Walls Kill/Wrap toggle (persistent), Pause/Resume, Restart, Speed, High Score
// Canvas 400x400, grid 25x25 (smaller cells)

const vscode = require('vscode');
const { vm } = statusBarHelper.v1;

(function main(){
  const panel = vscode.window.createWebviewPanel(
    'sbhSnake',
    'ğŸ Snake â€” Game',
    vscode.ViewColumn.Active,
    { enableScripts: true, retainContextWhenHidden: true }
  );

  vm.onStop(() => { try { panel.dispose(); } catch {} });
  panel.onDidDispose(() => vm.stop());

  const nonce = Math.random().toString(36).slice(2);
  panel.webview.html = getHtml(nonce);
})();

function getHtml(nonce){
  return /* html */`
  <!doctype html>
  <html lang="en">
  <head>
    <meta charset="utf-8">
    <meta http-equiv="Content-Security-Policy"
      content="default-src 'none'; img-src data:; style-src 'unsafe-inline'; script-src 'nonce-${nonce}';">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Snake</title>
    <style>
      :root {
        --bg:#0f1115; --card:#161a22; --line:#222838; --fg:#e6e6e6; --muted:#9aa2b1;
        --accent:#7bd88f; --danger:#ff6b6b; --btn:#1d2330; --btnBorder:#2a3247;
      }
      html,body{height:100%}
      body{margin:0;background:var(--bg);color:var(--fg);font-family:ui-sans-serif,system-ui,-apple-system,"Segoe UI",Roboto,Arial}
      .wrap{max-width:760px;margin:0 auto;padding:16px}
      .top{display:flex;align-items:center;gap:10px;flex-wrap:wrap}
      .sp{flex:1}
      .card{background:var(--card);border:1px solid var(--line);border-radius:14px;padding:12px;margin-top:12px;box-shadow:0 10px 24px rgba(0,0,0,.25)}
      .kpis{display:flex;gap:12px;flex-wrap:wrap}
      .kpi{background:#121620;border:1px solid var(--line);border-radius:12px;padding:10px 12px;min-width:100px}
      .btn{background:var(--btn);border:1px solid var(--btnBorder);border-radius:10px;padding:8px 12px;cursor:pointer;color:var(--fg)}
      .btn:hover{filter:brightness(1.1)}
      .btn-danger{background:#2a1212;border-color:#502222;color:#ffbbbb}
      .row{display:flex;align-items:center;gap:10px;flex-wrap:wrap}
      .muted{color:var(--muted)}
      canvas{width:100%;height:auto;display:block;background:#000;border-radius:12px}
      input[type=range]{accent-color:var(--accent)}
      .legend{font-size:12px;color:var(--muted)}
      .title{font-size:18px;font-weight:700}
      .frame{padding:10px;border-radius:14px;border:1px solid #2b3144;background:#0e1219}
    </style>
  </head>
  <body>
    <div class="wrap">
      <div class="top">
        <div class="title">ğŸ Snake â€” Game</div>
        <div class="sp"></div>
        <button id="btnWalls" class="btn">ğŸ§± Walls: Kill</button>
        <button id="btnPause" class="btn">â¸ï¸ Pause</button>
        <button id="btnRestart" class="btn btn-danger">ğŸ”„ Restart</button>
      </div>

      <div class="card">
        <div class="kpis">
          <div class="kpi">Score: <b id="score">0</b></div>
          <div class="kpi">High Score: <b id="best">0</b></div>
          <div class="kpi">Speed: <b id="speedLabel">120ms</b></div>
          <input id="speed" type="range" min="60" max="220" step="10" value="120">
        </div>
      </div>

      <div class="card frame">
        <canvas id="game" width="600" height="500"></canvas>
        <div class="legend" style="margin-top:8px">
          Controls: Arrow keys / WASD Â· P = Pause/Resume Â· R = Restart Â· Walls: Kill/Wrap (toggle above)
        </div>
      </div>
    </div>

    <script nonce="${nonce}">
      // ====== Config & Elements ======
      const $ = (id)=>document.getElementById(id);
      const cvs = $('game'), ctx = cvs.getContext('2d');
      const scoreEl=$('score'), bestEl=$('best');
      const btnWalls=$('btnWalls'), btnPause=$('btnPause'), btnRestart=$('btnRestart');
      const speedRange=$('speed'), speedLabel=$('speedLabel');

      const GRID = 30;                       // grid size (NxN)
      const CELL = Math.floor(cvs.width/GRID); // pixels per cell, derived from canvas size
      const BEST_LS_KEY = 'sbh_snake_best';
      const WALL_LS_KEY = 'sbh_snake_wall_mode'; // 'kill' | 'wrap'

      // ====== State ======
      let snake, dir, nextDir, food, score, best, timer, tickMs, paused, dead;
      let wallMode; // 'kill' or 'wrap'

      function loadWallMode(){ const v = localStorage.getItem(WALL_LS_KEY); return v==='wrap'?'wrap':'kill'; }
      function saveWallMode(v){ localStorage.setItem(WALL_LS_KEY, v); }
      function updateWallBtn(){ btnWalls.textContent = (wallMode==='kill') ? 'ğŸ§± Walls: Kill' : 'ğŸŒ€ Walls: Wrap'; }

      function init(){
        snake = [
          {x: Math.floor(GRID/2),   y: Math.floor(GRID/2)},
          {x: Math.floor(GRID/2)-1, y: Math.floor(GRID/2)},
          {x: Math.floor(GRID/2)-2, y: Math.floor(GRID/2)},
        ];
        dir='RIGHT'; nextDir='RIGHT';
        score=0; paused=false; dead=false;
        spawnFood();
        updateScore();
        tickMs = Number(speedRange.value);
        wallMode = loadWallMode();
        updateWallBtn();
        loopStart();
      }

      function loopStart(){ clearInterval(timer); timer=setInterval(tick, tickMs); }
      function loopPause(p=true){
        paused=p;
        if (paused){ clearInterval(timer); btnPause.textContent='â–¶ï¸ Resume'; }
        else { loopStart(); btnPause.textContent='â¸ï¸ Pause'; }
      }

      function updateScore(){
        scoreEl.textContent = String(score);
        best = Math.max(Number(localStorage.getItem(BEST_LS_KEY)||0), score);
        localStorage.setItem(BEST_LS_KEY, best);
        bestEl.textContent = String(best);
      }

      function spawnFood(){
        while(true){
          const fx = Math.floor(Math.random()*GRID);
          const fy = Math.floor(Math.random()*GRID);
          if (!snake.some(s=>s.x===fx && s.y===fy)){ food={x:fx,y:fy}; break; }
        }
      }

      // ====== Input ======
      const dirMap = {
        'ArrowLeft':'LEFT','ArrowUp':'UP','ArrowRight':'RIGHT','ArrowDown':'DOWN',
        'a':'LEFT','w':'UP','d':'RIGHT','s':'DOWN','A':'LEFT','W':'UP','D':'RIGHT','S':'DOWN'
      };
      window.addEventListener('keydown', e=>{
        if (e.key==='p'||e.key==='P'){ loopPause(!paused); return; }
        if (e.key==='r'||e.key==='R'){ restart(); return; }
        const nd = dirMap[e.key];
        if (!nd) return;
        if ((dir==='LEFT' && nd==='RIGHT')||(dir==='RIGHT'&&nd==='LEFT')||
            (dir==='UP'&&nd==='DOWN')||(dir==='DOWN'&&nd==='UP')) return;
        nextDir = nd;
      });
      window.addEventListener('blur', ()=>{ if(!dead) loopPause(true); });
      window.addEventListener('focus', ()=>{ if(!dead) loopPause(false); });

      // ====== Game Loop ======
      function tick(){
        if (paused || dead) return;

        dir = nextDir;
        const head = {...snake[0]};
        if (dir==='LEFT') head.x--;
        else if (dir==='RIGHT') head.x++;
        else if (dir==='UP') head.y--;
        else if (dir==='DOWN') head.y++;

        // Walls: Kill or Wrap
        if (wallMode === 'kill') {
          if (head.x<0 || head.y<0 || head.x>=GRID || head.y>=GRID){ return gameOver(); }
        } else {
          head.x = (head.x + GRID) % GRID;
          head.y = (head.y + GRID) % GRID;
        }

        // Self-collision
        if (snake.some(s=>s.x===head.x && s.y===head.y)){ return gameOver(); }

        // Eat
        const ate = (head.x===food.x && head.y===food.y);
        snake.unshift(head);
        if (ate){ score++; updateScore(); spawnFood(); } else { snake.pop(); }

        draw();
      }

      // ====== Drawing ======
      function draw(){
        ctx.fillStyle='#000'; ctx.fillRect(0,0,cvs.width,cvs.height);
        for (let y=0;y<GRID;y++){
          for (let x=0;x<GRID;x++){
            ctx.fillStyle = ((x+y)%2===0)?'#0a0a0a':'#0c0c0c';
            ctx.fillRect(x*CELL, y*CELL, CELL, CELL);
          }
        }
        // food
        ctx.fillStyle='#ff5252'; roundRect(xpx(food.x), ypx(food.y), CELL, CELL, 4);
        // snake
        for (let i=0;i<snake.length;i++){
          const s=snake[i];
          ctx.fillStyle = (i===0)?'#7bd88f':'#57c274';
          roundRect(xpx(s.x), ypx(s.y), CELL, CELL, i===0?6:4);
        }
      }

      function xpx(gx){ return gx*CELL; }
      function ypx(gy){ return gy*CELL; }
      function roundRect(x,y,w,h,r){
        ctx.beginPath();
        ctx.moveTo(x+r,y);
        ctx.arcTo(x+w,y,x+w,y+h,r);
        ctx.arcTo(x+w,y+h,x,y+h,r);
        ctx.arcTo(x,y+h,x,y,r);
        ctx.arcTo(x,y,x+w,y,r);
        ctx.closePath();
        ctx.fill();
      }

      function gameOver(){
        dead = true; clearInterval(timer);
        ctx.fillStyle='rgba(0,0,0,.55)'; ctx.fillRect(0,0,cvs.width,cvs.height);
        ctx.fillStyle='#fff'; ctx.textAlign='center';
        ctx.font='bold 28px system-ui'; ctx.fillText('Game Over', cvs.width/2, cvs.height/2 - 10);
        ctx.font='16px system-ui'; ctx.fillText('Press R to Restart', cvs.width/2, cvs.height/2 + 18);
        return true;
      }

      function restart(){ clearInterval(timer); init(); draw(); }

      // ====== UI Events ======
      btnPause.addEventListener('click', ()=> loopPause(!paused));
      btnRestart.addEventListener('click', restart);
      btnWalls.addEventListener('click', ()=>{
        wallMode = (wallMode==='kill') ? 'wrap' : 'kill';
        saveWallMode(wallMode);
        updateWallBtn();
      });

      speedRange.addEventListener('input', ()=>{
        tickMs = Number(speedRange.value);
        speedLabel.textContent = tickMs + 'ms';
        if (!paused && !dead) loopStart();
      });

      // init
      speedLabel.textContent = speedRange.value + 'ms';
      bestEl.textContent = String(localStorage.getItem(BEST_LS_KEY)||0);
      init();
      draw();
    </script>
  </body>
  </html>`;
}

