          </div>

          <div class="legend" style="grid-column:1/-1">æç¤ºï¼šé»æ£‹ç›¤ä¸‹å­ï¼›Undo æœƒå›é€€ã€Œä½ èˆ‡ AI çš„æœ€å¾Œå…©æ‰‹ã€ï¼Œç¢ºä¿å›åˆ°ä½ çš„å›åˆã€‚</div>
        </div>
      </div>

      <div class="card">
        <div class="board">
          <!-- 420Ã—420ï¼Œ15Ã—15 ç›¤ï¼Œæ ¼è·æœƒè‡ªç®— -->
          <canvas id="board" width="420" height="420" tabindex="0"></canvas>
        </div>
      </div>
    </div>

    <script nonce="${nonce}">
      const $ = id => document.getElementById(id);
      const cvs = $('board'), ctx = cvs.getContext('2d');
      const selSide=$('selSide'), levelEl=$('level'), lvlText=$('lvl'), lvlLabel=$('lvlLabel'), turnLabel=$('turnLabel');
      const btnNew=$('btnNew'), btnUndo=$('btnUndo');

      // ----- ç›¤é¢è¨­å®š -----
      const N = 15;               // 15Ã—15
      const PAD = 20;             // å…§è·
      const W = cvs.width, H = cvs.height;
      const GAP = Math.floor((W - PAD*2) / (N-1));  // æ ¼è·
      const R = Math.floor(GAP * 0.42);             // æ£‹å­åŠå¾‘
      const EMPTY=0, BLACK=1, WHITE=2;

      // ç‹€æ…‹
      let board, turn, human=BLACK, ai=WHITE, lastMove=null, over=false;
      const history=[]; // push {x,y,side}

      // é›£åº¦å°æ‡‰ï¼ˆå€™é¸æ•¸ã€æœå°‹æ·±åº¦ã€æ™‚é–“ä¸Šé™ï¼‰
      function cfgFor(level){
        switch(+level){
          case 1: return { topK:8, depth:0, limitMs:40 };            // éš¨æ©Ÿæ–¼å‰å¹¾å + å¨è„…è™•ç†
          case 2: return { topK:8, depth:0, limitMs:80 };            // è²ªå©ª
          case 3: return { topK:10, depth:1, limitMs:120 };          // å¼·è²ªå©ªï¼ˆå³æ™‚å µ/æ®ºï¼‰
          case 4: return { topK:10, depth:2, limitMs:180 };          // 2 å±¤
          case 5: return { topK:14, depth:2, limitMs:220 };          // 2 å±¤æ›´å¤šå€™é¸
          case 6: return { topK:10, depth:3, limitMs:350 };          // 3 å±¤é™æ™‚
          default: return { topK:10, depth:1, limitMs:120 };
        }
      }

      // ----- åˆå§‹åŒ– -----
      function newBoard(){
        board = Array.from({length:N}, ()=>Array(N).fill(EMPTY));
        history.length=0; lastMove=null; over=false;
      }
      function setSides(){
        if (selSide.value==='black'){ human=BLACK; ai=WHITE; }
        else { human=WHITE; ai=BLACK; }
        turn = BLACK; // é»‘å…ˆ
        updateLabels();
      }
      function updateLabels(){
        lvlText.textContent = String(levelEl.value);
        lvlLabel.textContent = String(levelEl.value);
        const t = (turn===human)?'ä½ ':'AI';
        const tside = (turn===BLACK)?'é»‘':'ç™½';
        turnLabel.textContent = \`\${t}ï¼ˆ\${tside}ï¼‰\`;
      }

      // ----- ç¹ªè£½ -----
      function drawBoard(){
        ctx.clearRect(0,0,W,H);
        // æ£‹ç›¤åº•
        for(let y=0;y<H;y+=20){
          for(let x=0;x<W;x+=20){
            ctx.fillStyle = ((x+y)%40===0)?'#0b0f18':'#0c111a';
            ctx.fillRect(x,y,20,20);
          }
        }
        // ç·š
        ctx.strokeStyle='#2b3144'; ctx.lineWidth=1.2;
        for(let i=0;i<N;i++){
          const x=PAD+i*GAP, y=PAD+i*GAP;
          ctx.beginPath(); ctx.moveTo(PAD, PAD+i*GAP); ctx.lineTo(W-PAD, PAD+i*GAP); ctx.stroke();
          ctx.beginPath(); ctx.moveTo(PAD+i*GAP, PAD); ctx.lineTo(PAD+i*GAP, H-PAD); ctx.stroke();
        }
        // æ˜Ÿä½ (3,3)(3,11)(7,7)(11,3)(11,11)
        const stars=[[3,3],[3,11],[7,7],[11,3],[11,11]];
        ctx.fillStyle='#9aa2b1';
        for(const [gx,gy] of stars){
          const {cx,cy}=cellXY(gx,gy);
          ctx.beginPath(); ctx.arc(cx,cy,3,0,Math.PI*2); ctx.fill();
        }
        // æ£‹å­
        for(let y=0;y<N;y++){
          for(let x=0;x<N;x++){
            const v=board[y][x];
            if (v!==EMPTY){
              const {cx,cy}=cellXY(x,y);
              const grad=ctx.createRadialGradient(cx,cy,2,cx,cy,R);
              if (v===BLACK){ grad.addColorStop(0,'#555'); grad.addColorStop(1,'#111'); }
              else { grad.addColorStop(0,'#fff'); grad.addColorStop(1,'#d9d9d9'); }
              ctx.fillStyle=grad; ctx.beginPath(); ctx.arc(cx,cy,R,0,Math.PI*2); ctx.fill();
            }
          }
        }
        // æœ€å¾Œä¸€æ­¥é«˜äº®
        if (lastMove){
          const {cx,cy}=cellXY(lastMove.x,lastMove.y);
          ctx.strokeStyle='#ffd34d'; ctx.lineWidth=2;
          ctx.beginPath(); ctx.arc(cx,cy,R+3,0,Math.PI*2); ctx.stroke();
        }
        // å‹è² æç¤º
        if (over){
          ctx.fillStyle='rgba(0,0,0,.55)'; ctx.fillRect(0,0,W,H);
          ctx.fillStyle='#fff'; ctx.font='bold 22px system-ui'; ctx.textAlign='center';
          ctx.fillText((winnerSide===human?'ä½ è´äº† ğŸ‰':'AI å‹å‡º ğŸ¤–'), W/2, H/2 - 12);
          ctx.font='14px system-ui'; ctx.fillText('æŒ‰ New Game é‡æ–°é–‹å§‹', W/2, H/2 + 16);
        }
      }

      function cellXY(x,y){ return { cx: PAD + x*GAP, cy: PAD + y*GAP }; }
      function posToCell(px,py){
        // å°‡æ»‘é¼ åº§æ¨™ snap åˆ°æœ€è¿‘æ ¼é»
        function snap(v){ return Math.round((v - PAD) / GAP); }
        const x=snap(px), y=snap(py);
        if (x<0||x>=N||y<0||y>=N) return null;
        const {cx,cy}=cellXY(x,y);
        const dist=Math.hypot(px-cx, py-cy);
        if (dist<=GAP*0.5) return {x,y};
        return null;
      }

      // ----- å‹è² åˆ¤å®š -----
      function countDir(x,y,dx,dy,side){
        let c=0, i=1;
        while(true){
          const nx=x+dx*i, ny=y+dy*i;
          if (nx<0||ny<0||nx>=N||ny>=N) break;
          if (board[ny][nx]!==side) break;
          c++; i++;
        }
        return c;
      }
      function isFive(x,y,side){
        const dirs=[[1,0],[0,1],[1,1],[1,-1]];
        for (const [dx,dy] of dirs){
          const c = 1 + countDir(x,y,dx,dy,side) + countDir(x,y,-dx,-dy,side);
          if (c>=5) return true;
        }
        return false;
      }
      let winnerSide=null;

      // ----- è©•åˆ†ï¼ˆå•Ÿç™¼å¼ï¼‰ -----
      // ä»¥ã€Œé€£å­æ•¸ + é›™é‚Šæ˜¯å¦é–‹æ”¾ã€è©•åˆ†ï¼›AI åˆ†æ•¸ - å°æ‰‹åˆ†æ•¸
      function evaluate(sideView=ai){
        let scoreAI=0, scoreHu=0;
        const dirs=[[1,0],[0,1],[1,1],[1,-1]];

        function lineScore(x,y,dx,dy,side){
          // åªåœ¨ã€Œæ­¤æ–¹å‘çš„èµ·é»ã€è¨ˆåˆ†ï¼šå‰ä¸€æ ¼ä¸æ˜¯åŒè‰²
          const px=x-dx, py=y-dy;
          if (px>=0&&py>=0&&px<N&&py<N && board[py][px]===side) return 0;

          let i=0, len=0;
          while(true){
            const nx=x+dx*i, ny=y+dy*i;
            if (nx<0||ny<0||nx>=N||ny>=N) break;
            if (board[ny][nx]!==side) break;
            len++; i++;
          }
          const leftOpen = (px>=0&&py>=0&&px<N&&py<N && board[py][px]===EMPTY);
          const rx=x+dx*len, ry=y+dy*len;
          const rightOpen = (rx>=0&&ry>=0&&rx<N&&ry<N && board[ry][rx]===EMPTY);
          const open = (leftOpen?1:0)+(rightOpen?1:0);

          // åŸºç¤æ¬Šé‡
          if (len>=5) return 1000000;
          if (len===4 && open===2) return 100000;
          if (len===4 && open===1) return 8000;
          if (len===3 && open===2) return 1500;
          if (len===3 && open===1) return 200;
          if (len===2 && open===2) return 80;
          if (len===2 && open===1) return 20;
          if (len===1 && open===2) return 6;
          return 0;
        }

        for(let y=0;y<N;y++){
          for(let x=0;x<N;x++){
            const v=board[y][x];
            if (v===EMPTY) continue;
            for(const [dx,dy] of dirs){
              const s = lineScore(x,y,dx,dy,v);
              if (v===ai) scoreAI += s; else scoreHu += s;
            }
          }
        }
        return scoreAI - scoreHu;
      }

      // å±€éƒ¨è©•åˆ†ï¼šæŠŠ (x,y) ç•¶ä½œ side ä¸‹ï¼Œå›å‚³æ­¤é»çš„å¨è„…å€¼
      function pointScore(x,y,side){
        if (board[y][x]!==EMPTY) return -Infinity;
        let score=0;
        const dirs=[[1,0],[0,1],[1,1],[1,-1]];
        for(const [dx,dy] of dirs){
          // å¾€å…©é‚Šå»¶ä¼¸ï¼Œè¨ˆç®—é€£å­èˆ‡ç©ºä½æƒ…æ³
          let a=0,b=0,blockA=false,blockB=false;
          for(let i=1;i<5;i++){
            const nx=x+dx*i, ny=y+dy*i;
            if (nx<0||ny<0||nx>=N||ny>=N){ blockA=true; break; }
            const v=board[ny][nx];
            if (v===side) a++; else { if (v!==EMPTY) blockA=true; break; }
          }
          for(let i=1;i<5;i++){
            const nx=x-dx*i, ny=y-dy*i;
            if (nx<0||ny<0||nx>=N||ny>=N){ blockB=true; break; }
            const v=board[ny][nx];
            if (v===side) b++; else { if (v!==EMPTY) blockB=true; break; }
          }
          const len=a+b+1;
          const open = (blockA?0:1)+(blockB?0:1);
          if (len>=5){ score += 1e6; continue; }
          if (len===4 && open===2) score += 1e5;
          else if (len===4 && open===1) score += 6e3;
          else if (len===3 && open===2) score += 1200;
          else if (len===3 && open===1) score += 150;
          else if (len===2 && open===2) score += 60;
          else if (len===2 && open===1) score += 16;
        }
        return score;
      }

      // å€™é¸èµ°æ³•ï¼šåªå–ã€Œè·é›¢ä»»ä¸€æ£‹å­ 2 æ ¼å…§ã€çš„ç©ºé»
      function hasNeighbor2(x,y){
        for(let dy=-2; dy<=2; dy++){
          for(let dx=-2; dx<=2; dx++){
            if (dx===0 && dy===0) continue;
            const nx=x+dx, ny=y+dy;
            if (nx<0||ny<0||nx>=N||ny>=N) continue;
            if (board[ny][nx]!==EMPTY) return true;
          }
        }
        return false;
      }
      function genCandidates(topK, biasForSide=ai){
        const list=[];
        let hasStone=false;
        for(let y=0;y<N;y++) for(let x=0;x<N;x++) if(board[y][x]!==EMPTY){ hasStone=true; break; }
        if (!hasStone){
          // ç©ºç›¤ â†’ å–ä¸­å¿ƒ
          const c = Math.floor(N/2);
          return [{x:c,y:c,score:0}];
        }
        for(let y=0;y<N;y++){
          for(let x=0;x<N;x++){
            if (board[y][x]!==EMPTY) continue;
            if (!hasNeighbor2(x,y)) continue;
            const off = pointScore(x,y,biasForSide);
            const def = pointScore(x,y,biasForSide===ai?human:ai) * 0.96; // é˜²å®ˆç¨å¾®å¼±åŒ–
            const sc = Math.max(off, def);
            list.push({x,y,score:sc, offensive:off, defensive:def});
          }
        }
        list.sort((a,b)=>b.score-a.score);
        return list.slice(0, topK);
      }

      // å³æ™‚æ®º/å µï¼šè‹¥æœ‰ç›´æ¥äº”é€£ï¼Œæˆ–å°æ‰‹å¯äº”é€£ï¼Œå…ˆè™•ç†
      function immediateMove(){
        const cand = genCandidates(30, ai);
        // 1) æˆ‘æ–¹ç›´æ¥å–å‹
        for(const c of cand){
          board[c.y][c.x]=ai;
          const win=isFive(c.x,c.y,ai);
          board[c.y][c.x]=EMPTY;
          if (win) return c;
        }
        // 2) æ“‹å°æ‰‹è‡´å‹
        for(const c of cand){
          board[c.y][c.x]=human;
          const oppWin=isFive(c.x,c.y,human);
          board[c.y][c.x]=EMPTY;
          if (oppWin) return c; // æ”¾åœ¨é€™è£¡å µ
        }
        return null;
      }

      // Minimax + Alpha-Beta + é™æ™‚
      function search(depth, alpha, beta, curSide, cfg, deadline){
        if (performance.now() > deadline) return { score:evaluate(), move:null, cutoff:true };
        if (depth===0) return { score:evaluate(), move:null };

        // å³æ™‚å‹è² å¿«é€Ÿåˆ¤
        if (lastMove && isFive(lastMove.x,lastMove.y,lastMove.side)){
          const s = (lastMove.side===ai)? 999999 : -999999;
          return { score:s, move:null };
        }

        const cand = genCandidates(cfg.topK, curSide);
        if (cand.length===0) return { score:evaluate(), move:null };

        let bestMove=null;

        if (curSide===ai){
          let best=-Infinity;
          for(const c of cand){
            board[c.y][c.x]=curSide;
            const prev=lastMove; lastMove={x:c.x,y:c.y,side:curSide};
            let val;
            // å¿«é€Ÿçµ‚æ­¢ï¼šæ”¾ä¸‹å³å‹
            if (isFive(c.x,c.y,curSide)){
              val=999999 - (cfg.depth-depth); // åå¥½æ›´å¿«å‹
            }else{
              const r = search(depth-1, alpha, beta, human, cfg, deadline);
              val=r.score;
              if (r.cutoff) { board[c.y][c.x]=EMPTY; lastMove=prev; return { score:best, move:bestMove, cutoff:true }; }
            }
            board[c.y][c.x]=EMPTY; lastMove=prev;

            if (val>best){ best=val; bestMove={x:c.x,y:c.y}; }
            if (val>alpha) alpha=val;
            if (beta<=alpha) break; // beta-cut
          }
          return { score:best, move:bestMove };
        }else{
          let best=Infinity;
          for(const c of cand){
            board[c.y][c.x]=curSide;
            const prev=lastMove; lastMove={x:c.x,y:c.y,side:curSide};
            let val;
            if (isFive(c.x,c.y,curSide)){
              val=-999999 + (cfg.depth-depth);
            }else{
              const r = search(depth-1, alpha, beta, ai, cfg, deadline);
              val=r.score;
              if (r.cutoff) { board[c.y][c.x]=EMPTY; lastMove=prev; return { score:best, move:bestMove, cutoff:true }; }
            }
            board[c.y][c.x]=EMPTY; lastMove=prev;

            if (val<best){ best=val; bestMove={x:c.x,y:c.y}; }
            if (val<beta) beta=val;
            if (beta<=alpha) break; // alpha-cut
          }
          return { score:best, move:bestMove };
        }
      }

      // AI ä¸‹å­
      async function aiMove(){
        if (over || turn!==ai) return;

        const level = +levelEl.value;
        const cfg = cfgFor(level);
        const deadline = performance.now() + cfg.limitMs;

        // Lv1/Lv2/Lv3 éƒ½å…ˆæª¢æŸ¥å³æ™‚æ®º/å µ
        if (level<=3){
          const imm = immediateMove();
          if (imm){ place(imm.x,imm.y,ai); return; }
        }

        let move=null;
        if (cfg.depth===0){
          // è²ªå©ªï¼éš¨æ©Ÿæ–¼å‰K
          const cand = genCandidates(cfg.topK, ai);
          if (cand.length===0){ // å¹¾ä¹ä¸æœƒ
            const c = Math.floor(N/2); move={x:c,y:c};
          }else{
            if (level===1){
              const k = Math.max(1, Math.min(6, cand.length));
              move = cand[(Math.random()*k)|0];
            }else{
              move = cand[0];
            }
          }
        }else{
          const res = search(cfg.depth, -Infinity, Infinity, ai, cfg, deadline);
          move = res.move;
          if (!move){
            // è¶…æ™‚æˆ–æ²’æœ‰æ›´å¥½ â†’ ç”¨å³æ™‚æˆ–è²ªå©ªåšä¿åº•
            const imm = immediateMove();
            if (imm) move=imm; else move = genCandidates(1, ai)[0];
          }
        }

        if (move) place(move.x, move.y, ai);
      }

      // è½å­ï¼ˆå«å‹è² æª¢æŸ¥ï¼‰
      function place(x,y,side){
        if (board[y][x]!==EMPTY || over) return false;
        board[y][x]=side;
        history.push({x,y,side});
        lastMove={x,y,side};
        // å‹è² 
        if (isFive(x,y,side)){
          over=true; winnerSide=side;
        }else{
          // æ›æ‰‹
          turn = (side===BLACK)? WHITE : BLACK;
        }
        updateLabels(); drawBoard();
        // è‹¥è¼ªåˆ° AIï¼Œæ’ä¸€ä¸‹
        if (!over && turn===ai){
          setTimeout(aiMove, 30);
        }
        return true;
      }

      // æ‚”æ£‹ï¼šå›åˆ°ç©å®¶å›åˆï¼ˆä¸€æ¬¡åƒæ‰ AI èˆ‡ç©å®¶å„ä¸€æ­¥ï¼Œå¦‚æœ AI é‚„æ²’ä¸‹å°±åªé€€ç©å®¶ä¸€æ­¥ï¼‰
      function undo(){
        if (history.length===0 || over) return;
        // å¦‚æœæœ€å¾Œä¸€æ­¥æ˜¯ AIï¼Œå°±å…ˆé€€ AIï¼›å†çœ‹ç©å®¶
        let popped = history.pop();
        board[popped.y][popped.x]=EMPTY;

        if (popped.side===ai && history.length>0){
          popped = history.pop();
          board[popped.y][popped.x]=EMPTY;
        }
        over=false; winnerSide=null;
        // è¨­å®šå›åˆï¼šè‹¥æœ‰æ£‹å‰‡ç›¸åæ–¼æœ€å¾Œæ­¥ï¼Œå¦å‰‡é»‘å…ˆ
        const last = history[history.length-1];
        if (last){ turn = (last.side===BLACK)? WHITE : BLACK; lastMove=last; }
        else { turn = BLACK; lastMove=null; }
        // ç¢ºä¿æ˜¯ç©å®¶å›åˆï¼Œå¦‚ä¸æ˜¯å°±å†é€€ä¸€æ­¥
        if (turn!==human && history.length>0){
          const p = history.pop(); board[p.y][p.x]=EMPTY;
          const last2 = history[history.length-1];
          turn = last2 ? ((last2.side===BLACK)? WHITE : BLACK) : BLACK;
          lastMove = last2 || null;
        }
        updateLabels(); drawBoard();
      }

      // äº‹ä»¶ï¼šä¸‹å­
      cvs.addEventListener('mousedown', (e)=>{
        if (over || turn!==human) return;
        const rect=cvs.getBoundingClientRect();
        const pos = posToCell(e.clientX - rect.left, e.clientY - rect.top);
        if (!pos) return;
        place(pos.x,pos.y,human);
      });

      // æ§åˆ¶åˆ—
      btnNew.addEventListener('click', ()=>{
        newBoard(); setSides(); drawBoard();
        // è‹¥ AI å…ˆæ‰‹
        if (turn===ai) setTimeout(aiMove, 60);
        cvs.focus();
      });
      btnUndo.addEventListener('click', ()=>{ undo(); cvs.focus(); });
      selSide.addEventListener('change', ()=>{
        const wasHuman=human;
        newBoard(); setSides(); drawBoard();
        if (turn===ai) setTimeout(aiMove, 60);
      });
      levelEl.addEventListener('input', ()=>{ lvlText.textContent=String(levelEl.value); lvlLabel.textContent=String(levelEl.value); });

      // å•Ÿå‹•
      newBoard(); setSides(); drawBoard();
      if (turn===ai) setTimeout(aiMove, 80);
      setTimeout(()=>cvs.focus(), 0);
    </script>
  </body>
  </html>`;
}

// Clipboard Tabs â€” åˆ†é å¼å‰ªè²¼ç°¿ï¼ˆä½¿ç”¨ statusBarHelper.v1.storage æ°¸ä¹…ä¿å­˜ï¼‰
// å°å°ºå¯¸ / å¯ç·¨è¼¯ / toolbar æœƒè‡ªå‹•æ›è¡Œ / é—œé–‰é¢æ¿è‡ªå‹• vm.stop
const vscode = require('vscode');
const { storage, vm } = statusBarHelper.v1;

(async function main () {
  const setText = (t) => (typeof vm.setLabel === 'function' ? vm.setLabel(t) : (vm.setText?.(t)));
  const setTip  = (t) => vm.setTooltip?.(t);
  setText('ğŸ“‹ Tabs');
  setTip('Clipboard Tabsï¼šåŸ·è¡Œå³é–‹å•Ÿ/èšç„¦é¢æ¿ï¼ˆè³‡æ–™å­˜æ–¼ SBH storageï¼‰');

  // ------------- storageï¼ˆworkspace å„ªå…ˆï¼Œå¦å‰‡ç”¨ globalï¼‰ -------------
  const store = storage?.workspace || storage?.global;
  if (!store) throw new Error('statusBarHelper.v1.storage ä¸å¯ç”¨');

  const STORE_KEY = 'clipboard.tabs.v1';
  const defaultState = { active: 'default', tabs: [ { id: 'default', name: 'Default', items: [] } ] };

  async function loadState() {
    const s = await store.get(STORE_KEY, null);
    return s && s.tabs ? s : defaultState;
  }
  async function saveState(state) {
    await store.set(STORE_KEY, state);
  }

  // ------------- å–®ä¾‹é¢æ¿ -------------
  const g = globalThis;
  const panel = await createOrRevealPanel();

  vm.onStop?.(() => {
    try { panel?.dispose(); } catch {}
  });

  function safeStop(reason){
    try{
      if (g.__sbhStopping) return;
      g.__sbhStopping = true;
      vm.stop?.(reason);
    } finally { setTimeout(()=>{ g.__sbhStopping = false; }, 0); }
  }

  async function createOrRevealPanel(){
    if (g.__sbhClipboardTabsPanel && !g.__sbhClipboardTabsPanel._disposed) {
      try { g.__sbhClipboardTabsPanel.reveal(); return g.__sbhClipboardTabsPanel; } catch {}
    }
    const p = vscode.window.createWebviewPanel(
      'sbhClipboardTabs', 'Clipboard Tabs', vscode.ViewColumn.Active,
      { enableScripts: true, retainContextWhenHidden: true }
    );
    const nonce = Math.random().toString(36).slice(2);
    p.webview.html = getHtml(nonce);

    // åˆå§‹è³‡æ–™ï¼šç­‰ webview è¦ï¼ˆreq:initï¼‰å†çµ¦ï¼Œä¸¦å›å¡« storage çš„è³‡æ–™
    p.webview.onDidReceiveMessage(async (msg) => {
      switch (msg.type) {
        case 'req:init': {
          const state = await loadState();
          p.webview.postMessage({ type: 'init', state });
          break;
        }
        case 'save': {
          // Webview é€šçŸ¥è³‡æ–™æ”¹è®Š â†’ å¯«å…¥ storage
          if (msg.state && msg.state.tabs) {
            await saveState(msg.state);
            // å¯å›å€‹ ACKï¼ˆä¸ä¸€å®šè¦ç”¨ï¼‰
            p.webview.postMessage({ type: 'saved' });
          }
          break;
        }
        case 'getSelection': {
          const ed = vscode.window.activeTextEditor;
          const text = ed ? ed.document.getText(ed.selection) : '';
          p.webview.postMessage({ type: 'selection', text });
          break;
        }
        case 'getClipboard': {
          const text = await vscode.env.clipboard.readText();
          p.webview.postMessage({ type: 'clipboard', text });
          break;
        }
        case 'writeClipboard': {
          await vscode.env.clipboard.writeText(String(msg.text || ''));
          vscode.window.showInformationMessage('å·²è¤‡è£½åˆ°ç³»çµ±å‰ªè²¼ç°¿');
          break;
        }
        case 'notify': {
          if (msg.level === 'warn') vscode.window.showWarningMessage(msg.message || '');
          else if (msg.level === 'error') vscode.window.showErrorMessage(msg.message || '');
          else vscode.window.showInformationMessage(msg.message || '');
          break;
        }
      }
    });

    p.onDidDispose(() => {
      p._disposed = true;
      // é—œé–‰åˆ†é  â†’ çµæŸè…³æœ¬
      safeStop('webview-closed');
    });

    g.__sbhClipboardTabsPanel = p;
    return p;
  }

  // ---------------- Webview ----------------
  function getHtml(nonce) {
    const csp = [
      "default-src 'none'",
      "img-src https:",
      "style-src 'unsafe-inline'",
      `script-src 'nonce-${nonce}'`,
    ].join('; ');
    return /*html*/`
