    if (err) {
      vscode.window.showErrorMessage(`Git Add å¤±æ•—ï¼š\\n${stderr}`);
    } else {
      vscode.window.showInformationMessage('âœ… git add . åŸ·è¡ŒæˆåŠŸ');
