// Please require any Node.js modules your script needs here
const vscode = require('vscode');
const { exec } = require('child_process');
const fs = require('fs');
const fsp = require('fs').promises;
const path = require('path');
const { vm } = statusBarHelper.v1;

const workspaceFolders = vscode.workspace.workspaceFolders;
if (!workspaceFolders) return;
const cwd = workspaceFolders[0].uri.fsPath;

const uri = (vscode.window.tabGroups.activeTabGroup?.activeTab?.input)?.uri;
if (!uri || uri.scheme !== 'file' || !uri.fsPath.endsWith('.xlsx')) {
  vscode.window.showWarningMessage('è«‹é¸å–ä¸€å€‹ .xlsx æª”æ¡ˆå†åŸ·è¡Œ TMS Transform');
  vm.stop();
  return;
}

const excelPath = uri.fsPath;
const tmsPath = path.join(cwd, 'tms');
const targetPath = path.join(cwd, 'src/assets/static/i18n');

exec(`otp transform-tms-i18n "${excelPath}"`, { cwd }, async (err, stdout, stderr) => {
  if (err) {
    vscode.window.showErrorMessage(`OTP Transform å¤±æ•—ï¼š\n${stderr}`);
    vm.stop();
    return;
  }

  if (!fs.existsSync(tmsPath)) {
    vscode.window.showWarningMessage('è½‰æ›æˆåŠŸä½†æ‰¾ä¸åˆ° tms è³‡æ–™å¤¾');
    vm.stop();
    return;
  }

  try {
    await copyFilesRecursive(tmsPath, targetPath);
    await fsp.rm(tmsPath, { recursive: true, force: true });
    await fsp.rm(excelPath, { recursive: true, force: true });
    vscode.window.showInformationMessage('âœ… å·²å®Œæˆè½‰æ›ã€è¦†è“‹ i18n ä¸¦åˆªé™¤åŸå§‹æª”');
    vm.stop();
  } catch (copyErr) {
    vscode.window.showErrorMessage(`è™•ç†éç¨‹ä¸­ç™¼ç”ŸéŒ¯èª¤ï¼š${copyErr}`);
    vm.stop();
  }
});

async function copyFilesRecursive(src, dest) {
  await fsp.mkdir(dest, { recursive: true });
  const entries = await fsp.readdir(src, { withFileTypes: true });

  for (const entry of entries) {
    const srcPath = path.join(src, entry.name);
    const destPath = path.join(dest, entry.name);

    if (entry.isDirectory()) {
      await copyFilesRecursive(srcPath, destPath);
    } else {
      await fsp.copyFile(srcPath, destPath);
    }
  }
}

