// Please require any Node.js modules your script needs here
const vscode = require('vscode');
const { exec } = require('child_process');
const fs = require('fs');
const fsp = require('fs').promises;
const path = require('path');
const process = require('process');
const { promisify }  = require('util');
const execAsync = promisify(exec);
const { vm } = statusBarHelper.v1;

// Your script here 
async function main() {
    // æœ€å‰é¢åŠ ä¸€å€‹ modal ç¢ºèªæç¤º
    const choice = await vscode.window.showInformationMessage(
      'æ˜¯å¦è¦åŸ·è¡Œã€Œæ‰“ç‰ˆè™Ÿã€å‹•ä½œï¼Ÿï¼ˆæœƒå˜—è©¦ npm version + push git tagï¼‰',
      { modal: true },
      'æ˜¯',
      'å¦'
    );
    if (choice !== 'æ˜¯') {
      // ä½¿ç”¨è€…å–æ¶ˆ
      vm.stop();
      return;
    }

    const workspaceFolders = vscode.workspace.workspaceFolders;
    if (!workspaceFolders) return;
    const cwd = workspaceFolders[0].uri.fsPath;
    const pkgPath = path.join(cwd, 'package.json');

    let pkg;
    try {
        pkg = JSON.parse(fs.readFileSync(pkgPath, 'utf8'));
    } catch {
        vscode.window.showErrorMessage('ç„¡æ³•è®€å– package.json');
        vm.stop();
        return;
    }

    let currentVersion = pkg.version; // ä¾‹å¦‚ 6.39.0-sp91001
    const baseVersion = currentVersion.split('-')[0]; // ä¾‹å¦‚ 6.39.0
    const tags = (await execAsync('git tag', { cwd })).stdout.split('\n').filter(Boolean);
    let newTag = '';

    const match = currentVersion.match(/-(sp\d+|uat|hotfix\d{8})(\d{3})$/);
    if (!match) {
        vscode.window.showErrorMessage(`âŒ ç„¡æ³•è§£æç‰ˆæœ¬è™Ÿå°¾ç¢¼ï¼Œåªè™•ç† sp / uat / hotfix é¡å‹`);
        vm.stop();
        return;
    }

    // ğŸ”’ åˆ†æ”¯åˆæ³•æ€§æª¢æŸ¥
    const branch = (await execAsync('git rev-parse --abbrev-ref HEAD', { cwd })).stdout.trim();
    const prefix = match[1];


    if (prefix.startsWith('sp')) {
        const sprintNum = prefix.slice(2); // sp91 â†’ 91
        if (!branch.startsWith(`sprint/${sprintNum}`)) {
            vscode.window.showErrorMessage(`âŒ åƒ…èƒ½åœ¨ sprint/${sprintNum} åˆ†æ”¯ä¸Šæ‰“ ${prefix} tagï¼Œç•¶å‰ç‚º ${branch}`);
            vm.stop();
            return;
        }
    } else if (prefix === 'uat') {
        if (branch !== 'develop') {
            vscode.window.showErrorMessage(`âŒ åƒ…èƒ½åœ¨ develop åˆ†æ”¯ä¸Šæ‰“ uat tagï¼Œç•¶å‰ç‚º ${branch}`);
            vm.stop();
            return;
        }
    } else if (prefix.startsWith('hotfix')) {
        const dateStr = prefix.slice(6);
        if (!branch.startsWith(`hotfix/${dateStr}`)) {
            vscode.window.showErrorMessage(`âŒ åƒ…èƒ½åœ¨ hotfix/${dateStr} åˆ†æ”¯ä¸Šæ‰“ hotfix tagï¼Œç•¶å‰ç‚º ${branch}`);
            vm.stop();
            return;
        }
    }

    const currentNum = parseInt(match[2]); // 001 â†’ 1
    const nextNum = currentNum + 1;

    newTag = `${baseVersion}-${prefix}${String(nextNum).padStart(3, '0')}`;

    const fullTag = `v${newTag}`;
    if (tags.includes(fullTag)) {
        vscode.window.showErrorMessage(`âŒ tag ${fullTag} å·²å­˜åœ¨`);
        vm.stop();
        return;
    }

    try {
        vscode.window.showInformationMessage(`âœ… bumping version: ${currentVersion} â†’ ${newTag}`);
        await execAsync(`npm version ${newTag}`, { cwd });
        await execAsync(`git push`, { cwd });
        await execAsync(`git push origin ${fullTag}`, { cwd });
        vscode.window.showInformationMessage(`âœ… å·²å»ºç«‹ä¸¦æ¨é€ç‰ˆæœ¬ tagï¼š${fullTag}`);
        vm.stop();
    } catch (err) {
        vscode.window.showErrorMessage(`âŒ bump version å¤±æ•—ï¼š${err.stderr || err.message}`);
        vm.stop();
    }
}
main();

// sbh - Angular environment åˆ‡æ›ï¼ˆæ”¹é€²ï¼šåªæœ‰åœ¨éœ€è¦æ™‚æ‰åŸ·è¡Œ git checkoutï¼‰
// è·¯å¾‘ï¼šprojects/trading-lib/src/multiple-network-connections/assets/serverLocalConfig.json
const vscode = require('vscode');
const fs = require('fs');
const path = require('path');
const { vm } = statusBarHelper.v1;
const { exec } = require('child_process');
const { promisify } = require('util');
const execAsync = promisify(exec);

const DefaultConfig = "";
const tsConfig = "";

const vietConfig= "";

(async function () {
  try {
    const platforms = [{ id: 'pc', label: 'PC' }, { id: 'm', label: 'M' }];
    const envs = [
      { id: 'dev', label: 'dev', code: 'dev', env: 'dev', production: false, config: DefaultConfig },
      { id: 'qat', label: 'qat', code: 'qat', env: 'qat', production: true, config: DefaultConfig },
      { id: 'uat', label: 'uat', code: 'qa',  env: 'qa',  production: true, config: DefaultConfig },
      { id: 'ts',  label: 'ts',  code: 'topsunx', env: 'topsunx', production: true, config: tsConfig },
      { id: 'vietcom',  label: 'vietcom',  code: 'vietcom', env: 'vietcom', production: true, config:vietConfig}
    ];

    const pickPlatform = await vscode.window.showQuickPick(
      platforms.map(p => ({ label: p.label, id: p.id })),
      { placeHolder: 'é¸æ“‡å¹³å° (PC / M)' }
    );
    if (!pickPlatform) { vm.stop(); return; }

    const pickEnv = await vscode.window.showQuickPick(
      envs.map(e => ({ label: e.label, id: e.id })),
      { placeHolder: 'é¸æ“‡ç’°å¢ƒ (dev / qat / uat / ts)' }
    );
    if (!pickEnv) { vm.stop(); return; }

    const workspaceFolders = vscode.workspace.workspaceFolders;
    if (!workspaceFolders || workspaceFolders.length === 0) {
      vscode.window.showErrorMessage('æ‰¾ä¸åˆ° workspaceï¼Œè«‹å…ˆé–‹å•Ÿå°ˆæ¡ˆè³‡æ–™å¤¾ã€‚');
      vm.stop();
      return;
    }
    const cwd = workspaceFolders[0].uri.fsPath;

    const envFilePath = path.join(cwd, 'src', 'environments', 'environment.ts');
    if (!fs.existsSync(envFilePath)) {
      vscode.window.showErrorMessage(`æ‰¾ä¸åˆ° ${envFilePath}`);
      vm.stop();
      return;
    }

    const serverLocalConfigFilePath = path.join(
      cwd,
      'projects',
      'trading-lib',
      'src',
      'multiple-network-connections',
      'assets',
      'serverLocalConfig.json'
    );
    if (!fs.existsSync(serverLocalConfigFilePath)) {
      vscode.window.showErrorMessage(`æ‰¾ä¸åˆ° ${serverLocalConfigFilePath}`);
      vm.stop();
      return;
    }

    // helperï¼šæ›¿æ› environment.ts ä¸­ key çš„å€¼ï¼ˆoutput ç”¨ single-quoteï¼‰
    function replaceKeyAnywhere(input, key, value) {
      const re = new RegExp(`([\"']?${key}[\"']?\\s*:\\s*)(['"\`])[^'"\`]*\\2`, 'g');
      return input.replace(re, (m, p1) => `${p1}'${value}'`);
    }

    // è®€ environment.ts
    const originalEnvRaw = fs.readFileSync(envFilePath, 'utf8');
    let content = originalEnvRaw;

    // è®€ serverLocalConfig.json ä¸¦ parse
    const originalServerLocalRaw = fs.readFileSync(serverLocalConfigFilePath, 'utf8');
    let serverLocalJson;
    try {
      serverLocalJson = JSON.parse(originalServerLocalRaw);
    } catch (e) {
      vscode.window.showErrorMessage(`è§£æ ${serverLocalConfigFilePath} å¤±æ•—ï¼š${e.message}`);
      vm.stop();
      return;
    }
    const originalServerConfigValue = serverLocalJson.config;

    // æ›¿æ› environment.ts çš„ code / env / root / production
    const platformRootMap = { pc: '/web/', m: '/mweb/' };
    const platformId = pickPlatform.id;
    const envObj = envs.find(x => x.id === pickEnv.id);
    if (!envObj) {
      vscode.window.showErrorMessage('é¸æ“‡çš„ç’°å¢ƒç„¡æ•ˆ');
      vm.stop();
      return;
    }

    content = replaceKeyAnywhere(content, 'code', envObj.code);
    content = replaceKeyAnywhere(content, 'env', envObj.env);
    const newRoot = platformRootMap[platformId] || '/web/';
    content = replaceKeyAnywhere(content, 'root', newRoot);
    content = content.replace(/([\"']?production[\"']?\s*:\s*)(true|false)/g, `$1${envObj.production ? 'true' : 'false'}`);

    const outch = vscode.window.createOutputChannel('Env Switcher');
    outch.show(true);
    outch.appendLine(`Debug: serverLocalConfigFilePath full: ${serverLocalConfigFilePath}`);

    // æŒ‡å®š trading-lib å­ repo rootï¼ˆæ˜ç¢ºç”¨ projects/trading-libï¼‰
    const repoRoot = path.join(cwd, 'projects', 'trading-lib');
    outch.appendLine(`Debug: using repoRoot: ${repoRoot}`);

    let serverLocalActionInfo = '';

    if (['dev', 'qat', 'uat'].includes(envObj.id)) {
      // æª¢æŸ¥ repoRoot æ˜¯å¦ç‚º git repo
      let isGitRepo = false;
      try {
        await execAsync('git rev-parse --is-inside-work-tree', { cwd: repoRoot });
        isGitRepo = true;
      } catch (e) {
        isGitRepo = false;
      }

      if (!isGitRepo) {
        // æ²’æœ‰ gitï¼šè©¢å•æ˜¯å¦ç›´æ¥å¯«å…¥
        const ans = await vscode.window.showInformationMessage(
          'projects/trading-lib ä¸æ˜¯ git repositoryï¼Œè¦ç›´æ¥å¯«å…¥ serverLocalConfig.json çš„ config å—ï¼Ÿï¼ˆæœƒè¦†è“‹è©²æ¬„ä½ï¼Œä¸”ä¸å»ºç«‹å‚™ä»½ï¼‰',
          { modal: true }, 'ç›´æ¥å¯«å…¥', 'å–æ¶ˆ'
        );
        if (ans !== 'ç›´æ¥å¯«å…¥') { vm.stop(); return; }
        serverLocalJson.config = envObj.config;
        try {
          fs.writeFileSync(serverLocalConfigFilePath, JSON.stringify(serverLocalJson, null, 2), 'utf8');
          serverLocalActionInfo = `ç›´æ¥å¯«å…¥ï¼ˆprojects/trading-lib ä¸æ˜¯ git repoï¼‰: config â†’ ${serverLocalJson.config}`;
        } catch (e) {
          vscode.window.showErrorMessage('å¯«å› serverLocalConfig.json å¤±æ•—ï¼š' + (e && e.message ? e.message : String(e)));
          vm.stop();
          return;
        }
      } else {
        // repo å­˜åœ¨ï¼Œå–å¾—ç›¸å°è·¯å¾‘ï¼ˆposixï¼‰
        const relPath = path.relative(repoRoot, serverLocalConfigFilePath).split(path.sep).join('/');
        outch.appendLine(`Debug: relPath (for git): ${relPath}`);

        // æª¢æŸ¥æ˜¯å¦è¢« git è¿½è¹¤
        let isTracked = true;
        try {
          await execAsync(`git ls-files --error-unmatch "${relPath}"`, { cwd: repoRoot });
        } catch (e) {
          isTracked = false;
        }

        if (!isTracked) {
          // æœªè¢«è¿½è¹¤ï¼šè©¢å•æ˜¯å¦ç›´æ¥å¯«å…¥
          const ans = await vscode.window.showInformationMessage(
            `projects/trading-lib ä¸­çš„æª”æ¡ˆ ${relPath} æœªè¢« git è¿½è¹¤ï¼Œè¦ç›´æ¥å¯«å…¥ serverLocalConfig.json çš„ config å—ï¼Ÿï¼ˆæœƒè¦†è“‹è©²æ¬„ä½ï¼Œä¸”ä¸å»ºç«‹å‚™ä»½ï¼‰`,
            { modal: true }, 'ç›´æ¥å¯«å…¥', 'å–æ¶ˆ'
          );
          if (ans !== 'ç›´æ¥å¯«å…¥') { vm.stop(); return; }
          serverLocalJson.config = envObj.config;
          try {
            fs.writeFileSync(serverLocalConfigFilePath, JSON.stringify(serverLocalJson, null, 2), 'utf8');
            serverLocalActionInfo = `ç›´æ¥å¯«å…¥ï¼ˆuntrackedï¼‰: config â†’ ${serverLocalJson.config}`;
          } catch (e) {
            vscode.window.showErrorMessage('å¯«å› serverLocalConfig.json å¤±æ•—ï¼š' + (e && e.message ? e.message : String(e)));
            vm.stop();
            return;
          }
        } else {
          // è¢«è¿½è¹¤ï¼šæª¢æŸ¥æ˜¯å¦æœ‰æœ¬åœ°è®Šæ›´ï¼ˆgit status --porcelainï¼‰
          let statusOut = '';
          try {
            const st = await execAsync(`git status --porcelain -- "${relPath}"`, { cwd: repoRoot });
            statusOut = (st && st.stdout) ? st.stdout.trim() : '';
          } catch (e) {
            // è‹¥ git status å‡ºéŒ¯ä¹Ÿä¸é˜»æ–·ï¼Œè½‰æˆåŸ·è¡Œ checkout çš„è·¯å¾‘ï¼ˆä½†æœƒç´€éŒ„ debugï¼‰
            outch.appendLine(`git status error: ${e.stderr || e.message || String(e)}`);
            statusOut = null; // è¡¨ç¤ºç„¡æ³•åˆ¤å®š
          }

          if (statusOut === '') {
            // cleanï¼šæ²’æœ‰è®Šæ›´ï¼Œæ²’å¿…è¦é‚„åŸ
            serverLocalActionInfo = `æª”æ¡ˆå·²èˆ‡ HEAD ä¸€è‡´ï¼Œç„¡éœ€é‚„åŸï¼ˆ${relPath}ï¼‰`;
            outch.appendLine(serverLocalActionInfo);
          } else {
            // æœ‰è®Šæ›´æˆ–ç„¡æ³•åˆ¤å®šï¼šå˜—è©¦ git checkout -- <file>
            try {
              await execAsync(`git checkout -- "${relPath}"`, { cwd: repoRoot });
              const restoredRaw = fs.readFileSync(serverLocalConfigFilePath, 'utf8');
              serverLocalJson = JSON.parse(restoredRaw);
              serverLocalActionInfo = `å·²ä½¿ç”¨ git é‚„åŸ ${relPath}ï¼ˆconfig: ${originalServerConfigValue} â†’ ${serverLocalJson.config}ï¼‰`;
            } catch (e) {
              const errMsg = (e && (e.stderr || e.message)) ? (e.stderr || e.message) : String(e);
              outch.appendLine(`git checkout error: ${errMsg}`);
              if (errMsg && errMsg.indexOf('did not match any file') !== -1) {
                // pathspec mismatchï¼šè©¢å•æ˜¯å¦ç›´æ¥å¯«å…¥ï¼ˆfallbackï¼‰
                const ans = await vscode.window.showInformationMessage(
                  'git é‚„åŸå¤±æ•—ï¼ˆpathspec æœª matchï¼‰ï¼Œè¦æ”¹ç‚ºç›´æ¥å¯«å…¥ serverLocalConfig.json çš„ config å—ï¼Ÿï¼ˆæœƒè¦†è“‹è©²æ¬„ä½ï¼Œä¸”ä¸å»ºç«‹å‚™ä»½ï¼‰',
