          <div class="kpi">Score: <b id="score">0</b></div>
          <div class="kpi">Lines: <b id="lines">0</b></div>
          <div class="kpi">Level: <b id="level">1</b></div>
          <div class="kpi">High Score: <b id="best">0</b></div>
        </div>
        <div class="legend" style="margin-top:6px">
