    </div>

    <script nonce="${nonce}">
      // ====== Elements / Persistent Setting ======
      const $ = id => document.getElementById(id);
      const gc = $('game').getContext('2d');
      const hc = $('hold').getContext('2d');
      const nc = $('next').getContext('2d');

      const scoreEl=$('score'), linesEl=$('lines'), levelEl=$('level'), bestEl=$('best');
      const btnPause=$('btnPause'), btnRestart=$('btnRestart');
      const startLevelInput=$('startLevel'), startLevelLabel=$('startLevelLabel');
      const btnApply=$('btnApply');

      const SETTINGS_KEY = 'sbh_tetris_settings_v3';
      function loadSettings(){
        try{
          const v = JSON.parse(localStorage.getItem(SETTINGS_KEY)||'{}');
          return { startLevel: Number.isFinite(v.startLevel)? v.startLevel : 1 };
        }catch{ return { startLevel:1 }; }
      }
      function saveSettings(s){ localStorage.setItem(SETTINGS_KEY, JSON.stringify(s)); }

      const S = loadSettings();
      startLevelInput.value = String(S.startLevel);
      startLevelLabel.textContent = S.startLevel;

      // å›ºå®šåƒæ•¸
      const BASE_MS  = 700; // åŸºç¤é‡åŠ›ï¼ˆms/æ ¼ï¼‰
      const SOFT_MS  = 60;  // æŒ‰ä½â†“ çš„é€£çºŒ soft drop é–“éš”ï¼ˆms/æ ¼ï¼‰
      const MIN_MS   = 80;  // æœ€å¿«é‡åŠ›
      const LOCK_DELAY_MS = 500; // â˜… è½åœ°å¾Œçš„ã€Œå»¶é²é–å®šã€æ™‚é–“ï¼Œå¯åœ¨é€™è£¡èª¿æ•´

      // ====== Game Constants ======
      const W = 10, H = 20, CELL = 24;
      const BEST_KEY = 'sbh_tetris_high';
      const COLORS = {
        I:'#00f0f0', O:'#f0f000', T:'#a020f0', S:'#00d000', Z:'#d00000', J:'#0000d0', L:'#f0a000', GHOST:'rgba(255,255,255,0.15)'
      };
      const ROT = {
        I: [
          [[0,1],[1,1],[2,1],[3,1]],
          [[2,0],[2,1],[2,2],[2,3]],
          [[0,2],[1,2],[2,2],[3,2]],
          [[1,0],[1,1],[1,2],[1,3]],
        ],
        O: [[[1,0],[2,0],[1,1],[2,1]],[[1,0],[2,0],[1,1],[2,1]],[[1,0],[2,0],[1,1],[2,1]],[[1,0],[2,0],[1,1],[2,1]]],
        T: [
          [[1,0],[0,1],[1,1],[2,1]],
          [[1,0],[1,1],[2,1],[1,2]],
          [[0,1],[1,1],[2,1],[1,2]],
          [[1,0],[0,1],[1,1],[1,2]],
        ],
        S: [
          [[1,0],[2,0],[0,1],[1,1]],
          [[1,0],[1,1],[2,1],[2,2]],
          [[1,1],[2,1],[0,2],[1,2]],
          [[0,0],[0,1],[1,1],[1,2]],
        ],
        Z: [
          [[0,0],[1,0],[1,1],[2,1]],
          [[2,0],[1,1],[2,1],[1,2]],
          [[0,1],[1,1],[1,2],[2,2]],
          [[1,0],[0,1],[1,1],[0,2]],
        ],
        J: [
          [[0,0],[0,1],[1,1],[2,1]],
          [[1,0],[2,0],[1,1],[1,2]],
          [[0,1],[1,1],[2,1],[2,2]],
          [[1,0],[1,1],[0,2],[1,2]],
        ],
        L: [
          [[2,0],[0,1],[1,1],[2,1]],
          [[1,0],[1,1],[1,2],[2,2]],
          [[0,1],[1,1],[2,1],[0,2]],
          [[0,0],[1,0],[1,1],[1,2]],
        ]
      };
      const TYPES = Object.keys(ROT);

      // ====== Game State ======
      let board, queue, cur, hold=null, canHold=true;
      let score=0, lines=0, level=S.startLevel, best=Number(localStorage.getItem(BEST_KEY)||0);
      let paused=false, over=false, timer=null;

      // Soft drop
      let softHeld=false, softTimer=null;

      // â˜… Lock Delay ç‹€æ…‹
      let grounded=false;      // æ˜¯å¦å·²ç¶“ã€Œæ¥åœ°ã€ï¼ˆä¸‹ä¸€æ ¼ä¸‹ä¸å»ï¼‰
      let lockExpire=0;        // ä½•æ™‚åˆ°æœŸé–å®šï¼ˆtime msï¼‰

      bestEl.textContent = String(best);

      // ====== Helpers ======
      function emptyBoard(){ return Array.from({length:H},()=>Array(W).fill(null)); }
      function newBag(){ const a=[...TYPES]; for(let i=a.length-1;i>0;i--){ const j=(Math.random()*(i+1))|0; [a[i],a[j]]=[a[j],a[i]] } return a; }
      function ensureQueue(){ if(queue.length<7) queue.push(...newBag()); }
      function spawn(){
        const type = queue.shift(); ensureQueue();
        cur = { type, r:0, x:3, y:0 };
        grounded=false; lockExpire=0;
        if (!canPlace(cur, cur.x, cur.y)){ gameOver(); }
        canHold = true;
      }
      function cells(p){ return ROT[p.type][p.r]; }
      function canPlace(p, nx, ny, nr=p.r){
        const shape = ROT[p.type][nr];
        for(const [dx,dy] of shape){
          const x = nx+dx, y = ny+dy;
          if (x<0 || x>=W || y<0 || y>=H) return false;
          if (board[y][x]) return false;
        }
        return true;
      }
      function startLockDelay(){ grounded=true; lockExpire = Date.now() + LOCK_DELAY_MS; }
      function clearLock(){ grounded=false; lockExpire=0; }

      function lock(){
        for(const [dx,dy] of cells(cur)){
          const x=cur.x+dx, y=cur.y+dy;
          if (y>=0 && y<H) board[y][x] = cur.type;
        }
        grounded=false; lockExpire=0;
        clearLines(); spawn();
      }
      function clearLines(){
        let cleared=0;
        for(let y=H-1;y>=0;){
          if (board[y].every(Boolean)){ board.splice(y,1); board.unshift(Array(W).fill(null)); cleared++; }
          else y--;
        }
        if (cleared>0){
          score += [0,100,300,500,800][cleared] * level;
          lines += cleared;
          const newLevel = Math.max(1, S.startLevel + Math.floor(lines/10));
          if (newLevel!==level){ level=newLevel; restartGravity(); }
          updateHUD();
        }
      }
      function holdSwap(){
        if (!canHold || over) return;
        if (hold===null){ hold=cur.type; spawn(); }
        else { const t=hold; hold=cur.type; cur={type:t, r:0, x:3, y:0}; if(!canPlace(cur,cur.x,cur.y)){ gameOver(); return; } }
        canHold=false; drawSide();
      }

      // æˆåŠŸç§»å‹•/æ—‹è½‰å¾Œï¼Œæ›´æ–°æ¥åœ°ç‹€æ…‹ï¼šå¦‚æœä»æ¥åœ°å°±é‡ç½® lock delay
      function refreshLockAfterAction(){
        if (canPlace(cur, cur.x, cur.y+1)) {
          clearLock();
        } else {
          startLockDelay(); // still grounded â†’ reset timer
        }
      }

      function rotate(dir){ // +1 CW / -1 CCW
        if (over) return;
        const nr = (cur.r + (dir>0?1:3)) % 4;
        const kicks = [[0,0],[1,0],[-1,0],[0,-1],[2,0],[-2,0]];
        for (const [kx,ky] of kicks){
          const nx = cur.x+kx, ny=cur.y+ky;
          if (canPlace(cur,nx,ny,nr)){ cur.r=nr; cur.x=nx; cur.y=ny; refreshLockAfterAction(); draw(); return; }
        }
      }

      function move(dx){
        if (over) return;
        const nx = cur.x + dx;
        if (canPlace(cur, nx, cur.y)){ cur.x = nx; refreshLockAfterAction(); draw(); }
      }

      function softDropStep(){
        if (paused || over) return;
        if (canPlace(cur, cur.x, cur.y+1)){
          cur.y++; score += 1; updateHUD(false);
          clearLock(); // ä¸€æ—¦å¾€ä¸‹æˆåŠŸï¼Œå–æ¶ˆæ¥åœ°ç‹€æ…‹
          draw();
        } else {
          if (!grounded) { startLockDelay(); }
          // å·²æ¥åœ°ï¼šç­‰åˆ°æ™‚é–“åˆ°æ‰ lock
          if (Date.now() >= lockExpire){ lock(); }
          draw();
        }
      }

      function hardDrop(){
        if (over) return;
        let dist=0; while (canPlace(cur,cur.x,cur.y+1)){ cur.y++; dist++; }
        score += dist*2; updateHUD(false);
        lock(); draw(); // Hard drop ç«‹å³é–å®š
      }

      // ====== Gravity ======
      function gravityMs(lv){
        return Math.max(MIN_MS, BASE_MS - (lv-1)*60);
      }
      function restartGravity(){
        clearInterval(timer);
        timer = setInterval(()=>{
          if (paused || over) return;
          if (canPlace(cur, cur.x, cur.y+1)){
            cur.y++;
            clearLock();
          } else {
            if (!grounded) { startLockDelay(); }
            else if (Date.now() >= lockExpire) { lock(); }
          }
          draw();
        }, gravityMs(level));
      }

      // ====== Drawing ======
      function drawCell(ctx,x,y,color){
        ctx.fillStyle=color;
        ctx.fillRect(x*CELL, y*CELL, CELL, CELL);
        ctx.fillStyle='rgba(255,255,255,0.07)';
        ctx.fillRect(x*CELL, y*CELL, CELL, Math.max(2, CELL*0.18));
      }
      function ghostY(){ let gy=cur.y; while (canPlace(cur,cur.x,gy+1)) gy++; return gy; }
      function draw(){
        const c = gc.canvas; gc.clearRect(0,0,c.width,c.height);
        for(let y=0;y<H;y++){ for(let x=0;x<W;x++){ gc.fillStyle=((x+y)%2===0)?'#0a0a0a':'#0c0c0c'; gc.fillRect(x*CELL,y*CELL,CELL,CELL); } }
        for(let y=0;y<H;y++){ for(let x=0;x<W;x++){ const t=board[y][x]; if(t) drawCell(gc,x,y,COLORS[t]); } }
        const gy = ghostY();
        for(const [dx,dy] of cells(cur)){ const x=cur.x+dx,y=gy+dy; gc.fillStyle=COLORS.GHOST; gc.fillRect(x*CELL,y*CELL,CELL,CELL); }
        for(const [dx,dy] of cells(cur)){ const x=cur.x+dx,y=cur.y+dy; drawCell(gc,x,y,COLORS[cur.type]); }

        // å¦‚æœæ¥åœ°ä¸­ï¼Œå¯åœ¨åº•éƒ¨ç•«ä¸€åœˆæ·¡é‚Šæç¤ºï¼ˆå¯é¸ï¼‰
        // if (grounded) { gc.strokeStyle='rgba(255,255,255,.15)'; gc.lineWidth=2; gc.strokeRect(0, (H-1)*CELL+1, W*CELL, CELL-2); }

        drawSide();
        if (over) drawGameOver();
      }
      function drawGameOver(){
        const c = gc.canvas; gc.fillStyle='rgba(0,0,0,0.6)'; gc.fillRect(0,0,c.width,c.height);
        gc.fillStyle='#fff'; gc.textAlign='center'; gc.font='bold 22px system-ui'; gc.fillText('Game Over', c.width/2, c.height/2 - 10);
        gc.font='14px system-ui'; gc.fillText('Press R to Restart', c.width/2, c.height/2 + 16);
      }
      function drawMini(ctx,type,cell=16){
        ctx.clearRect(0,0,ctx.canvas.width,ctx.canvas.height);
        if(!type) return;
        const shape=ROT[type][0]; const ox=2, oy=2;
        for(const [dx,dy] of shape){
          ctx.fillStyle=COLORS[type]; ctx.fillRect((ox+dx)*cell,(oy+dy)*cell,cell,cell);
          ctx.fillStyle='rgba(255,255,255,0.08)'; ctx.fillRect((ox+dx)*cell,(oy+dy)*cell,cell,Math.max(2,cell*0.18));
        }
      }
      function drawSide(){
        drawMini(hc, hold, 16);
        nc.clearRect(0,0,nc.canvas.width,nc.canvas.height);
        const show = queue.slice(0,5), cell=14;
        show.forEach((t,i)=>{
          const offY = i*46 + 4;
          const shape = ROT[t][0];
          for(const [dx,dy] of shape){
            nc.fillStyle=COLORS[t];
            nc.fillRect((2+dx)*cell, (1+dy)*cell + offY, cell, cell);
            nc.fillStyle='rgba(255,255,255,0.08)';
            nc.fillRect((2+dx)*cell, (1+dy)*cell + offY, cell, Math.max(2,cell*0.18));
          }
        });
      }

      // ====== HUD ======
      function updateHUD(updateBest=true){
        scoreEl.textContent = String(score);
        linesEl.textContent = String(lines);
        levelEl.textContent = String(level);
        if (updateBest){
          const b = Math.max(Number(localStorage.getItem(BEST_KEY)||0), score);
          localStorage.setItem(BEST_KEY, b);
          bestEl.textContent = String(b);
        }
      }
      function gameOver(){ over=true; clearInterval(timer); stopSoftHeld(); updateHUD(); draw(); }

      // ====== Reset / Init ======
      function resetGame(){
        board = emptyBoard();
        queue = [...newBag(), ...newBag()];
        cur=null; hold=null; canHold=true;
        score=0; lines=0; level=S.startLevel; paused=false; over=false;
        grounded=false; lockExpire=0;
        updateHUD();
        spawn();
        restartGravity();
        draw();
      }

      // ====== Input ======
      function stopSoftHeld(){ softHeld=false; clearInterval(softTimer); softTimer=null; }
      window.addEventListener('keydown', (e)=>{
        if (e.key===' '){ e.preventDefault(); }
        if (over){ if (e.key==='r'||e.key==='R'){ resetGame(); } return; }

        if (e.key==='p'||e.key==='P'){ paused=!paused; btnPause.textContent = paused?'â–¶ï¸ Resume':'â¸ï¸ Pause'; return; }
        if (paused) return;

        if (e.key==='ArrowLeft'){ move(-1); }
        else if (e.key==='ArrowRight'){ move(1); }
        else if (e.key==='ArrowDown'){
          if (!softHeld){ softHeld=true; softDropStep(); softTimer=setInterval(softDropStep, Math.max(10, SOFT_MS)); }
        }
        else if (e.key==='ArrowUp' || e.key==='x' || e.key==='X'){ rotate(+1); }
        else if (e.key==='z' || e.key==='Z'){ rotate(-1); }
        else if (e.key==='c' || e.key==='C' || e.key==='Shift'){ holdSwap(); }
        else if (e.key===' '){ hardDrop(); }
        else if (e.key==='r' || e.key==='R'){ resetGame(); }
      });
      window.addEventListener('keyup', (e)=>{ if (e.key==='ArrowDown'){ stopSoftHeld(); } });
      window.addEventListener('blur', ()=>{ if(!over){ paused=true; btnPause.textContent='â–¶ï¸ Resume'; stopSoftHeld(); } });

      // Buttons
      btnPause.addEventListener('click', ()=>{ if(over) return; paused=!paused; btnPause.textContent = paused?'â–¶ï¸ Resume':'â¸ï¸ Pause'; });
      btnRestart.addEventListener('click', resetGame);

      // Settings UI
      startLevelInput.addEventListener('input', ()=> startLevelLabel.textContent = startLevelInput.value);
      btnApply.addEventListener('click', ()=>{
        S.startLevel = Number(startLevelInput.value);
        saveSettings(S);
        resetGame();
      });

      // Init
      resetGame();
    </script>
  </body>
  </html>`;
}

