    const ICON=(code)=>{ if([0].includes(code))return'â˜€ï¸'; if([1,2].includes(code))return'â›…';
      if([3].includes(code))return'â˜ï¸'; if([45,48].includes(code))return'ğŸŒ«ï¸';
