// é›£åº¦ï¼š1=è¶…ä¼‘é–’(éš¨æ©Ÿ+å¨è„…)ã€2=è²ªå©ªã€3=å¼·è²ªå©ª(å³æ™‚å µ/æ®º)ã€4=æœå°‹2å±¤ã€5=æœå°‹2å±¤(æ›´å¤šå€™é¸)ã€6=æœå°‹3å±¤(é™æ™‚)

const vscode = require('vscode');
const { vm } = statusBarHelper.v1;

(function main(){
  const panel = vscode.window.createWebviewPanel(
    'sbhGomoku',
    'âš«ï¸âšªï¸ Gomoku â€” äº”å­æ£‹ (PvE)',
